INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B243A47160962E439123F26B6BA1ECC3', X'6F39F242B085DF45A2921DE9AFCAB4D9', X'6F39F242B085DF45A2921DE9AFCAB4D9', 2, NULL, NULL, 2, NULL, 0, 'To use, simply apply 1 to 2 tablespoons of mixture (depending on the size of cut) to steaks. Grill as directed.');
