INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F6706A89606A44E893FABD7023681CA', X'BB6226FEF7A4BD4E9BACDB811917209D', X'BB6226FEF7A4BD4E9BACDB811917209D', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1436A44826D7AD409A7D7B0022F08F91', X'BB6226FEF7A4BD4E9BACDB811917209D', X'BB6226FEF7A4BD4E9BACDB811917209D', 4, NULL, NULL, 2, NULL, 0, 'Grill steak over as high a heat as you can for 2 minutes. Flip and continue grilling for 2 more minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A2F74A5A970244093EFB09579673F35', X'BB6226FEF7A4BD4E9BACDB811917209D', X'BB6226FEF7A4BD4E9BACDB811917209D', 5, NULL, NULL, 2, NULL, 0, 'Move to a cooler part of the grill or reduce heat to medium and continue grilling until done. About 4 minutes per side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'988DF0339C08214699A7E40E083E2FB8', X'32CD06829C421145868D538160B26929', X'32CD06829C421145868D538160B26929', 0, NULL, NULL, 2, NULL, 0, 'Place the tri-tip pieces into a resealable plastic bag. Combine the marinade ingredients and pour it over the meat. Massage the marinade into the meat. Seal the bag and place it into the refrigerator for 4 to 8 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F859D7223DE00B41A2ADC268861CAB51', X'32CD06829C421145868D538160B26929', X'32CD06829C421145868D538160B26929', 1, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium-high meat. Remove the meat from the bag and place it onto the grill. Discard the marinade as it is not safe to reuse it.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03A4B20FBE7C094C9B22B3BA85E4A3F4', X'32CD06829C421145868D538160B26929', X'32CD06829C421145868D538160B26929', 2, NULL, NULL, 2, NULL, 0, 'Cook the tri-tip for 20 minutes, turning occasionally.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9775CF0D69A2984EA7603828F09E6DB9', X'32CD06829C421145868D538160B26929', X'32CD06829C421145868D538160B26929', 3, NULL, NULL, 2, NULL, 0, 'During the last half of cooking, wrap the tortillas in aluminum foil and add them to the grill. Turn them over a few times while tri-tip is finishing up. Once the beef has reached your desired doneness, remove it and the tortillas from the grill. Allow the meat to rest for 5 minutes before carving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D6C996ED851764491AF64911690015F', X'32CD06829C421145868D538160B26929', X'32CD06829C421145868D538160B26929', 4, NULL, NULL, 2, NULL, 0, 'After resting, remove the roast pieces to a cutting board. Thinly slice the steak on the bias (against the grain) for the most tenderness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'493E4DC754A11E4D9DA49D9D0F5A6EFD', X'32CD06829C421145868D538160B26929', X'32CD06829C421145868D538160B26929', 5, NULL, NULL, 2, NULL, 0, 'To assemble the tacos, simply add a few slices of grilled tri-tip to a tortilla. Top each taco with guacamole, pico de gallo, and a sprinkle of cilantro. Fold the tortillas and enjoy. Serve with your favorite sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88AD5A8A59893B4199F622430E871C74', X'99677EAD303F93458CB73C463D162AF9', X'99677EAD303F93458CB73C463D162AF9', 0, NULL, NULL, 2, NULL, 0, 'Remove 3 tablespoons of crushed pineapple from can and place into a bowl. Carefully dip out 3 tablespoons of pineapple juice from the can. If this proves difficult to do, place the remaining crushed pineapple in a strainer over a bowl. Press down with a spatula to release as much liquid as you can. This should give you the necessary amount for the recipe. Add remaining the marinade ingredients (soy sauce, minced shallots, olive oil, cider vinegar, honey, minced garlic, minced ginger, black pepper, salt) to the bowl and combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B08906D1F47A147AFC736A1DD42D8DA', X'99677EAD303F93458CB73C463D162AF9', X'99677EAD303F93458CB73C463D162AF9', 1, NULL, NULL, 2, NULL, 0, 'Place the flank steak into a glass baking dish or plastic container and coat it on both sides with the marinade mixture. Cover dish tightly with plastic wrap and place into refrigerator for 2 to 12 hours. Turn the steak a few times during the marinating process and replace the plastic wrap over the dish each time.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC3AC29D4C0BD248A8D9B45B4BBB98CF', X'99677EAD303F93458CB73C463D162AF9', X'99677EAD303F93458CB73C463D162AF9', 2, NULL, NULL, 2, NULL, 0, 'Preheat the grill to medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA202B644F0985449C4A15317FAB560C', X'99677EAD303F93458CB73C463D162AF9', X'99677EAD303F93458CB73C463D162AF9', 3, NULL, NULL, 2, NULL, 0, 'Remove the steak from the glass dish. Place the steak on the heated grill. Cook about 5 to 7 minutes per side, depending on the thickness of the cut, until done. Remove the steaks from grill once they''ve reached the desired doneness and let them rest for 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBB546E0C7524740A9D1B97CEB6BE6ED', X'99677EAD303F93458CB73C463D162AF9', X'99677EAD303F93458CB73C463D162AF9', 4, NULL, NULL, 2, NULL, 0, 'Slice the flank steaks into strips across the grain.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BBE4F959C7A8643AD655BFE6718DBF8', X'E06DDC4158DA2247924CE1B39808D1E1', X'E06DDC4158DA2247924CE1B39808D1E1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A758F1A9B5480B4C8DD950611F539816', X'E06DDC4158DA2247924CE1B39808D1E1', X'E06DDC4158DA2247924CE1B39808D1E1', 1, NULL, NULL, 2, NULL, 0, 'Combine espresso beans, brown sugar, garlic powder, black pepper, and chili powder. Rub mixture onto steaks and place into a resealable container. Cover and allow to refrigerate for an hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A62C5D18150F054DB47C12B62C5B6A36', X'E06DDC4158DA2247924CE1B39808D1E1', X'E06DDC4158DA2247924CE1B39808D1E1', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium heat. Remove steaks from fridge and place on grill. Cook for about 1 to 1 1/2 hours, depending on the thickness of roast and desired doneness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2171301E483AA747B1B172A12461F7E3', X'E06DDC4158DA2247924CE1B39808D1E1', X'E06DDC4158DA2247924CE1B39808D1E1', 3, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat allow to sit for 5 minutes before slicing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCE4D332F89B8E4C835BFD0837D91B28', X'E06DDC4158DA2247924CE1B39808D1E1', X'E06DDC4158DA2247924CE1B39808D1E1', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F92CC07077910F4F979991068AF5C3DE', X'7E8EB0E0DD8622479F082EE438D0EEB5', X'7E8EB0E0DD8622479F082EE438D0EEB5', 0, NULL, NULL, 2, NULL, 0, 'Combine rub ingredients in a small bowl. Allow steaks to come to room temperature before grilling.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E27C63543E7AD740A11B44389E1580B8', X'7E8EB0E0DD8622479F082EE438D0EEB5', X'7E8EB0E0DD8622479F082EE438D0EEB5', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for high heat. Apply spice mixture to both sides of steak. Place steaks on ?the grill and cook for 7 to 10 minutes, turning once.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B74FD95B5775C74AAC04AE6881804800', X'7E8EB0E0DD8622479F082EE438D0EEB5', X'7E8EB0E0DD8622479F082EE438D0EEB5', 2, NULL, NULL, 2, NULL, 0, 'Once steaks have reached the desired doneness, remove from heat and let rest for at least 5 minutes before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A4913934054845A89474A8102CCA8D', X'C66AB4AE1314F148AB909DBAF7250411', X'C66AB4AE1314F148AB909DBAF7250411', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
