INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9BAFBE0E6C8854FBA88A7A0910B9089', X'C66AB4AE1314F148AB909DBAF7250411', X'C66AB4AE1314F148AB909DBAF7250411', 2, NULL, NULL, 2, NULL, 0, 'Add the rosemary, thyme, and marjoram, and stir. Let sit for 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04BA7EF10845E64BBAA6A8FF61F9D403', X'C66AB4AE1314F148AB909DBAF7250411', X'C66AB4AE1314F148AB909DBAF7250411', 3, NULL, NULL, 2, NULL, 0, 'Place the filet mignon into a shallow glass dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7920756266FC34F829E5A23E274AA03', X'C66AB4AE1314F148AB909DBAF7250411', X'C66AB4AE1314F148AB909DBAF7250411', 4, NULL, NULL, 2, NULL, 0, 'Pour the herb mixture over the steaks, turning them over to coat evenly. Cover and let marinate for 2 to 4 hours in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D78373625D2C514FB4E16BFFD061941A', X'C66AB4AE1314F148AB909DBAF7250411', X'C66AB4AE1314F148AB909DBAF7250411', 5, NULL, NULL, 2, NULL, 0, 'Preheat the grill for high heat. Right before placing steaks on the grill, oil the grate with a long pair of tongs, folded paper towels, and oil. Make 3 to 4 passes on the grate.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7A4E8B9BD437340B441EBA708CA46D7', X'C66AB4AE1314F148AB909DBAF7250411', X'C66AB4AE1314F148AB909DBAF7250411', 6, NULL, NULL, 2, NULL, 0, 'Remove the steaks from the fridge, and season both sides with salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ED59C21A3F16F45BDB7FBDCE308640A', X'C66AB4AE1314F148AB909DBAF7250411', X'C66AB4AE1314F148AB909DBAF7250411', 7, NULL, NULL, 2, NULL, 0, 'Place the steaks on the grill and cook for 5 to 6 minutes per side, or to the desired doneness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFEF9F8AACE61342B88EB29B28FC6884', X'C66AB4AE1314F148AB909DBAF7250411', X'C66AB4AE1314F148AB909DBAF7250411', 8, NULL, NULL, 2, NULL, 0, 'Remove steaks from the heat and let rest for at least 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42906776EBD8A447B6145B391D0D2896', X'C66AB4AE1314F148AB909DBAF7250411', X'C66AB4AE1314F148AB909DBAF7250411', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92C8C6DCE5035749AF30A4238B12BBCE', X'3442915771245D41A3A56B880E2DAF14', X'3442915771245D41A3A56B880E2DAF14', 0, NULL, NULL, 2, NULL, 0, 'Place flat-iron steaks into a resealable plastic bag. Combine marinade ingredients and pour over steaks, making sure every inch of meat is coated well. Gently, release air from bag, seal, and place into the refrigerator for 2 to 8 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEE8551A46EBED40A87E24BA6F7E04C4', X'3442915771245D41A3A56B880E2DAF14', X'3442915771245D41A3A56B880E2DAF14', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84FC79AECBEAE04B80284552BCFC5AD1', X'3442915771245D41A3A56B880E2DAF14', X'3442915771245D41A3A56B880E2DAF14', 2, NULL, NULL, 2, NULL, 0, 'Remove steaks from bag and discard any leftover marinade. Place steaks onto the grill and cook for 2 to 3 minutes per side. Reduce heat to medium and cook for another 12 to 15 minutes, depending on the thickness of the meat and desired doneness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB9676C086434845B4844F8DB8955AED', X'3442915771245D41A3A56B880E2DAF14', X'3442915771245D41A3A56B880E2DAF14', 3, NULL, NULL, 2, NULL, 0, 'Once cooked, remove steaks from heat and place onto a large cutting board. Let meat rest for a few minutes before carving. Slice meat into thin pieces and place onto a serving platter. Cover platter with foil to keep warm while you get the other ingredients ready.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35CB3A483E1ABC4697C68CE03DA5940F', X'3442915771245D41A3A56B880E2DAF14', X'3442915771245D41A3A56B880E2DAF14', 4, NULL, NULL, 2, NULL, 0, 'To assemble: Simply place 3 to 4 strips of meat into warmed corn tortillas and top with guacamole, pico de gallo, and any other condiments of choice. Serve with seasoned rice, refried beans, and lime wedges.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2008EA8442F004C97F8B5AD4A820724', X'FB9B2B8236465C40929852644D7006B9', X'FB9B2B8236465C40929852644D7006B9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F58EE09741BB0C458B303BC5FE78AAFD', X'FB9B2B8236465C40929852644D7006B9', X'FB9B2B8236465C40929852644D7006B9', 1, NULL, NULL, 2, NULL, 0, 'Make the marinade by combining the lime juice, salt, garlic powder, cumin, pepper, and coriander.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'272A9034C2A40E49A7C61FA008489BA1', X'FB9B2B8236465C40929852644D7006B9', X'FB9B2B8236465C40929852644D7006B9', 2, NULL, NULL, 2, NULL, 0, 'Trim off visible fat from steaks. Use a fork to prick the steaks deeply all over and on both sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C13BF228169621498642FE79D6634422', X'FB9B2B8236465C40929852644D7006B9', X'FB9B2B8236465C40929852644D7006B9', 3, NULL, NULL, 2, NULL, 0, 'Place trimmed steaks in a resealable plastic bag and pour the marinade over the steaks, making sure to coat well and refrigerate for 30 minutes, turning once.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ED9BCAECF82B547BFFD568BDCF93C76', X'FB9B2B8236465C40929852644D7006B9', X'FB9B2B8236465C40929852644D7006B9', 4, NULL, NULL, 2, NULL, 0, 'Heat an outdoor grill, indoor grill, broiler, or grill pan on the stovetop and remove steaks from the marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D754DEA2161F84E9592DAAFCEBBD1AF', X'FB9B2B8236465C40929852644D7006B9', X'FB9B2B8236465C40929852644D7006B9', 5, NULL, NULL, 2, NULL, 0, 'Place steaks on a hot grill or another surface that has been very lightly coated with cooking spray and cook each side for 4 to 5 minutes or until done to your liking.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3001D8DEC8426446BA67688DA55D1FD2', X'FB9B2B8236465C40929852644D7006B9', X'FB9B2B8236465C40929852644D7006B9', 6, NULL, NULL, 2, NULL, 0, 'Enjoy as a steak with your choice of sides or slice (after the meat has rested for 10 minutes) against the grain and serve with your favorite lettuce for a main-course salad.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7FA5FD4858E8B4486EF91AD596D2075', X'D9D162839D9C7A48A5E9038B728F5C30', X'D9D162839D9C7A48A5E9038B728F5C30', 0, NULL, NULL, 2, NULL, 0, 'Combine all marinade ingredients in a medium bowl. Place the tri-tip roast in a large resealable plastic bag. Pour marinade over meat, seal bag and allow to marinate for 4 to 6 hours in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52D9BA4263623D4EAFE7A08DFCB34B05', X'D9D162839D9C7A48A5E9038B728F5C30', X'D9D162839D9C7A48A5E9038B728F5C30', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill on medium heat and prepare for indirect cooking. Remove roast from the pan making sure to reserve marinade. Place roast onto a lightly oiled grill grate and allow to cook until internal temperature reaches 150 to 165 F or about 25 to 35 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F0CACCAF4C9B848A2DD92A7E82F0FF9', X'D9D162839D9C7A48A5E9038B728F5C30', X'D9D162839D9C7A48A5E9038B728F5C30', 2, NULL, NULL, 2, NULL, 0, 'While the tri-tip is cooking, it is time to prepare the sauce. Using side burner and a medium saucepan, bring reserved marinade to boil. Combine 1 tablespoon of cornstarch with 3 tablespoons of water. Add mixture to marinade. Stir until the sauce has thickened. Reduce heat to low and allow to simmer for an additional 3 to 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8CC5E7ED43E3E4C99143093226CAA54', X'D9D162839D9C7A48A5E9038B728F5C30', X'D9D162839D9C7A48A5E9038B728F5C30', 3, NULL, NULL, 2, NULL, 0, 'Once tri-tip has reached the desired doneness, remove from grill and tent with aluminum foil, and allow meat to rest for 10 minutes. Carve roast, top with sauce, and serve with you favorite sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EA6116FAAF1FE4A88F2658E2B4D89C0', X'D9D162839D9C7A48A5E9038B728F5C30', X'D9D162839D9C7A48A5E9038B728F5C30', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B34EFA520E70B49938AFB8E9AEA0797', X'07088ED9F87F4E4CB86B7BE726EFD990', X'07088ED9F87F4E4CB86B7BE726EFD990', 0, NULL, NULL, 2, NULL, 0, 'Combine minced garlic, olive oil, and dried oregano in a bowl. Place steaks on a large plate and coat well on both sides with mixture. Cover lightly with plastic and place in refrigerator for 2 hours. Remove from fridge 30 minutes before going onto the grill. This will help the meat come up to temperature before coming into contact with heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DBFC874F353444897F8CE9D88D7D857', X'07088ED9F87F4E4CB86B7BE726EFD990', X'07088ED9F87F4E4CB86B7BE726EFD990', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC782199C6435346BB11040DD09E8FD1', X'07088ED9F87F4E4CB86B7BE726EFD990', X'07088ED9F87F4E4CB86B7BE726EFD990', 2, NULL, NULL, 2, NULL, 0, 'Remove plastic wrap from steaks and season both sides of meat with salt and black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'699CAB90A1DBD34C8816F87A3C5C7580', X'07088ED9F87F4E4CB86B7BE726EFD990', X'07088ED9F87F4E4CB86B7BE726EFD990', 3, NULL, NULL, 2, NULL, 0, 'Place steaks on the grill and cook for 5 to 6 minutes per side, depending on thickness and desired doneness. Once cooked to preference, remove from heat and set onto a cutting board. Lightly tent with aluminum foil. Do not wrap tightly. Let steaks rest for 7 to 10 minutes. Slice and serve or serve steaks whole with your favorite side dishes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F5884C8CCC3B049995E9E36B3D9E87A', X'C060E554A4987F4391363810763151BE', X'C060E554A4987F4391363810763151BE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7D766DD0A7AFA49875E228C07CBB1CB', X'C060E554A4987F4391363810763151BE', X'C060E554A4987F4391363810763151BE', 1, NULL, NULL, 2, NULL, 0, 'In a large nonreactive baking dish or container, whisk together the soy sauce, garlic, dry mustard, and the coarsely ground black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F78B33F585EBD14E81745D33BA69DF1B', X'C060E554A4987F4391363810763151BE', X'C060E554A4987F4391363810763151BE', 2, NULL, NULL, 2, NULL, 0, 'Do not rinse the steaks, but pat them dry with a paper towel. Add them to the marinade and turn them to coat thoroughly. Cover with plastic wrap and refrigerate for 2 to 5 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D62130DA2DE57A42ADBAA05F7C38811D', X'C060E554A4987F4391363810763151BE', X'C060E554A4987F4391363810763151BE', 3, NULL, NULL, 2, NULL, 0, 'When ready to cook, oil the grill and prepare it for high direct heat, or about 450 F. If broiling, turn the oven to BROIL.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8815DA3280C2845B3A255AE440065EE', X'C060E554A4987F4391363810763151BE', X'C060E554A4987F4391363810763151BE', 4, NULL, NULL, 2, NULL, 0, 'When the grill or broiler has come up to temperature, remove the steaks from the marinade, discard the marinade, and place the steaks on your chosen cooking surface.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2B3974443CB134B87F4DBC8A560A9BE', X'C060E554A4987F4391363810763151BE', X'C060E554A4987F4391363810763151BE', 5, NULL, NULL, 2, NULL, 0, 'Grill the steaks until the desired temperature and doneness has been reached, using tongs to turn the steaks only once. To obtain grill marks, halfway through cooking the first side, turn it perpendicular on the grates to create the crosshatch pattern. After turning the steak over (remember: only once), repeat halfway through on the second side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6C9F30625ECBC4AA2F6B5FA38E41110', X'C060E554A4987F4391363810763151BE', X'C060E554A4987F4391363810763151BE', 6, NULL, NULL, 2, NULL, 0, 'Remove from the grill and serve with your favorite side dishes like a baked potato or French fries and a leafy green salad.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'425EEFBE2161DF47BD67F4DBCF1B7B61', X'C060E554A4987F4391363810763151BE', X'C060E554A4987F4391363810763151BE', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'184836FF253AD841A125FABCF88DE712', X'F31D1854BB5B4948A4503FD1AEEFF586', X'F31D1854BB5B4948A4503FD1AEEFF586', 0, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3116E5E631E9D4D815A201C465EA69E', X'F31D1854BB5B4948A4503FD1AEEFF586', X'F31D1854BB5B4948A4503FD1AEEFF586', 1, NULL, NULL, 2, NULL, 0, 'In a medium skillet, heat the oil and add the chopped onions. Cook for 1 minute.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69B9A3B8EFC2974D89764CFCB6F3BC7E', X'F31D1854BB5B4948A4503FD1AEEFF586', X'F31D1854BB5B4948A4503FD1AEEFF586', 2, NULL, NULL, 2, NULL, 0, 'Add mushrooms to the skillet and saute for 3 to 4 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60037D0B31D4924187FC83630AF90AAD', X'F31D1854BB5B4948A4503FD1AEEFF586', X'F31D1854BB5B4948A4503FD1AEEFF586', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic to the skillet and cook for 1 minute. Remove from heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5B9D9D44462E74781EC9D31C669E696', X'F31D1854BB5B4948A4503FD1AEEFF586', X'F31D1854BB5B4948A4503FD1AEEFF586', 4, NULL, NULL, 2, NULL, 0, 'In a medium saucepan combine the cream cheese, half-and-half, and pepper. Blend well. Cook over medium heat until the mixture is smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'310CE9CB9AE16E4E8D282FCC478DAA57', X'F31D1854BB5B4948A4503FD1AEEFF586', X'F31D1854BB5B4948A4503FD1AEEFF586', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked mushroom, onion, and garlic to the saucepan mixture. Keep warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B3C226269680D43A964D99E6734975F', X'F31D1854BB5B4948A4503FD1AEEFF586', X'F31D1854BB5B4948A4503FD1AEEFF586', 6, NULL, NULL, 2, NULL, 0, 'Place the steaks on the grill and cook them until done to your liking, about 5 minutes per side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0141DAF623706244834178C8168A196F', X'F31D1854BB5B4948A4503FD1AEEFF586', X'F31D1854BB5B4948A4503FD1AEEFF586', 7, NULL, NULL, 2, NULL, 0, 'Remove the steaks from the grill and let them rest for 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B4F14729398244DAF3C7A9E5E986214', X'F31D1854BB5B4948A4503FD1AEEFF586', X'F31D1854BB5B4948A4503FD1AEEFF586', 8, NULL, NULL, 2, NULL, 0, 'Place the steaks on individual serving plates. Pour the cream cheese and mushroom sauce over the steaks, top with chopped parsley, and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'374030C744FD294080CDF7985C8D9FF1', X'96CBEF27D7E16D41A2796717A1B07027', X'96CBEF27D7E16D41A2796717A1B07027', 0, NULL, NULL, 2, NULL, 0, 'Place steaks in a resealable plastic bag. Combine marinade ingredients, and pour over steaks. Make sure to work marinade well into the meat. Seal bag, and place into the refrigerator for 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'947FB82FC6253F4E976CAB72149B99F6', X'96CBEF27D7E16D41A2796717A1B07027', X'96CBEF27D7E16D41A2796717A1B07027', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat. While the grill is heating up, slice onion, and heat olive oil in large skillet on the stove. Add onion, salt, and black pepper. Allow the onion to cook on high for 2 minutes, reduce heat to medium-low, and cook for 5 minutes, turning often. Add wine and Worcestershire sauce, let cook for 2 to 3 minutes. Add stock, reduce heat to low, and cook for 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEB4674BDFBDAE47B0D0D361C4897F45', X'96CBEF27D7E16D41A2796717A1B07027', X'96CBEF27D7E16D41A2796717A1B07027', 2, NULL, NULL, 2, NULL, 0, 'Place steaks on the grill, and cook for 10 minutes turning often. Steaks should cook quickly since they are relatively small and thin. If they are thicker, leave on for an additional 2-3 minutes. Remove from heat, and place onto the cutting board. Allow meat to rest for a few minutes before slicing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D462A4111EC6B842B79422A3CF8D372A', X'96CBEF27D7E16D41A2796717A1B07027', X'96CBEF27D7E16D41A2796717A1B07027', 3, NULL, NULL, 2, NULL, 0, 'Prepare aioli by combining mayonnaise, mustard, and chili powder. If making ahead of time, place into an airtight container, and store in the fridge for up to 2 days after preparation.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAA755121F32C544BFE2D4F90DC219F8', X'96CBEF27D7E16D41A2796717A1B07027', X'96CBEF27D7E16D41A2796717A1B07027', 4, NULL, NULL, 2, NULL, 0, 'To assemble, place Swiss on the lower bun, add 6 to 8 slices of meat, 1/4 cup/60 mL of caramelized onion, and spread a generous amount of aioli on remaining inside half of the bun before topping sandwich.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92713B6ABF8BFB4D968F4C830118982D', X'4457B02943092647A96429B87983D778', X'4457B02943092647A96429B87983D778', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'230E82CF3454874A816B48F75238837E', X'4457B02943092647A96429B87983D778', X'4457B02943092647A96429B87983D778', 1, NULL, NULL, 2, NULL, 0, 'Place the steak in a shallow dish. Use a fork to prick the steak deeply, all over its surface, on both sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F71269C8623E064A9F294F12078A625C', X'4457B02943092647A96429B87983D778', X'4457B02943092647A96429B87983D778', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine remaining ingredients and mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D49756CB7259174CA145993AEA1C1A5E', X'4457B02943092647A96429B87983D778', X'4457B02943092647A96429B87983D778', 3, NULL, NULL, 2, NULL, 0, 'Pour over steak; cover and refrigerate for 1 to 24 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A39866E5A6560C45907A6DAC559E44DB', X'4457B02943092647A96429B87983D778', X'4457B02943092647A96429B87983D778', 4, NULL, NULL, 2, NULL, 0, 'When ready to grill, prepare and preheat grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'225A924BE0ADF24DA8479F5E47B7E86A', X'4457B02943092647A96429B87983D778', X'4457B02943092647A96429B87983D778', 5, NULL, NULL, 2, NULL, 0, 'Cook steak over medium coals for 3 to 4 minutes on each side, turning once, until medium doneness. This steak should not be cooked to medium-well or well-done, or it will be very tough.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16C7AE8FC328484285177A5FBC97F517', X'4457B02943092647A96429B87983D778', X'4457B02943092647A96429B87983D778', 6, NULL, NULL, 2, NULL, 0, 'Place on cutting board, cover with foil and let stand 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E46FD078D2BBEE4FAC11BADF9ACF68B3', X'4457B02943092647A96429B87983D778', X'4457B02943092647A96429B87983D778', 7, NULL, NULL, 2, NULL, 0, 'To serve, position the steak, so the grain is running from left to right. Hold the steak using tongs with your non-dominant hand. Using a very sharp knife, place the knife on the steak, then tilt it to the left. Slice the steak thinly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCDBA1CFB617B148A0E0B8A455A5938C', X'4457B02943092647A96429B87983D778', X'4457B02943092647A96429B87983D778', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFB6BEF3B1ADC24480F1C1EFF16C1CDA', X'4715838C84963C41B8BDA1F2FAFA484B', X'4715838C84963C41B8BDA1F2FAFA484B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'646533FB369ED142892250774302B33F', X'4715838C84963C41B8BDA1F2FAFA484B', X'4715838C84963C41B8BDA1F2FAFA484B', 1, NULL, NULL, 2, NULL, 0, 'Place steaks in a resealable plastic bag. Place remaining ingredients in blender or food processor. Blend mixture until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8314DEF36E953D4E9A68D8AE9786376E', X'4715838C84963C41B8BDA1F2FAFA484B', X'4715838C84963C41B8BDA1F2FAFA484B', 2, NULL, NULL, 2, NULL, 0, 'Pour half of mixture into bag with steaks. Make sure meat is well coated.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F3F61169748E645AEDCAED6EF177137', X'4715838C84963C41B8BDA1F2FAFA484B', X'4715838C84963C41B8BDA1F2FAFA484B', 3, NULL, NULL, 2, NULL, 0, 'Release air from the bag, seal, and place steaks into a refrigerator for 6-24 hours. Store remaining half of marinade in an airtight plastic container in a refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A4A9C5ADD684949B2A6F97E052AAD7F', X'4715838C84963C41B8BDA1F2FAFA484B', X'4715838C84963C41B8BDA1F2FAFA484B', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill. Remove steaks from bag and discard marinade. Grill steaks on medium- heat until they reach the desired doneness. Once cooked, remove heat and let rest.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E93D0DFBD24BA41804D0C2CC75AE19D', X'4715838C84963C41B8BDA1F2FAFA484B', X'4715838C84963C41B8BDA1F2FAFA484B', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, place reserved marinade in a small saucepan. Bring mixture to a boil, then reduce heat to medium-low and let simmer for 5-6 minutes, stirring often.');
