INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A3E9C74B00BEA42B7CEF281AC2C6505', X'33AF14AB52FBD04DAD70C4853A2222A6', X'33AF14AB52FBD04DAD70C4853A2222A6', 2, NULL, NULL, 2, NULL, 0, 'If you still have some room for direct grilling, lightly brush onions and peppers and place over direct heat for about one minute before moving to the griddle. Otherwise, pour 1 tablespoon/15 mL of the oil on the griddle.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9428E7138617F0459A1485B14D7CAE70', X'33AF14AB52FBD04DAD70C4853A2222A6', X'33AF14AB52FBD04DAD70C4853A2222A6', 3, NULL, NULL, 2, NULL, 0, 'Add onions and peppers. Cook until browned and soft.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F07F82FF43282F40899386B0B023B666', X'33AF14AB52FBD04DAD70C4853A2222A6', X'33AF14AB52FBD04DAD70C4853A2222A6', 4, NULL, NULL, 2, NULL, 0, 'Move aside and pour on another tablespoon of oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8155926AC4D36242B45CB0C612BD4B53', X'33AF14AB52FBD04DAD70C4853A2222A6', X'33AF14AB52FBD04DAD70C4853A2222A6', 5, NULL, NULL, 2, NULL, 0, 'Add beef. Toss shredded beef on the hot griddle until it is cooked through and completely browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB49071F173CE140B0823F867EC30096', X'33AF14AB52FBD04DAD70C4853A2222A6', X'33AF14AB52FBD04DAD70C4853A2222A6', 6, NULL, NULL, 2, NULL, 0, 'Pile into four mounds. Top with onions and peppers.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DD693385D485D41801259157632C989', X'33AF14AB52FBD04DAD70C4853A2222A6', X'33AF14AB52FBD04DAD70C4853A2222A6', 7, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper and top with cheese slices.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32871925966029459CCB4FDF77ADDDBD', X'33AF14AB52FBD04DAD70C4853A2222A6', X'33AF14AB52FBD04DAD70C4853A2222A6', 8, NULL, NULL, 2, NULL, 0, 'When the cheese starts to melt scoop up the mound with a large spatula and slide it into a roll.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD21FD8232B7B04DB22C7210AECFA523', X'33AF14AB52FBD04DAD70C4853A2222A6', X'33AF14AB52FBD04DAD70C4853A2222A6', 9, NULL, NULL, 2, NULL, 0, 'Serve with sweet peppers and lots of napkins.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C057169F36EFE469E269AE31DCF505A', X'33AF14AB52FBD04DAD70C4853A2222A6', X'33AF14AB52FBD04DAD70C4853A2222A6', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C19B0E81F6CAC449B4C0345A09D88C9', X'02D7D48724F3EB499BC66E94C3942C47', X'02D7D48724F3EB499BC66E94C3942C47', 0, NULL, NULL, 2, NULL, 0, 'Place skirt steak in resealable plastic bag. Combine marinade ingredients in a bowl. Pour over skirt steaks, making sure to evenly distribute marinade all over the meat. Seal bag and place into refrigerator for 2 to 5 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E12A3BAF37BE749B381E94E4C82DA72', X'02D7D48724F3EB499BC66E94C3942C47', X'02D7D48724F3EB499BC66E94C3942C47', 1, NULL, NULL, 2, NULL, 0, 'If using a charcoal grill, you want a two layer fire, with the coals nice and hot. Place marinated steaks onto grill grate and cook for 3 to 5 minutes per side, with the lid off. Make sure to watch it closely as these steaks cook quickly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D56AB75A095B046BDCBF82EAA220719', X'02D7D48724F3EB499BC66E94C3942C47', X'02D7D48724F3EB499BC66E94C3942C47', 2, NULL, NULL, 2, NULL, 0, 'Once cooked through remove from fire and allow to rest for 5 minutes. Slice against the grain into thin strips, serve onto warmed tortillas, and top with corn salsa, sliced green onion, guacamole, and chopped cilantro.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AA294A4A3755F48B3B38D924CF23068', X'02D7D48724F3EB499BC66E94C3942C47', X'02D7D48724F3EB499BC66E94C3942C47', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80576B2FE951AD4EA420A8A87E230CB4', X'C6E298D66A42064CA3FEE3C12215F194', X'C6E298D66A42064CA3FEE3C12215F194', 0, NULL, NULL, 2, NULL, 0, 'Combine vinegar, mustard, salt, black pepper, allspice, rosemary and oil in large, shallow glass dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E741342E7915E5488225EE8DD0F89F5B', X'C6E298D66A42064CA3FEE3C12215F194', X'C6E298D66A42064CA3FEE3C12215F194', 1, NULL, NULL, 2, NULL, 0, 'Add steak and onions; turn to coat. Let stand 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'256EED8A34022B4AA4FEA387B3AB3B1C', X'C6E298D66A42064CA3FEE3C12215F194', X'C6E298D66A42064CA3FEE3C12215F194', 2, NULL, NULL, 2, NULL, 0, 'Grill steak and onions 12 to 16 minutes (depending on thickness and desired doneness), turning a few time? and brushing with marinade during the first half of cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15A5C561DCCAD64F9FC24C0E065444D4', X'C6E298D66A42064CA3FEE3C12215F194', X'C6E298D66A42064CA3FEE3C12215F194', 3, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat, let meat rest for 5 minutes and then slice steak across the grain.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'849962312E9D604CA5E14632FEC28A1B', X'C6E298D66A42064CA3FEE3C12215F194', X'C6E298D66A42064CA3FEE3C12215F194', 4, NULL, NULL, 2, NULL, 0, 'Serve with onions.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'937B2DCE8860784ABC8DFAF2AACD7367', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 0, NULL, NULL, 2, NULL, 0, 'Finely chop garlic, shallot and onion and place in a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C2ADA7BED94684E8EB26BB2B091DD48', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 1, NULL, NULL, 2, NULL, 0, 'Finely chop parsley and tarragon and add to the bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1F1CBF63F806146B3C33F2FEEFB1276', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 2, NULL, NULL, 2, NULL, 0, 'Add the Worcestershire sauce, Dijon mustard, Guinness beer, soy sauce and seasonings then mix well to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15301CC6C9210F4CBAF9387B60D6FDC7', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 3, NULL, NULL, 2, NULL, 0, 'Let stand for 30 minutes to blend flavors. Place the rib eye in a resealable plastic bag and add marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42D9E061A05AB0419B8728759EBF0C96', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 4, NULL, NULL, 2, NULL, 0, 'Make sure steaks are well coated. Seal bag and place into refrigerator for 2 to 8 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'109AEA9222C3224AA058EB961841CF8F', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 5, NULL, NULL, 2, NULL, 0, 'Preheat the grill. Remove steak from bag making sure to reserve marinade. Place steak onto the grill and cook for 6 to 8 minutes per side, or until it has reached the desired doneness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D575B3CC3A33E44692B77D6EE77BABE5', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 6, NULL, NULL, 2, NULL, 0, 'Add remaining marinade in a small saucepan and simmer sauce over medium-high heat for 5 minutes or until slightly thickened. Make sure to stir often.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B076D02A236F754CAEAE854581B91042', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 7, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium or medium-low if necessary. Add more beer to the marinade at this stage if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC59959238D2674D8C53AD0D76B1DD31', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 8, NULL, NULL, 2, NULL, 0, 'Once cooked through, remove sauce from heat and let stand until steaks are ready.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA647F3E98250743ACABD8737170A208', X'3A834E0A4D634F4E81F149E41BD8E321', X'3A834E0A4D634F4E81F149E41BD8E321', 9, NULL, NULL, 2, NULL, 0, 'Serve steaks with sauce on top.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33C1DED4A96D3344A3A9380127B451C1', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat. (You can use a grill pan for indoor grilling as well.)');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86B15BF742184F448243CCFB1E0AB206', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine ground lamb, chopped onion, bread crumbs, chopped mint, egg, garlic, cumin, black pepper, and Aleppo pepper (or pepper flakes).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA5193E6DE09C42A6928D47B771E67F', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 2, NULL, NULL, 2, NULL, 0, 'Do not add salt at this point. Gently mix with hands until all ingredients are well combined. Form into 4 equal-sized patties and place onto a parchment-lined baking sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E73C44EAA2B3BB41BDF182C2FDF8712B', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 3, NULL, NULL, 2, NULL, 0, 'Using your thumb, make indentations halfway through the center of each patty. This will help the burgers to cook more evenly and not bubble up in the center.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6023AD8ADE7CAA478EB61FB3E96D24ED', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 4, NULL, NULL, 2, NULL, 0, 'Using folded paper towels and a pair of large grilling tongs (if you are grilling outdoors), oil the grill grates well. Make at least three passes, making sure that the surface is nice and coated.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CC6BC09F684BC4B81F85128244EDCB1', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 5, NULL, NULL, 2, NULL, 0, 'Place lamb burger patties onto grill and cook for 4-5 minutes per side, or until meat has reached the desired doneness. Use a large outdoor cooking spatula to flip these burgers. They will be a bit softer than beef burgers, not only because of the texture of ground lamb but because these patties also contain some wet ingredients. If you are grilling indoors, oil up the grilling pan, using the same method as you would outdoors, and use a regular-sized spatula to flip the burgers.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF1C88CF14481A4E9149E9A3270C11EA', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 6, NULL, NULL, 2, NULL, 0, 'Once cooked to your liking, remove from grill, place onto a plate and tent with aluminum foil. Brush burger buns with olive oil (vegetable oil work well too), and place oiled side down onto grill grates. Close the lid and let buns toast for 1 to 2 minutes. (If you are not grilling outdoors, toast your buns in a toaster oven for the same amount of time on the medium-low setting.) Remove. If using pita, place into a large piece of aluminum foil, seal edges and place onto the grill for 3 minutes, turn, and warm for another 3-4 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03AF49EB8175D4AB7DFD920EFF6A419', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 7, NULL, NULL, 2, NULL, 0, 'We recommend either a red pepper aioli or yogurt sauce for these burgers. For red pepper aioli, simply add the roasted red peppers to the mayonnaise in a food processor. Add the lemon juice, salt, and olive oil and blend to desired consistency.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F89B3E89ECC55F4183E6F86F97C200AB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 8, NULL, NULL, 2, NULL, 0, 'For yogurt sauce, in a bowl add the cucumber, plain yogurt, minced garlic, cilantro, and salt, and stir together, until completely combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C2C3F3BAC42034D9E53FE42BBEA48EF', X'91B8D8F8452DDE4FADEFED1E18C41DFB', X'91B8D8F8452DDE4FADEFED1E18C41DFB', 9, NULL, NULL, 2, NULL, 0, 'Assemble burgers to your liking and add extra items such as red onion and lettuce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E337B7E1DB774479A008CA9C578DF10', X'A520178562F7B048A72E02A4E74E1C97', X'A520178562F7B048A72E02A4E74E1C97', 0, NULL, NULL, 2, NULL, 0, 'Combine garlic, oregano, lemon juice and olive oil in a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1A95FE02445A645916076C5DDF97BAB', X'A520178562F7B048A72E02A4E74E1C97', X'A520178562F7B048A72E02A4E74E1C97', 1, NULL, NULL, 2, NULL, 0, 'Season marinade with kosher salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A227262182C48441A73ABFE8C743A45C', X'A520178562F7B048A72E02A4E74E1C97', X'A520178562F7B048A72E02A4E74E1C97', 2, NULL, NULL, 2, NULL, 0, 'Add lamb cubes; toss to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'632603991D6A0A4DA8FCA86BACEE68DB', X'A520178562F7B048A72E02A4E74E1C97', X'A520178562F7B048A72E02A4E74E1C97', 3, NULL, NULL, 2, NULL, 0, 'Cover and marinate in refrigerator 4 to 6 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B30C28119D8434A804C6A9C75722AF7', X'A520178562F7B048A72E02A4E74E1C97', X'A520178562F7B048A72E02A4E74E1C97', 4, NULL, NULL, 2, NULL, 0, 'Drain meat cubes; discard marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E2F8EC2F47D4243B552C3DBCB86B1E9', X'A520178562F7B048A72E02A4E74E1C97', X'A520178562F7B048A72E02A4E74E1C97', 5, NULL, NULL, 2, NULL, 0, 'Thread lamb, tomatoes, and onion onto skewers.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5968BF33C1398242B354D632D896C325', X'A520178562F7B048A72E02A4E74E1C97', X'A520178562F7B048A72E02A4E74E1C97', 6, NULL, NULL, 2, NULL, 0, 'Grill on medium heat for 12 to 15 minutes or to the desired degree of doneness; a reliable thermometer should register 145 F/65 C for medium-rare, 160 F/70 C for medium or 170 F/75 C for well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CEED3B3D88AFF479F2F857D7780EF9B', X'A520178562F7B048A72E02A4E74E1C97', X'A520178562F7B048A72E02A4E74E1C97', 7, NULL, NULL, 2, NULL, 0, 'Turn kabobs several times, brushing with marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A96C963C21FDC64C91DAB0D457B62041', X'A520178562F7B048A72E02A4E74E1C97', X'A520178562F7B048A72E02A4E74E1C97', 8, NULL, NULL, 2, NULL, 0, 'Serve with warm pita bread and Tzatziki sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4F9FA1D6FF4A5439FEB605DF95BABDB', X'BD717D4BCB42EE4F8E875759F2329E4A', X'BD717D4BCB42EE4F8E875759F2329E4A', 0, NULL, NULL, 2, NULL, 0, 'Trim racks of all visible fat. Combine remaining ingredients and mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'948AC4F4EA970B4BA462C9E98EBF2431', X'BD717D4BCB42EE4F8E875759F2329E4A', X'BD717D4BCB42EE4F8E875759F2329E4A', 1, NULL, NULL, 2, NULL, 0, 'Place lamb in a large glass baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0384B52FEDDA6478EE2E03341F699B5', X'BD717D4BCB42EE4F8E875759F2329E4A', X'BD717D4BCB42EE4F8E875759F2329E4A', 2, NULL, NULL, 2, NULL, 0, 'Spread mixture over lamb, carefully cover with plastic wrap and place into refrigerator for 6 to 24 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AA2DD0F40E6D842B721494B25552F59', X'BD717D4BCB42EE4F8E875759F2329E4A', X'BD717D4BCB42EE4F8E875759F2329E4A', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24F7319776D4E647B11F5D5B03AE2FDC', X'BD717D4BCB42EE4F8E875759F2329E4A', X'BD717D4BCB42EE4F8E875759F2329E4A', 4, NULL, NULL, 2, NULL, 0, 'Place lamb on grill over medium-high heat and sear for a couple of minutes per side, reduce heat or move off of direct heat and continue grilling until the internal temperature reaches 140 to 150 F. (60 to 65 C.) or about 15 minutes per side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04500109F37FE14D80106946F1345DFC', X'BD717D4BCB42EE4F8E875759F2329E4A', X'BD717D4BCB42EE4F8E875759F2329E4A', 5, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat and let rest for about 5 minutes before carving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B1D533EAB0DC9439636631694CD1097', X'078A8AEC3C781848B150FD82B75043B7', X'078A8AEC3C781848B150FD82B75043B7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA3745AAF261E8489B9BAD4AB5EB5B7D', X'078A8AEC3C781848B150FD82B75043B7', X'078A8AEC3C781848B150FD82B75043B7', 1, NULL, NULL, 2, NULL, 0, 'Combine the extra-virgin olive oil and white wine in a large mixing bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94B1F0BAB5C9524183801374D181F1EC', X'078A8AEC3C781848B150FD82B75043B7', X'078A8AEC3C781848B150FD82B75043B7', 2, NULL, NULL, 2, NULL, 0, 'Crush the juniper berries, peppercorns, garlic, and 1 teaspoon fine sea salt together in a mortar and pestle until you obtain a rough paste.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF39C4EB6DF0ED4A80A8D6106D0C2D3E', X'078A8AEC3C781848B150FD82B75043B7', X'078A8AEC3C781848B150FD82B75043B7', 3, NULL, NULL, 2, NULL, 0, 'Transfer this paste to the olive oil-and-wine mixture and stir to mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CBD853EA51AE843BA0242E82D2AB365', X'078A8AEC3C781848B150FD82B75043B7', X'078A8AEC3C781848B150FD82B75043B7', 4, NULL, NULL, 2, NULL, 0, 'Tear the bay leaves roughly and stir them in as well. Add the lemon slices (squeezing them just a bit before adding them to the marinade), and marinate the lamb in this marinade for at least 12 hours in the refrigerator, turning the pieces occasionally.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF63CBFAFBCE4E4D90A82D6AE97B0ECE', X'078A8AEC3C781848B150FD82B75043B7', X'078A8AEC3C781848B150FD82B75043B7', 5, NULL, NULL, 2, NULL, 0, 'Shake off any excess liquid before cooking the lamb chops. You can either cook the marinated lamb chops in a skillet over a brisk flame or grill them on charcoal (preferable) or gas grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7B555FCC2F3594092B8F063AF56AC1B', X'078A8AEC3C781848B150FD82B75043B7', X'078A8AEC3C781848B150FD82B75043B7', 6, NULL, NULL, 2, NULL, 0, 'In any case, lay lemon slices from the marinade over the chops as they cook, turn the chops occasionally, and season them well with sea salt and freshly ground black pepper when they''re almost done. It should take about 8 to 10 minutes for them to be done.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC91FC6312F326408D609A4EDA11CBA6', X'078A8AEC3C781848B150FD82B75043B7', X'078A8AEC3C781848B150FD82B75043B7', 7, NULL, NULL, 2, NULL, 0, 'Serve with lemon wedges that can be squeezed over the lamb before eating.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D38DEF5E6B71747A58E03FDD3191522', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC0FE785E36376499EDBF819814EADAE', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 1, NULL, NULL, 2, NULL, 0, 'Empty the can of beer into a container.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CE3C2532103F54799B7C22B3C06E672', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 2, NULL, NULL, 2, NULL, 0, 'Cut off the top of the can and pour in 12 ounces of beer.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32AD2A1EAA745940AF93AE3CAA7297A1', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 3, NULL, NULL, 2, NULL, 0, 'Add the bay leaves and thyme. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4033E93C97ED543960ED3777465E957', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 4, NULL, NULL, 2, NULL, 0, 'Make the rub by combining 2 tablespoons brown sugar with the paprika, salt, and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B28B3A1E9E248E4699591FD1371B6839', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 5, NULL, NULL, 2, NULL, 0, 'Make the turkey baste. In a separate bowl, combine the remaining 2 tablespoons brown sugar, the ketchup, vinegar, 2 tablespoons of beer, hot sauce, and cayenne.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76EB7AE9FCF9E94EBDDE8D98FCFC27DB', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 6, NULL, NULL, 2, NULL, 0, 'Prepare smoker for a 6-hour smoke at 250 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C552C2DB8AB2946AE434C03213D30D9', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 7, NULL, NULL, 2, NULL, 0, 'Rub surface of the turkey with the spice rub. Try to get as much as you can under the skin, particularly over the breast meat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1423A4C0DE3F8148BEA16CEEE667312A', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 8, NULL, NULL, 2, NULL, 0, 'Stand beer can up on the smoker grate and gently set the turkey over the can so that the can is completely inside the cavity of the turkey. Make sure that the turkey stands easily and doesn''t sway. The turkey needs to be stable.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B6EF8ADC0CB4B40BD8786B41A32D904', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 9, NULL, NULL, 2, NULL, 0, 'Once you have the turkey in the smoker, let it cook for about 6 hours at a temperature around 250 F. Baste the turkey with the basting mixture every 2 hours during ?the cooking process. Check for an internal temperature in the thigh of about 165 F/74 C. This is when the bird is done.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'734B209894379141BBF51469F464E77F', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 10, NULL, NULL, 2, NULL, 0, 'When cooked, remove from heat and let sit for a few minutes before carving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91A2ED35B53C1B49892A777B9C53026B', X'816FF18B2C24844CAC48080EF4517027', X'816FF18B2C24844CAC48080EF4517027', 11, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CBAE15C6FFB8241A563DB692762772E', X'D344AFDB16E5BC4FB1261ABCB78416D5', X'D344AFDB16E5BC4FB1261ABCB78416D5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7638FF1B8B4A54F95D2C05FF084CE10', X'D344AFDB16E5BC4FB1261ABCB78416D5', X'D344AFDB16E5BC4FB1261ABCB78416D5', 1, NULL, NULL, 2, NULL, 0, 'Cut butter into 8 quarter-inch wide slivers measuring an inch long. Push any extra butter into the folds created when the breast is wrapped. Salt and pepper all over.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAF5E23B4BF5954BBF9E8B4A6CE9CB17', X'D344AFDB16E5BC4FB1261ABCB78416D5', X'D344AFDB16E5BC4FB1261ABCB78416D5', 2, NULL, NULL, 2, NULL, 0, 'Make 8 half-inch slits in the top skin of the turkey breasts and slip butter into slits.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE0C9396355621438BAF138F1C13F4DA', X'D344AFDB16E5BC4FB1261ABCB78416D5', X'D344AFDB16E5BC4FB1261ABCB78416D5', 3, NULL, NULL, 2, NULL, 0, 'Cook for 5 minutes at high heat, then adjust grill to medium heat. Grill until the internal temperature reaches 175 F/80 C, about 25 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B27DABE90A2F484394BB60C97D36C9CC', X'D344AFDB16E5BC4FB1261ABCB78416D5', X'D344AFDB16E5BC4FB1261ABCB78416D5', 4, NULL, NULL, 2, NULL, 0, 'Remove and let cool 20 minutes before carving. If you need drippings for gravy, set a drip pan on bars or bricks under the rack, and remove it after the first 30 minutes or else it will burn off.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB2283D7950A6447A1D177F1E92D6D5D', X'D344AFDB16E5BC4FB1261ABCB78416D5', X'D344AFDB16E5BC4FB1261ABCB78416D5', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'856D95DCD798514CA7CC484EB34DC129', X'B6582E2484B1664C901B2B161AAE2A3F', X'B6582E2484B1664C901B2B161AAE2A3F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AC58FA6EFB02B428336E67AB02188B5', X'B6582E2484B1664C901B2B161AAE2A3F', X'B6582E2484B1664C901B2B161AAE2A3F', 1, NULL, NULL, 2, NULL, 0, 'Cook the onion and celery in butter over medium heat until soft, about 7 to 10 minutes. The object is to sweat the vegetables, not sauté them. They should have a glossier appearance rather than browning.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F111CAE5897E6A4B95017F3B46B38E93', X'B6582E2484B1664C901B2B161AAE2A3F', X'B6582E2484B1664C901B2B161AAE2A3F', 2, NULL, NULL, 2, NULL, 0, 'Add the remaining sauce ingredients to the pot. Simmer over medium-low heat for 5 minutes, stirring often. Add more brown sugar to make the sauce sweeter. Remove from heat and allow sauce to cool to room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B0CD83667C30A409EEF7092F8E4D62A', X'B6582E2484B1664C901B2B161AAE2A3F', X'B6582E2484B1664C901B2B161AAE2A3F', 3, NULL, NULL, 2, NULL, 0, 'Preheat the grill. Combine salt and black pepper and season the turkey legs.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D69A27E37B1BF4B953C76CEDC70BAE0', X'B6582E2484B1664C901B2B161AAE2A3F', X'B6582E2484B1664C901B2B161AAE2A3F', 4, NULL, NULL, 2, NULL, 0, 'Place turkey legs on the grill and cook over medium heat. Grill, covered for about 30 to 45 minutes or until nearly done. Turn occasionally.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A50DE56511AC634C8AE0282682C9B866', X'B6582E2484B1664C901B2B161AAE2A3F', X'B6582E2484B1664C901B2B161AAE2A3F', 5, NULL, NULL, 2, NULL, 0, 'Begin basting with sauce during the last 15 to 20 minutes of cook time. Watch out for burning. Reduce heat or move turkey legs to indirect portion of the grill to finish cooking. Once legs reach an internal temperature of 180 to 185 F, they''re done.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74BCECB6D11BCD46ABAF6701A1C117C5', X'B6582E2484B1664C901B2B161AAE2A3F', X'B6582E2484B1664C901B2B161AAE2A3F', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and loosely tent with aluminum foil. Let legs rest for 10 minutes before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'770C737F331F614CBA94BAC604D1B85B', X'B6582E2484B1664C901B2B161AAE2A3F', X'B6582E2484B1664C901B2B161AAE2A3F', 7, NULL, NULL, 2, NULL, 0, 'If you want to serve some of the sauce on the side, simply divide the sauce using one portion for basting and warming the reserved portion for serving. You can also double the sauce recipe if you really want to have quite a bit. The choice is all yours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83DD64441F81F74A888D577F29041354', X'920F7B8AEAF31C42AF58C072B5418F19', X'920F7B8AEAF31C42AF58C072B5418F19', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81AD0F85FAE86F409C5A7C817EC883FE', X'920F7B8AEAF31C42AF58C072B5418F19', X'920F7B8AEAF31C42AF58C072B5418F19', 1, NULL, NULL, 2, NULL, 0, 'Pat the turkey legs dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84AD1644FEBB2547957195C9E7B565C8', X'920F7B8AEAF31C42AF58C072B5418F19', X'920F7B8AEAF31C42AF58C072B5418F19', 2, NULL, NULL, 2, NULL, 0, 'In a large pot, combine the soda, sugar, hot sauce, red pepper flakes, and black pepper. Bring to a low boil and add the turkey legs.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16B9A00CC3031545B2BB7DCB1DB619BE', X'920F7B8AEAF31C42AF58C072B5418F19', X'920F7B8AEAF31C42AF58C072B5418F19', 3, NULL, NULL, 2, NULL, 0, 'Boil over medium heat for about 30 minutes or until the turkey legs register an internal temperature of 165 F/74 C. Preheat the grill to medium heat.');
