INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20B7F4C9AB865643B616C293EA3758B1', X'920F7B8AEAF31C42AF58C072B5418F19', X'920F7B8AEAF31C42AF58C072B5418F19', 5, NULL, NULL, 2, NULL, 0, 'Brush with the turkey legs honey and sprinkle them with the steak seasoning.');
