INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35A96D21D64F09448329DD569710D7AE', X'920F7B8AEAF31C42AF58C072B5418F19', X'920F7B8AEAF31C42AF58C072B5418F19', 7, NULL, NULL, 2, NULL, 0, 'Once cooked, remove the turkey legs from the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6D59D67CAE83647A37959DCB0BF1910', X'920F7B8AEAF31C42AF58C072B5418F19', X'920F7B8AEAF31C42AF58C072B5418F19', 8, NULL, NULL, 2, NULL, 0, 'Serve with your choice of sides and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F4315A039B24C43941453EAE9D16C4D', X'5D1E22F33FCF6941924E61485D121A6E', X'5D1E22F33FCF6941924E61485D121A6E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12FF4CDF7B56B244A6BF27EB9AE00889', X'5D1E22F33FCF6941924E61485D121A6E', X'5D1E22F33FCF6941924E61485D121A6E', 1, NULL, NULL, 2, NULL, 0, 'Trim the lamb of all excess fat and cut it into 1-inch cubes. Cut the bell peppers and onions into 1-inch pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34D976C3986DBF49BE4B4CCFFDD250FF', X'5D1E22F33FCF6941924E61485D121A6E', X'5D1E22F33FCF6941924E61485D121A6E', 2, NULL, NULL, 2, NULL, 0, 'In a small plastic or glass bowl, combine the marinade ingredients (olive oil, lemon juice, sea salt, cumin, grated ginger, and black pepper).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C3B08D8C9F1C547A032941C434DDFF9', X'5D1E22F33FCF6941924E61485D121A6E', X'5D1E22F33FCF6941924E61485D121A6E', 3, NULL, NULL, 2, NULL, 0, 'In a resealable container (a large zip-top bag works best), place the lamb pieces with the marinade. Massage them to mix and seal bag. Refrigerate the meat in the marinade for 2 to 24 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6B074B49AFEEE48855761B34E3DD2B6', X'5D1E22F33FCF6941924E61485D121A6E', X'5D1E22F33FCF6941924E61485D121A6E', 4, NULL, NULL, 2, NULL, 0, 'Preheat the grill to high heat. Thread lamb, onion, and pepper pieces onto skewers, intermixing the pieces or making skewers of just meat and just vegetables (see Recipe Tip below).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A10F966038E09F41BF229C1976F0269F', X'5D1E22F33FCF6941924E61485D121A6E', X'5D1E22F33FCF6941924E61485D121A6E', 5, NULL, NULL, 2, NULL, 0, 'Place the kebabs on the hot grill and cook for about 8 to 10 minutes, uncovered. Starting at the 2-minute mark, turn the skewers to cook another side of the cubes. Do this every 2 minutes until all four sides of the cubes have been grilled. Cook longer if lamb chunks are larger than 1-inch.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D6E7CEBC57C3A43A5B3FD70616E4B31', X'5D1E22F33FCF6941924E61485D121A6E', X'5D1E22F33FCF6941924E61485D121A6E', 6, NULL, NULL, 2, NULL, 0, 'Once cooked to your desired doneness, remove the kebabs from the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEBEBB64EB9108449DAC07D605CA839D', X'5D1E22F33FCF6941924E61485D121A6E', X'5D1E22F33FCF6941924E61485D121A6E', 7, NULL, NULL, 2, NULL, 0, 'Serve on or off of the skewers, with lemon and lime slices.');
