INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63BA0B3EF7872F4E86AB6D43F0A06804', X'A0C38F6274857940970A1583747376E8', X'A0C38F6274857940970A1583747376E8', 2, NULL, NULL, 2, NULL, 0, 'Place turkey in a resealable container or plastic bag, add minced garlic and brine, seal and allow to rest in refrigerator for up to 24 hours.');
