INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EBD94777EDC23499B7B55F37503B7D4', X'A0C38F6274857940970A1583747376E8', X'A0C38F6274857940970A1583747376E8', 6, NULL, NULL, 2, NULL, 0, 'Baste with honey-Dijon glaze during cooking process.');
