INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6840753984DAA4B9161A11AF161BE44', X'30C3C0F593C2FE4DAB02BCEB230D99A3', X'30C3C0F593C2FE4DAB02BCEB230D99A3', 5, NULL, NULL, 2, NULL, 0, 'Brush remaining mixture over turkey. You can reserve some of the baste to apply later, during the cooking process.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7152F03E8DF24F42B1F3946D08A6A294', X'30C3C0F593C2FE4DAB02BCEB230D99A3', X'30C3C0F593C2FE4DAB02BCEB230D99A3', 6, NULL, NULL, 2, NULL, 0, 'Place turkey in the smoker. When the internal temperature of the turkey reaches 165 F/75 C, the bird is done.');
