INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2F86605639BA04ABCAF742FB06F1411', X'11B2EFBC763A404E86427435C9AD7DA7', X'11B2EFBC763A404E86427435C9AD7DA7', 3, NULL, NULL, 2, NULL, 0, 'Remove turkey from marinade. Pour marinade into a small saucepan over medium heat and bring to a boil. Keep warm while the turkey is on the grill.');
