INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E045143E5A08A441B77514145EF26C45', X'11B2EFBC763A404E86427435C9AD7DA7', X'11B2EFBC763A404E86427435C9AD7DA7', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3802974EFB65B741847046BA8241E81A', X'E5499B397442A34A865CE6A4105459F8', X'E5499B397442A34A865CE6A4105459F8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D004EF3D8558124EB0EF4EE4CA262957', X'E5499B397442A34A865CE6A4105459F8', X'E5499B397442A34A865CE6A4105459F8', 1, NULL, NULL, 2, NULL, 0, 'Rinse the lemons clean. If you have any plans to use or otherwise eat the peel, you may even want to go so far as to scrub them with soap before rinsing them. Pat them dry. To release extra juice, roll them on a hard surface.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAEF5D44C1487A4FA0C8F9B12780F235', X'E5499B397442A34A865CE6A4105459F8', X'E5499B397442A34A865CE6A4105459F8', 2, NULL, NULL, 2, NULL, 0, 'Cut the lemons in half crosswise.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC6C97DA802B2442AD779696CAD64796', X'E5499B397442A34A865CE6A4105459F8', X'E5499B397442A34A865CE6A4105459F8', 3, NULL, NULL, 2, NULL, 0, 'Optional: Pop out and discard any visible seeds. Use the tip of a paring knife to urge any deeper-set seeds to the surface. This is a kind service to your fellow diners since it minimizes the chance that they''ll bite into a bitter seed while enjoying their lovely grilled lemons.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2D878AC3AFE0148A1AC24FD47CFF3C3', X'E5499B397442A34A865CE6A4105459F8', X'E5499B397442A34A865CE6A4105459F8', 4, NULL, NULL, 2, NULL, 0, 'Use a pastry brush, silicone grilling brush, or a paper towel to brush the cut sides with a bit of oil. You can sprinkle the cut sides with fine sea salt, too, if you want to add a more intense savory effect to the grilled lemons.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D4F9CE1847B3D4FA5908D1B71A81C66', X'E5499B397442A34A865CE6A4105459F8', X'E5499B397442A34A865CE6A4105459F8', 5, NULL, NULL, 2, NULL, 0, 'Set the lemons cut-side-down on the hot grill or in the frying pan. Cook until the lemons are heated through and charred on the cut side, about 3 minutes. Ideally, you''ll cook them without moving them so they char nicely, but adjust them if the heat seems uneven so they char evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'608B28674E9DD042BE0ED25F8E22A79F', X'E5499B397442A34A865CE6A4105459F8', X'E5499B397442A34A865CE6A4105459F8', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F30227BFBC3C5F4AB10CD00BF3F69596', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA76AAD629B05248BED1E0CE13883FD0', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 1, NULL, NULL, 2, NULL, 0, 'Cut salmon into serving pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C909CD2DA744D43A2AAE831353DCF09', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 2, NULL, NULL, 2, NULL, 0, 'Salt the salmon lightly and set it out at room temperature for 15 to 20 minutes before cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4975603E1A13D749939046E5A6E81E81', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 3, NULL, NULL, 2, NULL, 0, 'Fire up the grill. Make sure the surface is clean. Get some vegetable oil ready to wipe on the grill later.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F09632A38C99B244810200E54A38AA16', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 4, NULL, NULL, 2, NULL, 0, 'After 15 to 20 minutes of setting out, pat the salmon dry and then use a butter knife to scrape the skin side to get any moisture or scales off. This makes the skin side drier so it will not stick to the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0C8FF85DB05AA47B0F0DE61472A1276', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 5, NULL, NULL, 2, NULL, 0, 'Coat the salmon with oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55EA4F41B3F57749A7257D767EB38A6D', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 6, NULL, NULL, 2, NULL, 0, 'When the fire is good and hot, wipe down the grate with a paper towel that has been dipped in the oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EC132392628C54DB31344A70175B048', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 7, NULL, NULL, 2, NULL, 0, 'Put the salmon on the grill skin side up. It seems counterintuitive, but do it anyway because you want a good sear on the fish, and you cannot do this well if you start skin-side down; the fish will cook too much and will fall apart when you try to flip it. The uncooked skin helps hold things together.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6987CAAF1A68E947AB30C3B0D96A62CC', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 8, NULL, NULL, 2, NULL, 0, 'Leave the salmon on the grill for a solid 4 to 6 minutes, depending on thickness. A thin sockeye fillet might need closer to 3 minutes on one side. You will know it is ready when you can slide a spatula underneath the salmon. It might stick in one or two spots, but it should be mostly crusted over.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B664B62BC421948AF2F9E676F66FCA8', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 9, NULL, NULL, 2, NULL, 0, 'Gently flip the salmon onto the skin side and leave it again for at least 3 minutes, a bit longer if it is on the thick side. Exactly how long depends on thickness, but it''ll never be less than 2 minutes on high heat. Watch the fish, and if the flakes begin to separate, you''re done.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'144D38829E773149B6D0FFFA45E4B046', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 10, NULL, NULL, 2, NULL, 0, 'Take the salmon off the heat, sprinkle lemon juice on it, then let it rest for 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FC86AFD60258744913428FC3EDEA7FE', X'AD7090951F5BB549982A23A74BD5E2E5', X'AD7090951F5BB549982A23A74BD5E2E5', 11, NULL, NULL, 2, NULL, 0, 'Serve with your favorite sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E8C83260AF1A8439557B0C573CA15E1', X'0EEAD4A09B5AC444A7B90927E96A2463', X'0EEAD4A09B5AC444A7B90927E96A2463', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1D641D77EE691479218AA6429F0D65A', X'0EEAD4A09B5AC444A7B90927E96A2463', X'0EEAD4A09B5AC444A7B90927E96A2463', 1, NULL, NULL, 2, NULL, 0, 'To prepare the butter sauce, combine butter, lemon juice, garlic, and parsley in a small saucepan. Remove the pan from the heat once butter has melted.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9CCB136AC1E284B92B5745412DEADC5', X'0EEAD4A09B5AC444A7B90927E96A2463', X'0EEAD4A09B5AC444A7B90927E96A2463', 2, NULL, NULL, 2, NULL, 0, 'Preheat your grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2F9CCB21A773D4880197121DBE55924', X'0EEAD4A09B5AC444A7B90927E96A2463', X'0EEAD4A09B5AC444A7B90927E96A2463', 3, NULL, NULL, 2, NULL, 0, 'Right before placing the fish onto the grill, make sure to oil the grill grates. This can be achieved by using a large pair of tongs, folded paper towels, and a high smoke point oil (canola oil, peanut oil, sunflower oil). Olive oil will work in a pinch. Dip the paper towel into oil and run across the grates at least three times to create a non-stick surface. This will keep the fish from breaking during the cooking process.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F8B47868A617243AA6CD9AC425FF9D0', X'0EEAD4A09B5AC444A7B90927E96A2463', X'0EEAD4A09B5AC444A7B90927E96A2463', 4, NULL, NULL, 2, NULL, 0, 'Combine the onion powder, garlic, paprika, pepper, and salt in a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55A8C2AC5962E84B8C1EE03F6D5796F9', X'0EEAD4A09B5AC444A7B90927E96A2463', X'0EEAD4A09B5AC444A7B90927E96A2463', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the seasoning mixture on both sides of the fish.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B821E934576C248A849E95597533B6D', X'0EEAD4A09B5AC444A7B90927E96A2463', X'0EEAD4A09B5AC444A7B90927E96A2463', 6, NULL, NULL, 2, NULL, 0, 'Place the fish on the grill and cook for 7 minutes. Turn the fish and coat it with the butter sauce. Cook for about 7 more minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0A7C5EEB638A6429F0300BE81DD8122', X'0EEAD4A09B5AC444A7B90927E96A2463', X'0EEAD4A09B5AC444A7B90927E96A2463', 7, NULL, NULL, 2, NULL, 0, 'Once the fish reaches an internal temperature of at least 145 F, remove it from the heat, drizzle it with olive oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'690F25E9221C124D92C4FFF65E9C23D9', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'046684395945874DB75E72FA72C504EB', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 1, NULL, NULL, 2, NULL, 0, 'To split the lobster tails in half, place them on a cutting board, backside down. Use a large, sharp knife to cut down through the center to the shell the whole length.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A83403F842EE0445B28AA9384EE7DCCD', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 2, NULL, NULL, 2, NULL, 0, 'Fold the shell back (like you are opening a book), breaking through the shell. If the shell doesn''t break, you can use kitchen shears to cut through it. The flesh should now be exposed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F48FB32043E8D45A6ACCDAA861034BA', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 3, NULL, NULL, 2, NULL, 0, 'Pat the lobster tails dry with paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8117E9F271BFC248BB8A5C4A1C052C62', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 4, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4903A94E20D2BE44A8B39620A6826802', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 5, NULL, NULL, 2, NULL, 0, 'Combine the olive oil (or butter), lemon juice, dill, and salt in small mixing bowl until the salt has dissolved.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'272DEE84E2AC3F4894363238C8DD2D28', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 6, NULL, NULL, 2, NULL, 0, 'Brush the mixture onto the flesh side of lobster tails.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DF2400A73507247A529DA8A481CF29F', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 7, NULL, NULL, 2, NULL, 0, 'Place the lobster tails onto the grill and cook for 5 to 7 minutes, turning them once during the cooking process. Depending on the size of the lobster tail, it may need to cook longer.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13376708D4E5A04A975C2281DF7AC87E', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 8, NULL, NULL, 2, NULL, 0, 'Lobster tails will be done once meat reaches an internal temperature 140 F. The shells should turn a bright pink and the flesh should be opaque all the way through the center.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DB3C5177AC75341A6437D171BBA2BEF', X'56E9BCD84AB9824A865A368CB9D87826', X'56E9BCD84AB9824A865A368CB9D87826', 9, NULL, NULL, 2, NULL, 0, 'Remove the lobster tails from the heat. Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0E4C4F7E1319D44B5BC732A920378B5', X'1D0039D708617F449746CC64437749D8', X'1D0039D708617F449746CC64437749D8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02F5178FB988CA419290DE32E0C7797F', X'1D0039D708617F449746CC64437749D8', X'1D0039D708617F449746CC64437749D8', 1, NULL, NULL, 2, NULL, 0, 'Make sure your grill is clean. Rub the cooking grate with a thin layer of oil—1 or 2 teaspoons should be enough. Heat the grill (whether charcoal, gas, or electric) to medium-high. You should be able to hold your hand about 1 inch above the cooking grate for 3 to 4 seconds before pulling it away from the heat. A properly heated grill will help keep the fish from sticking excessively while also cooking it gently and thoroughly without setting it aflame.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4157890D45B0DA479451C74311208341', X'1D0039D708617F449746CC64437749D8', X'1D0039D708617F449746CC64437749D8', 2, NULL, NULL, 2, NULL, 0, 'While the grill heats, prepare the salmon fillets. Rinse them and pat them dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BADEA5766D8269468506F3C5A165ADC0', X'1D0039D708617F449746CC64437749D8', X'1D0039D708617F449746CC64437749D8', 3, NULL, NULL, 2, NULL, 0, 'Use tweezers to pull out any pin bones. Pull them out in the direction at which they''re coming out for the smoothest results. Don''t see any pin bones? Run your finger down the `seam` of the fillet—you''ll feel them.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B13DD2F7C9F9C44A784F108BB7D0BF5', X'1D0039D708617F449746CC64437749D8', X'1D0039D708617F449746CC64437749D8', 4, NULL, NULL, 2, NULL, 0, 'Brush the cooking grate with another 1 to 2 teaspoons of oil. Brush the 4 fillets with the remaining oil and sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC1C6423FE5B3348A6878674292CDAAA', X'1D0039D708617F449746CC64437749D8', X'1D0039D708617F449746CC64437749D8', 5, NULL, NULL, 2, NULL, 0, 'Place the salmon, skin-side-down, on the hot grill and cover. Cook, undisturbed, until the salmon just starts to release its fat (opaque mayonnaise-like stuff), or until it reaches 145 F, which takes about 15 to 20 minutes for most 1-inch thick fillets. Allow another 5 to 10 minutes for each extra inch of thickness. Use a spatula to remove the fish from the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84D7150B77A6B94EA639DCA307DC4239', X'1D0039D708617F449746CC64437749D8', X'1D0039D708617F449746CC64437749D8', 6, NULL, NULL, 2, NULL, 0, 'Serve grilled salmon immediately. Offer lemon wedges so people can spritz their salmon with fresh lemon juice, or serve with the garnish of your choice.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E61EC7E31580F418BA8CB6881CF0EA5', X'0538A55622EA4649B3B53469BB2289C5', X'0538A55622EA4649B3B53469BB2289C5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F09DE34AEFD7014E9E51077AA9EA50BF', X'0538A55622EA4649B3B53469BB2289C5', X'0538A55622EA4649B3B53469BB2289C5', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill with heavy cast-iron skillet or griddle in the grill. Get it good and hot, 15 to 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'799B93C3E298E043A5D62904B4F19753', X'0538A55622EA4649B3B53469BB2289C5', X'0538A55622EA4649B3B53469BB2289C5', 2, NULL, NULL, 2, NULL, 0, 'Reserve 12 tablespoons of melted butter for serving and pour the rest into a shallow dish.');
