INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CA5845652D8574E83CAB13AAF5F8D72', X'32EE79601ED552499C1F1184A3AECF1B', X'32EE79601ED552499C1F1184A3AECF1B', 3, NULL, NULL, 2, NULL, 0, 'Lightly coat inside and outside of each trout with olive oil. Season inside with salt and pepper and stuff with lemon slices and dill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'869C4C7F8CF2E4428613834BF9845780', X'32EE79601ED552499C1F1184A3AECF1B', X'32EE79601ED552499C1F1184A3AECF1B', 4, NULL, NULL, 2, NULL, 0, 'Place soaked cedar plank on grill over direct medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FE89C287EE3324680DDD3C6DFDC2E44', X'32EE79601ED552499C1F1184A3AECF1B', X'32EE79601ED552499C1F1184A3AECF1B', 5, NULL, NULL, 2, NULL, 0, 'When cedar plank starts to smoke, place fish on the plank.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A944C5E45E74874EA3489DFD5F801B36', X'32EE79601ED552499C1F1184A3AECF1B', X'32EE79601ED552499C1F1184A3AECF1B', 6, NULL, NULL, 2, NULL, 0, 'Grill for about 15 minutes or until the fish is done (reaching an internal temperature of 145 F). The flesh should be opaque and flake easily.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CDAE5F09F3A9843807041A528AA8F64', X'32EE79601ED552499C1F1184A3AECF1B', X'32EE79601ED552499C1F1184A3AECF1B', 7, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from grill and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A3CD9656AE1274FB284B9B10DF2AD6D', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2213CB5B949A9A4F9DD58F9DFF6F92B4', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 1, NULL, NULL, 2, NULL, 0, 'To prepare the baste, mix vinegar, sugar, oil, ginger, jalapeño, garlic, and cilantro in a bowl. Cover and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD755A5711DF4346B055A4312FE67706', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 2, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil. Drop the crabs, one at a time, headfirst into boiling water. Boil, then reduce heat and let simmer for 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07A0EEBDE4A27B42A7B34302F5981AB9', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 3, NULL, NULL, 2, NULL, 0, 'Remove crabs from the water and pull off the triangular tab from the belly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AE34FF4AFBCF34A889AD4FD22B6784F', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 4, NULL, NULL, 2, NULL, 0, 'Lift off the shell.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F52C23CE63589C45B3E7FE300348641E', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 5, NULL, NULL, 2, NULL, 0, 'Clean innards and gills from the crab. Wash, drain, and pat dry with paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E179E6D0E58F949AA68CD01BD246164', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 6, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A8FAFCD594D5D4D92267B5F157B3D36', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 7, NULL, NULL, 2, NULL, 0, 'Place the crabs on the grill. Cook with the lid closed, brushing with basting mixture after first 2 to 3 minutes of cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37C2229593AC60439163389224016B74', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 8, NULL, NULL, 2, NULL, 0, 'Turn crabs over halfway through grilling, at the 6-minute mark, and baste again.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6F43B3DCD4D4C45997F1D04755291CB', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 9, NULL, NULL, 2, NULL, 0, 'The crabs are done when the meat in the leg is opaque, which in total will take about 10 to 12 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'516296C241CF2840AFFF5871063481B6', X'F0D5F731B6CC604A969E50017C9303B4', X'F0D5F731B6CC604A969E50017C9303B4', 10, NULL, NULL, 2, NULL, 0, 'Place the crab on a serving dish and spoon remaining basting mixture over the crabs. You can replace the shells before serving if you wish. Serve with lemon wedges and drawn butter, as desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'401EF5732085BF42965C2C626E3E8B42', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 0, NULL, NULL, 2, NULL, 0, 'Soak mesquite chips in water for 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65A8F23D85AFE34B93DD889DA2B4CC39', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 1, NULL, NULL, 2, NULL, 0, 'Place mesquite chips in a smoker box or wrap in aluminum foil and punch holes through the foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'875E0A89AD97B64180FCE204FCCC76DF', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 2, NULL, NULL, 2, NULL, 0, 'Place on preheated grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BB9E48A2128314192DBDC5D3F34348B', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 3, NULL, NULL, 2, NULL, 0, 'Gently wash the halibut steaks and pat dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B02AF31314C3814984E9AA59EF6A37AB', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 4, NULL, NULL, 2, NULL, 0, 'Brush the halibut steaks with butter or olive oil (your choice). Sprinkle with pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B55D8B49DABBF4F8E338EC225838B99', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 5, NULL, NULL, 2, NULL, 0, 'Cut the orange into slices.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DD7386715243B478BDFF4EAEC4B4CDB', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 6, NULL, NULL, 2, NULL, 0, 'When the grill is good and hot, put on the steaks, placing orange slices on top to cover.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7CABBDF12A3094E8363A283EC290D24', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 7, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71ADBC02C51625469FF5E2EC6FB37E68', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 8, NULL, NULL, 2, NULL, 0, 'Turn after about 4 to 5 minutes (use your discretion, thicker steaks will take longer) cook until the middle is around 160 F (72 C), or until a skewer passes through the steak with uniform resistance (or as we always tell people, cook until done).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCAEBF438B4C99448DB8F24CB556E211', X'13F85055A1A17243A370D53B92A80F7B', X'13F85055A1A17243A370D53B92A80F7B', 9, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat, top with chopped parsley and serve with your favorite side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2F27F4092B40A4AA54579E47E4C1743', X'E9BEFE4D5EBD1F499F57F9821C941AD1', X'E9BEFE4D5EBD1F499F57F9821C941AD1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A491CF66A50F0148AC43F8F22BBD4658', X'E9BEFE4D5EBD1F499F57F9821C941AD1', X'E9BEFE4D5EBD1F499F57F9821C941AD1', 1, NULL, NULL, 2, NULL, 0, 'Prepare a smoker for a 2-hour smoke. Wash the fish and remove all the bones, if any.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9479CB41089A75498DA0455B93EAE66C', X'E9BEFE4D5EBD1F499F57F9821C941AD1', X'E9BEFE4D5EBD1F499F57F9821C941AD1', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the oil, lemon juice, garlic powder, salt, and lemon pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F3FCD2C81763C4B89A5363DEB18978F', X'E9BEFE4D5EBD1F499F57F9821C941AD1', X'E9BEFE4D5EBD1F499F57F9821C941AD1', 3, NULL, NULL, 2, NULL, 0, 'Brush the liquid mixture onto both sides of the tilapia fillets.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAD74AA9A3B0E946B8B7544106982AB1', X'E9BEFE4D5EBD1F499F57F9821C941AD1', X'E9BEFE4D5EBD1F499F57F9821C941AD1', 4, NULL, NULL, 2, NULL, 0, 'Place the fish in the smoker for 1 1/2 to 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D62DFB3AA69E9499E1B24EF70346CCC', X'E9BEFE4D5EBD1F499F57F9821C941AD1', X'E9BEFE4D5EBD1F499F57F9821C941AD1', 5, NULL, NULL, 2, NULL, 0, 'Remove from heat and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1758C06C9FCC804298E4F12CE405E904', X'E9BEFE4D5EBD1F499F57F9821C941AD1', X'E9BEFE4D5EBD1F499F57F9821C941AD1', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65CB32565531394290956FD3648BE414', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except the salmon.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02FA6CF37FD4534FAC7334B95501B9F7', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', 1, NULL, NULL, 2, NULL, 0, 'Lay salmon fillets out on a tray or baking sheet. Top with the butter-rum mixture, spreading it evenly over salmon. Let sit for about 15 to 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8D6A4A96F38964E9C1A63C3AD117740', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill and prepare for indirect grilling. This recipe works particularly well with a charcoal grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F23B97E888052A439516354BEBF1BB2B', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', 3, NULL, NULL, 2, NULL, 0, 'When grill is ready, gently place salmon fillets over the unheated part (indirect) of the grill. Keep the grill on low heat. You can add soaked mesquite chips for an extra smoky flavor. Close ?the lid and let salmon grill for 30 to 45 minutes depending on thickness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E3882A852999240B62058FAEA6FDB59', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', X'901B238F3BD4AA4E8AEE7DF6971F0C0E', 4, NULL, NULL, 2, NULL, 0, 'Check for doneness by flaking apart the fish at the thickest part. Cooked salmon flakes easily and evenly. The color should be light pink without any shine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'533FD58A99950A45861ABA6C2DEDFF7B', X'ECBB4CF972D41249BD67F70BF6663863', X'ECBB4CF972D41249BD67F70BF6663863', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'302E1B6E35FF434B9F2D0FECCC1BF5A6', X'ECBB4CF972D41249BD67F70BF6663863', X'ECBB4CF972D41249BD67F70BF6663863', 1, NULL, NULL, 2, NULL, 0, 'Rinse and pat dry the fish fillets with paper towels. Place them in a shallow baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D62BBFCE3254F4F8467CB2ED7D0393C', X'ECBB4CF972D41249BD67F70BF6663863', X'ECBB4CF972D41249BD67F70BF6663863', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the balsamic vinegar, lemon juice, mustard, onion, and chopped parsley. Mix and pour over the fish. Cover with plastic wrap and refrigerate for 20 to 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B7C701C55AFE1498C65C716839DC3A9', X'ECBB4CF972D41249BD67F70BF6663863', X'ECBB4CF972D41249BD67F70BF6663863', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill and oil the grates well. Remove fish from marinade and place on grill. Grill on high heat for about 4 to 6 minutes or until fish reaches an internal temperature of at least 145 F. You want this sole to be moist and tender so be sure not to overcook the fish; not only will it be dried out and inedible but it will also smell and taste fishy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1838B1F6DBDAA4CA2EA082538E8257A', X'ECBB4CF972D41249BD67F70BF6663863', X'ECBB4CF972D41249BD67F70BF6663863', 4, NULL, NULL, 2, NULL, 0, 'Once cooked through, remove from grill, sprinkle salt and pepper, garnish with lemon slices, and serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48C8D048DD256F488BB2698243E1E186', X'ECBB4CF972D41249BD67F70BF6663863', X'ECBB4CF972D41249BD67F70BF6663863', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'763C15E9304C1843860F195A29BF1610', X'7610D925A1804F4A8E38E0E6B4B44686', X'7610D925A1804F4A8E38E0E6B4B44686', 0, NULL, NULL, 2, NULL, 0, 'Place catfish in a shallow dish. Mix together sauce ingredients and pour over catfish fillets. Cover and allow to marinate for 30 to 45 minutes in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF00ADF299B5234AA60F26B60E010AA8', X'7610D925A1804F4A8E38E0E6B4B44686', X'7610D925A1804F4A8E38E0E6B4B44686', 1, NULL, NULL, 2, NULL, 0, 'Remove fillets from the dish, discard marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A9782C78C61E446AEBE13C9AEF04910', X'7610D925A1804F4A8E38E0E6B4B44686', X'7610D925A1804F4A8E38E0E6B4B44686', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat. Place fillets on grill and cook for 12 to 15 minutes (or until internal temperature reaches 145 to 150 F), turning once.');
