INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB4EC769A7A693479F7E1952D60A8CC4', X'3CDE08405101914C8D5371E8D9C611B6', X'3CDE08405101914C8D5371E8D9C611B6', 1, NULL, NULL, 2, NULL, 0, 'Add salsa, wine, lemon juice, oregano, cumin. Bring to a boil. Reduce heat and simmer for 8 minutes. Remove from heat.');
