INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF7CB3736DD8004FA924C4C2B3280E19', X'BED2393D4112814D9EEABDCB76F1A759', X'BED2393D4112814D9EEABDCB76F1A759', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
