INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E18F56121EC29546AB6711779F8D0E2C', X'BED2393D4112814D9EEABDCB76F1A759', X'BED2393D4112814D9EEABDCB76F1A759', 5, NULL, NULL, 2, NULL, 0, 'Grill tilapia fillets over a medium-high heat for about 4 to 5 minutes per side or until the internal temperature at the thickest part of fish reaches between 145 to 150 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0144DA530734EA43979348304C25F688', X'BED2393D4112814D9EEABDCB76F1A759', X'BED2393D4112814D9EEABDCB76F1A759', 6, NULL, NULL, 2, NULL, 0, 'Serve by pouring a small amount of the sauce over the tilapia fillets.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76205EB606F3904F9A4916565F693F59', X'C38C777E451BB74EA9FE2EB1F2174735', X'C38C777E451BB74EA9FE2EB1F2174735', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2E14129A8D9E94E87CA5D98DEE376D7', X'C38C777E451BB74EA9FE2EB1F2174735', X'C38C777E451BB74EA9FE2EB1F2174735', 1, NULL, NULL, 2, NULL, 0, 'If the salmon is frozen, allow it to defrost in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0400CED36C9064499E90295F51D0C67B', X'C38C777E451BB74EA9FE2EB1F2174735', X'C38C777E451BB74EA9FE2EB1F2174735', 2, NULL, NULL, 2, NULL, 0, 'Combine the lemon juice, lemon zest, oil, thyme, garlic, and salt in a large resealable bag. Add the salmon and turn the fillets or steaks to coat them evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D96598D95ABE64B873583455BBED809', X'C38C777E451BB74EA9FE2EB1F2174735', X'C38C777E451BB74EA9FE2EB1F2174735', 3, NULL, NULL, 2, NULL, 0, 'Seal the bag and refrigerate it for 30 minutes to 1 hour. Do not allow it to marinate any longer as the salmon can become mushy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32EB9DFC3FDC494CB0F7BD9AE5122573', X'C38C777E451BB74EA9FE2EB1F2174735', X'C38C777E451BB74EA9FE2EB1F2174735', 4, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium-high heat. Right before placing salmon on the grill, oil the grill grates well using large tongs, folded paper towels, and oil. Make several passes across the grates. This will help the fish from sticking and breaking apart.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'948FF23697ADEF4796DBA445F0710CEB', X'C38C777E451BB74EA9FE2EB1F2174735', X'C38C777E451BB74EA9FE2EB1F2174735', 5, NULL, NULL, 2, NULL, 0, 'Remove the salmon from the bag and place on the hot grill. Brush salmon with marinade from the ziptop bag only once, at the start. Grill the salmon for about 4 to 6 minutes per side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3509B8E4F059D4E8B6EDE62E3916055', X'C38C777E451BB74EA9FE2EB1F2174735', X'C38C777E451BB74EA9FE2EB1F2174735', 6, NULL, NULL, 2, NULL, 0, 'Remove the salmon from the heat once the internal temperature of the fish reaches 145 F. The salmon should be light in color and flake easily through the thickest part.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91188B792B38DA499FBAF6AEE11CA7A2', X'C38C777E451BB74EA9FE2EB1F2174735', X'C38C777E451BB74EA9FE2EB1F2174735', 7, NULL, NULL, 2, NULL, 0, 'Optionally, squeeze fresh lemon over each fillet right before you take it off the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38835EB50FE3DC4BAEAF1A3051A97AD0', X'C38C777E451BB74EA9FE2EB1F2174735', X'C38C777E451BB74EA9FE2EB1F2174735', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8B13C2A99C96D4194F6011576627575', X'8A74816C92195145B79061AE948C2318', X'8A74816C92195145B79061AE948C2318', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
