INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'057DD82C8D915D47AFD92586CB0C04E5', X'8A74816C92195145B79061AE948C2318', X'8A74816C92195145B79061AE948C2318', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and allow to cool. Place shrimp in a large zip-top bag. Pour marinade over top. Seal and refrigerate for 20 to 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BD9B5B4A528FF4F848324B901BEBF20', X'8A74816C92195145B79061AE948C2318', X'8A74816C92195145B79061AE948C2318', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill. Remove shrimp from marinade and place on a hot grill. Grill about 5 to 10 minutes, turning occasionally until done. Cooked shrimp will be pink and firm to the touch.');
