INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0633045FDEE734489AA460F37BE856A8', X'0F977351DA00FA45831E85E163023771', X'0F977351DA00FA45831E85E163023771', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat on the internal temperature of fish reaches at least 145 F and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'742F3036483BF549A547C3907B4A817A', X'38BC549371FD4340A30ECB7C3A3FB366', X'38BC549371FD4340A30ECB7C3A3FB366', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
