INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A363D0A873BAAC4C9FAAC65AF2733C93', X'A9D0F74FCD19CD47ACC4DC7A391178E0', X'A9D0F74FCD19CD47ACC4DC7A391178E0', 0, NULL, NULL, 2, NULL, 0, 'Rinse swordfish steaks well under cold water and pat dry with paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C39754533DB4048ABD71D6FB89ED6E3', X'A9D0F74FCD19CD47ACC4DC7A391178E0', X'A9D0F74FCD19CD47ACC4DC7A391178E0', 1, NULL, NULL, 2, NULL, 0, 'Combine olive oil, lemon juice, salt, pepper, Worcestershire sauce, and Tabasco sauce. Stir well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5915175460E38A4E9733026C349B9C70', X'A9D0F74FCD19CD47ACC4DC7A391178E0', X'A9D0F74FCD19CD47ACC4DC7A391178E0', 2, NULL, NULL, 2, NULL, 0, 'Brush fish with the mixture then sprinkle with about half of the paprika. Set remaining lemon juice mixture and paprika aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'072816D4C5318C4F89E61C1673F3A3C4', X'A9D0F74FCD19CD47ACC4DC7A391178E0', X'A9D0F74FCD19CD47ACC4DC7A391178E0', 3, NULL, NULL, 2, NULL, 0, 'Place the swordfish on a well-greased grill over medium coals or in a greased grilling basket.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F45DFCF5670F1C4FB9504FA30E00733E', X'A9D0F74FCD19CD47ACC4DC7A391178E0', X'A9D0F74FCD19CD47ACC4DC7A391178E0', 4, NULL, NULL, 2, NULL, 0, 'Grill the fish over medium coals for about eight minutes on each side or until fish flakes easily with a fork or grill at about 350 F on a gas grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7194545E4F15EF4C8EED24634E49B0A6', X'A9D0F74FCD19CD47ACC4DC7A391178E0', X'A9D0F74FCD19CD47ACC4DC7A391178E0', 5, NULL, NULL, 2, NULL, 0, 'Baste with the lemon juice mixture several times and sprinkle the finished fish with the remaining paprika before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03DD45514C3B7744B13218F94B00C39C', X'68E1EA29A8AB3646A2EE7FFF31F4FD72', X'68E1EA29A8AB3646A2EE7FFF31F4FD72', 0, NULL, NULL, 2, NULL, 0, 'Combine olive oil, lime juice, lime zest, fresh basil, bourbon, salt and black pepper in a small bowl. Place tilapia fillets in a resealable plastic bag and pour marinade over top. Make sure all surfaces are coated well with the marinade. Seal bag and refrigerator for 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E013D16CEB4DF14B8E4E7A8EF4A8FF0C', X'68E1EA29A8AB3646A2EE7FFF31F4FD72', X'68E1EA29A8AB3646A2EE7FFF31F4FD72', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDA8516BC972894288A38CB931D2D30D', X'68E1EA29A8AB3646A2EE7FFF31F4FD72', X'68E1EA29A8AB3646A2EE7FFF31F4FD72', 2, NULL, NULL, 2, NULL, 0, 'Remove fish from bag and pour marinade into a saucepan. Bring marinade to a boil, immediately reduce heat and let simmer on low for 5 minutes. Remove from heat, cover and keep warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CDE80E1F14F64499965E2E8FAFB9956', X'68E1EA29A8AB3646A2EE7FFF31F4FD72', X'68E1EA29A8AB3646A2EE7FFF31F4FD72', 3, NULL, NULL, 2, NULL, 0, 'Grill tilapia fillets for 3 to 5 minutes per side over high heat. When fish reaches an internal temperature of 145 F. and appears opaque in color, remove from grill and serve with a few spoonfuls of marinade drizzled over top.');
