INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98C371F36876D84A83673919FF66D6DB', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4B3A431D3144E4EB37D5F84A6FDEC6F', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 2, NULL, NULL, 2, NULL, 0, 'Right before placing the fish onto the grill, oil the grill grates well, using tongs, folded paper towels and a high smoke point oil. Make 3 to 4 passes across grates to create a non-stick surface. (This is particularly important when grilling fish, as it tends to stick to the grates and break apart.)');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBF3671F23BFD94C88F6E532AA41904B', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 3, NULL, NULL, 2, NULL, 0, 'Place salmon fillets in a shallow pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'553E479939EBDB46ADDCA2C64E885902', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 4, NULL, NULL, 2, NULL, 0, 'Combine 2 teaspoons lime juice, spices, sugar, and garlic.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BB355E22E47154CB3EBE9A3D17F213D', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 5, NULL, NULL, 2, NULL, 0, 'Spread mixture evenly over fillets.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45E1628C37B21E439C482CE57585487D', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 6, NULL, NULL, 2, NULL, 0, 'Let stand until the grill has heated up properly. (If you do this ahead of time, place seasoned fish in the refrigerator for a maximum of 30 minutes.)');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED2708C86728364F9ED572D616EFBF79', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 7, NULL, NULL, 2, NULL, 0, 'Place salmon skin side down onto grill and cook indirectly for about 18 to 20 minutes, or until internal temperature at the thickest part of fish reaches between 145 to 150 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF34365B0F3CD4448D385724417996AC', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 8, NULL, NULL, 2, NULL, 0, 'Remember not to turn the salmon when it is on the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E738BB74EE860147A26A46ADDEF88834', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 9, NULL, NULL, 2, NULL, 0, 'Once cooked through (an internal temperature of 145 F.), remove from grill and squeeze fresh lime juice on each fillet before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D98424B57E5E5D448EB721F37493CAA2', X'F3E5154184C13C47A43E4CB4C773A8A4', X'F3E5154184C13C47A43E4CB4C773A8A4', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74A3D555BA814B49957FA740745FC29A', X'1D71DB21B23CB44AB5E8DCE80ABF33CA', X'1D71DB21B23CB44AB5E8DCE80ABF33CA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat grill to medium-high heat.');
