INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C89ACD92A100724B84D2129185305BAA', X'1D71DB21B23CB44AB5E8DCE80ABF33CA', X'1D71DB21B23CB44AB5E8DCE80ABF33CA', 3, NULL, NULL, 2, NULL, 0, 'Brush fish fillets with extra-virgin olive oil and sprinkle with salt and pepper. Place fish on a well-oiled grill rack and cook for 8 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A26A2D7E6181F438A37341507EAA704', X'1D71DB21B23CB44AB5E8DCE80ABF33CA', X'1D71DB21B23CB44AB5E8DCE80ABF33CA', 4, NULL, NULL, 2, NULL, 0, 'Baste cod with butter sauce and carefully flip over fish. Cook for an additional 6 to 7 minutes, basting fish a total of 2 to 3 times, or until fish is opaque, flakes easily with a fork, and reaches an internal temperature of 145 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55D88218D2B50343A6929E8A70CA2309', X'1D71DB21B23CB44AB5E8DCE80ABF33CA', X'1D71DB21B23CB44AB5E8DCE80ABF33CA', 5, NULL, NULL, 2, NULL, 0, 'Remove cod from the grill, top with lemon wedges, and serve.');
