INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3139EC07CDCC124993CB8914CFB7D723', X'1A7A035EEA1BCE4784873F11E795B54C', X'1A7A035EEA1BCE4784873F11E795B54C', 7, NULL, NULL, 2, NULL, 0, 'Plate the fish and serve with your favorite sides.');
