INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'056B70EFB4AF0D419A25EA6D9096FBE7', X'6B1C1FDF291E0E4099F43B5CEEE7801E', X'6B1C1FDF291E0E4099F43B5CEEE7801E', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the orange juice, soy sauce, ketchup, chopped parsley or cilantro, lemon juice, minced garlic, black pepper, salt, and red pepper flakes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'744C58ED202961448C08EDA169082208', X'6B1C1FDF291E0E4099F43B5CEEE7801E', X'6B1C1FDF291E0E4099F43B5CEEE7801E', 2, NULL, NULL, 2, NULL, 0, 'Place the fish in a shallow baking dish and pour the marinade over the top. Using tongs, turn the shark steaks to coat well. Cover with plastic wrap and marinate in the refrigerator for 1 to 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20281469D195364DA55917A5FFE80477', X'6B1C1FDF291E0E4099F43B5CEEE7801E', X'6B1C1FDF291E0E4099F43B5CEEE7801E', 3, NULL, NULL, 2, NULL, 0, 'Heat a grill to medium-high heat. Right before placing fish on the grill, oil the grill grates using a pair of outdoor cooking tongs and folded paper towels soaked with oil. Make about 3 to 4 passes across the grates to ensure a nonstick cooking surface. This is important when grilling fish since it tends to fall apart if conditions are not quite right.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D519D0A149FE842BFB0453FE930A098', X'6B1C1FDF291E0E4099F43B5CEEE7801E', X'6B1C1FDF291E0E4099F43B5CEEE7801E', 4, NULL, NULL, 2, NULL, 0, 'Remove the fish from the marinade, reserving the marinade and pouring it into a saucepan. Bring to a high simmer for 2 minutes, stirring often. Reduce the heat to medium-low and let simmer for 10 to 12 minutes. Remove from the heat and cover to keep the sauce warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D2E4AB69041F24C811192E97ED3CB66', X'6B1C1FDF291E0E4099F43B5CEEE7801E', X'6B1C1FDF291E0E4099F43B5CEEE7801E', 5, NULL, NULL, 2, NULL, 0, 'Place the fish on the preheated grill and cook for 5 to 6 minutes per side, or until the fish reaches an internal temperature of 145 F and flakes easily when tested with a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FA597EFFEF3764BA2233476D5C2556D', X'6B1C1FDF291E0E4099F43B5CEEE7801E', X'6B1C1FDF291E0E4099F43B5CEEE7801E', 6, NULL, NULL, 2, NULL, 0, 'Once cooked, remove the shark steaks from the grill and serve with sauce.');
