INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'918ACFB84E823048BB7363199579CF51', X'2E822455613D2C40AC756995DF211848', X'2E822455613D2C40AC756995DF211848', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52E8CD988360A04AAE179225B89F783A', X'2E822455613D2C40AC756995DF211848', X'2E822455613D2C40AC756995DF211848', 4, NULL, NULL, 2, NULL, 0, 'Remove shrimp from marinade and thread onto skewers alternating between pineapple and red onion chunks.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69141819915D5B45BF0A4F870F146B63', X'2E822455613D2C40AC756995DF211848', X'2E822455613D2C40AC756995DF211848', 5, NULL, NULL, 2, NULL, 0, 'Place reserved marinade in a pot over medium-high heat, bring to boil, a reduce heat to medium-low and cook for 5 to 6 minutes, stirring occasionally. Watch to make sure that it doesn''t burn. You can cook the marinade on the side burner if your grill has one. Once cooked, remove from heat and cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E2235AD633BCE4B8859DCA7595C5ECB', X'2E822455613D2C40AC756995DF211848', X'2E822455613D2C40AC756995DF211848', 6, NULL, NULL, 2, NULL, 0, 'Place shrimp kebabs onto grill and cook for 3 to 4 minutes per side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8709970100E8E4B9178581CF0C4D2BD', X'2E822455613D2C40AC756995DF211848', X'2E822455613D2C40AC756995DF211848', 7, NULL, NULL, 2, NULL, 0, 'Remove from heat and serve with sauce drizzled over top.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86F10FDD47FDE84E966992744FD9E9C6', X'ACB6D352F37E014B8494BEBF0BCE4EB3', X'ACB6D352F37E014B8494BEBF0BCE4EB3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat a grill to medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38BB50A616A59241824072CEF01FCA2B', X'ACB6D352F37E014B8494BEBF0BCE4EB3', X'ACB6D352F37E014B8494BEBF0BCE4EB3', 1, NULL, NULL, 2, NULL, 0, 'Roast the corn on medium heat until sweet and tender, about 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95A463D59E4E5C43AD96E5CC1A085DF9', X'ACB6D352F37E014B8494BEBF0BCE4EB3', X'ACB6D352F37E014B8494BEBF0BCE4EB3', 2, NULL, NULL, 2, NULL, 0, 'Husk the corn toward the end of cooking and char corn directly on the hot grill for extra flavor and color, about 3 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8923D7DBDDBC644F90177174D16FC8B2', X'ACB6D352F37E014B8494BEBF0BCE4EB3', X'ACB6D352F37E014B8494BEBF0BCE4EB3', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, bring together the sour cream, mayonnaise, and lime juice.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0E35F0DB0E78F44BA04C889D8021350', X'ACB6D352F37E014B8494BEBF0BCE4EB3', X'ACB6D352F37E014B8494BEBF0BCE4EB3', 4, NULL, NULL, 2, NULL, 0, 'Using a microplane or a garlic press, grate the garlic into the mixture. Add the salt and whisk to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDC6587092537944B16DDB1F6C5A730A', X'ACB6D352F37E014B8494BEBF0BCE4EB3', X'ACB6D352F37E014B8494BEBF0BCE4EB3', 5, NULL, NULL, 2, NULL, 0, 'Spread the mayo mixture onto a flat plate. Combine the Parmesan cheese and queso fresco on a separate flat plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2966734F70B02D48B8BD4220A08433EB', X'ACB6D352F37E014B8494BEBF0BCE4EB3', X'ACB6D352F37E014B8494BEBF0BCE4EB3', 6, NULL, NULL, 2, NULL, 0, 'Roll the corn in the mayo mixture to completely coat each ear. You can use corn on the cob holders to help you roll them; tongs will also work. Do the same in the cheese mixture and set the corn aside on a platter.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E539E6E7AF02441B81AC89DF343AC4D', X'ACB6D352F37E014B8494BEBF0BCE4EB3', X'ACB6D352F37E014B8494BEBF0BCE4EB3', 7, NULL, NULL, 2, NULL, 0, 'Top the corn with chopped cilantro and a sprinkle of chili powder. Serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C4FAE068193D441B44DE69EB90DAFED', X'A6429BCFC160E945B17DC74995D89C91', X'A6429BCFC160E945B17DC74995D89C91', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2824DA6ABED4DE4B8B333082A4E0EC6A', X'A6429BCFC160E945B17DC74995D89C91', X'A6429BCFC160E945B17DC74995D89C91', 1, NULL, NULL, 2, NULL, 0, 'Preheat a grill on low heat if you''re using gas, or create an indirect heat section if you''re using charcoal.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'007EE95DE40EBD4394105086F8BD8351', X'A6429BCFC160E945B17DC74995D89C91', X'A6429BCFC160E945B17DC74995D89C91', 2, NULL, NULL, 2, NULL, 0, 'Bring a medium pot of water to boil. Add the potatoes and cook for 8 to 10 minutes, or until almost done, but not quite tender. Drain completely and spread out on a towel, baking sheet, or cooling rack to let cool a bit and dry off.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7FFBCD51C40384ABCBD4A3D1D3C2D85', X'A6429BCFC160E945B17DC74995D89C91', X'A6429BCFC160E945B17DC74995D89C91', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the dressing: in a small bowl, whisk together the lemon juice, garlic, soy sauce, honey, and dijon mustard until fully mixed. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DBB0E088DF079468118B5894795A275', X'A6429BCFC160E945B17DC74995D89C91', X'A6429BCFC160E945B17DC74995D89C91', 4, NULL, NULL, 2, NULL, 0, 'Once the grill is hot, toss the carrots with 1/3 of the olive oil and season with salt and pepper. Position over indirect heat (if using charcoal) and close the grill. Cook for 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'633FFE8C7AD87743A99D30E41A7AFBA2', X'A6429BCFC160E945B17DC74995D89C91', X'A6429BCFC160E945B17DC74995D89C91', 5, NULL, NULL, 2, NULL, 0, 'Toss the cabbage in another 1/3 of the oil and season with salt and pepper. Turn the carrots on the grill and add the cabbage, Turn up the heat if you''re using gas, or slide the veggies closer to the direct heat if you''re using charcoal. Close the grill and cook for 4 to 5 minutes, or until the cabbage is lightly charred.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60CA308CE117BB48B6C6A91C282F53CD', X'A6429BCFC160E945B17DC74995D89C91', X'A6429BCFC160E945B17DC74995D89C91', 6, NULL, NULL, 2, NULL, 0, 'Toss the potatoes in the remaining oil and season with salt and pepper. Flip the cabbage and add the potatoes to a hot section of the grill. Cook for 3 to 5 more minutes, flipping the potatoes halfway through. The potatoes should have grill marks and be cooked through.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F23AD40761960149AF7F16E8985E3C32', X'A6429BCFC160E945B17DC74995D89C91', X'A6429BCFC160E945B17DC74995D89C91', 7, NULL, NULL, 2, NULL, 0, 'Remove all of the vegetables to a platter. Toss with the dressing and top with the parsley. Serve warm or room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDF385117F6FB74E9B3CFF79AC39AE03', X'C8F2EC85D50C9742922840A0634A8C22', X'C8F2EC85D50C9742922840A0634A8C22', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92542808BC9D0440B3254A60F8B2425B', X'C8F2EC85D50C9742922840A0634A8C22', X'C8F2EC85D50C9742922840A0634A8C22', 1, NULL, NULL, 2, NULL, 0, 'Combine marinade ingredients in a small bowl and set aside. Place flat steak, chicken, or tofu in a resealable bag. Pour marinade over top making sure that all surfaces are coated. Seal bag and place in refrigerator. Marinate steak for 1-3 hours, chicken for 1-2 hours, and tofu or vegetables for 15-30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FA6E0603DDAC145BB40D86405CEF780', X'C8F2EC85D50C9742922840A0634A8C22', X'C8F2EC85D50C9742922840A0634A8C22', 2, NULL, NULL, 2, NULL, 0, 'While meat is marinating, prepare the lime-cilantro sauce. Place seeded and chopped jalapeño, green onion, cilantro, lime juice, and 1/2 cup/120 mL of the yogurt in a blender or food processor. Puree mixture well. If you intend to use a condiment bottle to drizzle sauce over the nachos, you will need to make sure there are no chunks remaining. Most blenders are efficient all pulverizing vegetable matter, but in case you are left with some unprocessed pieces, run the mixture through a sieve into a bowl. Add the remaining 1/2 cup/120 mL of yogurt and season with 1/4 teaspoon/1.75 mL salt. Transfer into condiment bottle (use a funnel to make this step easier) and store in the refrigerator until nachos are done.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BC0C2E42365BE45B5CB2AEA9E7B28D7', X'C8F2EC85D50C9742922840A0634A8C22', X'C8F2EC85D50C9742922840A0634A8C22', 3, NULL, NULL, 2, NULL, 0, 'Prepare grill for medium high heat. If using the oven, set to 375 degrees F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A826960213B66747874F5CB6E6532CD6', X'C8F2EC85D50C9742922840A0634A8C22', X'C8F2EC85D50C9742922840A0634A8C22', 4, NULL, NULL, 2, NULL, 0, 'Wash and rinse sweet potatoes. Dry off with a paper towel and chop off pointy ends. Cut into 1/4` slices. Add 2 tablespoons/30 mL of olive oil to cast iron pan. Make sure the entire bottom is well coated. Layer sweet potatoes by stacking them halfway on top of the previous one, working from the outside to the center in a circular pattern. Season with salt and place onto the grill on direct heat for 10 minutes. Move to indirect heat and cook for an additional 15-20 minutes. Watch for burning and adjust heat accordingly. ??');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BD9ADB62BE6644399D845CEE00CE8F5', X'C8F2EC85D50C9742922840A0634A8C22', X'C8F2EC85D50C9742922840A0634A8C22', 5, NULL, NULL, 2, NULL, 0, 'Once sweet potatoes are cooking over indirect heat, it is time to cook the corn. Remove husks and threads and place onto the grill, rotating corn every 30 seconds to 1 minute, until it begins to blister and char a bit. Don''t over do this process or you''ll end up with hard, burnt corn. Remove from heat and let cool for a few minutes. Using a sharp knife, carefully cut away kernels and place into a bowl. Cover with plastic wrap until ready to use.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFFFA9ADE079FD438A5FE244969F0CFB', X'C8F2EC85D50C9742922840A0634A8C22', X'C8F2EC85D50C9742922840A0634A8C22', 6, NULL, NULL, 2, NULL, 0, 'Remove steaks from marinade and place onto grill. If opting for a thin steak, it will be fully cooked in 5-7 minutes. Tofu and other vegetables will be between 3-5 minutes. If using chicken breast, consider cooking this first before the corn. This will take longer and needs to reach a safe temperature of 165 degrees F. in the thickest part of the breast before it is considered done. Once meat (tofu, or vegetables) has cooked through, remove from heat and cut into bite sized pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0025BE1ED6699E479EA0BA8238A20621', X'C8F2EC85D50C9742922840A0634A8C22', X'C8F2EC85D50C9742922840A0634A8C22', 7, NULL, NULL, 2, NULL, 0, 'Now it''s time to assemble the nachos while the cast iron skillet is still on the grill. Place the pinto beans on top of the sweet potatoes. Next, the grilled corn kernels. Top with sliced steak (or whatever you choose for this portion), then crumbled queso fresco. Place the lid back on the grill and let it go for 5-7 minutes until everything has warmed through and cheese has melted.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B94DBD635C0CA3448635F411945028DD', X'C8F2EC85D50C9742922840A0634A8C22', X'C8F2EC85D50C9742922840A0634A8C22', 8, NULL, NULL, 2, NULL, 0, 'Using heat resistant gloves, carefully remove cast iron skillet and place onto hot pads on a safe surface. The nachos will be sizzling at this point, so let them rest for 5-10 minutes or so. Drizzle liberally with sauce, garnish with cilantro, sliced green onions, and serve. The best way to get them out of the pan is with a spatula. Use a fork to eat these nachos, especially while it''s still piping hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35CD4634ADE36E47BDD12A8F1749FD94', X'65F875C093B8544591F6EB317A6BB6D3', X'65F875C093B8544591F6EB317A6BB6D3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06B4398C4976E043BD2ECFF7D44E4BEC', X'65F875C093B8544591F6EB317A6BB6D3', X'65F875C093B8544591F6EB317A6BB6D3', 1, NULL, NULL, 2, NULL, 0, 'Fire up your grill, or if using a grill pan then preheat to hot but not searing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF4ACF7A1A535C4ABAD2121DDA63893D', X'65F875C093B8544591F6EB317A6BB6D3', X'65F875C093B8544591F6EB317A6BB6D3', 2, NULL, NULL, 2, NULL, 0, 'Pat dry the washed hearts with kitchen paper. Remove any browned or wilted outer leaves and check inside for any insects or remaining dirt (it is unlikely you will find any but best to check).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'768FFE75F61B9847A082FF8D5B9D7337', X'65F875C093B8544591F6EB317A6BB6D3', X'65F875C093B8544591F6EB317A6BB6D3', 3, NULL, NULL, 2, NULL, 0, 'Halve the romaine hearts lengthwise, leaving the cores intact. Brush all the surfaces of the lettuce with the olive oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A8AA84ACE018348B9FB7CCD120E3AA7', X'65F875C093B8544591F6EB317A6BB6D3', X'65F875C093B8544591F6EB317A6BB6D3', 4, NULL, NULL, 2, NULL, 0, 'Grill the hearts, cut side down, over medium heat for 2 minutes on each side, until char marks appear on the surface of the lettuce and it wilts slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'656D3880A441304B9FC56C2401B78BB2', X'65F875C093B8544591F6EB317A6BB6D3', X'65F875C093B8544591F6EB317A6BB6D3', 5, NULL, NULL, 2, NULL, 0, 'Alternately, turn a broiler on high heat and place the hearts of romaine 5 inches from the heat. Broil them for 2 minutes on each side, until the lettuce begins to brown and wilts very slightly. Watch the cooking process closely, as the lettuce will cook very quickly and burn.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'732145F3EBAD0F42B80A3F868F888F95', X'65F875C093B8544591F6EB317A6BB6D3', X'65F875C093B8544591F6EB317A6BB6D3', 6, NULL, NULL, 2, NULL, 0, 'Place the grilled lettuce, cut side up, on a serving platter. Drizzle the romaine with balsamic vinegar, sprinkle with black pepper and freshly grated parmesan cheese (do not use pre-grated cheese as this does not melt as easily.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B15AE92D460BF4AAFC106265770F462', X'65F875C093B8544591F6EB317A6BB6D3', X'65F875C093B8544591F6EB317A6BB6D3', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92B4ED390D337F4F8BED5F98DB179366', X'E8AFC99D6F920846904D0EF48EB3CC3F', X'E8AFC99D6F920846904D0EF48EB3CC3F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'782E732A08F2DF41A6B293FE89E68E0B', X'E8AFC99D6F920846904D0EF48EB3CC3F', X'E8AFC99D6F920846904D0EF48EB3CC3F', 1, NULL, NULL, 2, NULL, 0, 'Heat a gas or charcoal grill to medium-high heat (you should be able to hold your hand about 1 inch above the cooking grate for 3 to 4 seconds before pulling it away from the intensity of the heat).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'564CE61EA4D3764DBAE7C15F9BD75F20', X'E8AFC99D6F920846904D0EF48EB3CC3F', X'E8AFC99D6F920846904D0EF48EB3CC3F', 2, NULL, NULL, 2, NULL, 0, 'While the grill heats, trim off and discard any wilted greens at the top of the spring onions. Cut off and discard the root end of the onions as well. Be careful to cut right at the bottom of the onion and the top of the roots so the bulb stays together and you can cook and serve the onions intact.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97C3112710F960499ADA28EE62C4775B', X'E8AFC99D6F920846904D0EF48EB3CC3F', X'E8AFC99D6F920846904D0EF48EB3CC3F', 3, NULL, NULL, 2, NULL, 0, 'Brush the spring onions with olive oil or vegetable oil. Be liberal with the application of the oil — it prevents the onions from sticking to the hot grill and adds tons of flavor as it mixes with the juices from the onions.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97C335991F54D646AC577B65F01FDD21', X'E8AFC99D6F920846904D0EF48EB3CC3F', X'E8AFC99D6F920846904D0EF48EB3CC3F', 4, NULL, NULL, 2, NULL, 0, 'Put the spring onions on the preheated grill and cook them, turning once, until grill marks appear on both sides and the spring onions feel fork tender all the way through, about 15 minutes total cooking time.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'714F0C447D912F4684394AC6A78EE7C8', X'E8AFC99D6F920846904D0EF48EB3CC3F', X'E8AFC99D6F920846904D0EF48EB3CC3F', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the grilled spring onions with salt and freshly ground black pepper to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D552ADE5690E04485605A603F9CB9AA', X'E8AFC99D6F920846904D0EF48EB3CC3F', X'E8AFC99D6F920846904D0EF48EB3CC3F', 6, NULL, NULL, 2, NULL, 0, 'Spritz the onions with fresh lemon juice or serve the lemon wedges on the side so everyone can dress their own plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'299D2DD2D475774386933E4773A3CD6A', X'470CC9CFA70CA34B807F1B4DB8151037', X'470CC9CFA70CA34B807F1B4DB8151037', 0, NULL, NULL, 2, NULL, 0, 'Bring a large stock pot of water to boil. Add 1/2 tablespoon or 7.5 mL salt to water. Cut each chayote in half lengthwise and place into pot. Let cook on medium high heat for 15 minutes. Remove from heat and set onto clean towels to drain and cool. About 10 minutes. Once cooled, scoop out seeds and some of the flesh to make room for the filling. Blot with paper towels to remove any excess moisture and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FAA654F33D03D42AC3CAFE1C7EECD8B', X'470CC9CFA70CA34B807F1B4DB8151037', X'470CC9CFA70CA34B807F1B4DB8151037', 1, NULL, NULL, 2, NULL, 0, 'While chayote is simmering, prepare the filling. Fry ground beef with white parts of the green onion. After 3 minutes, add the green part of the green onions, minced garlic, and remaining ingredients except for the broth. Continue cooking for another 6 minutes or until beef has cooked through. Remove from heat and pour in the chicken (or vegetable) stock. Cover and set aside for at least 10 minutes. If opting to keep it vegetarian, simply replace ground beef with meat substitute of your choice or sauteed vegetables.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'892AB47CDD782C49A66039D92D0C425D', X'470CC9CFA70CA34B807F1B4DB8151037', X'470CC9CFA70CA34B807F1B4DB8151037', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat. If using a charcoal grill, keep a space available for indirect grilling.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B85D6F3F6B80E34B8BA0A009C48A2C25', X'470CC9CFA70CA34B807F1B4DB8151037', X'470CC9CFA70CA34B807F1B4DB8151037', 3, NULL, NULL, 2, NULL, 0, 'Brush olive oil onto chayote halves. Make sure to get both the flesh and skin portions. Place cut side down onto grill for 3 to 4 minutes to achieve grill marks. Rotate once. Remove from grill and place about 1/3 cup or 80 mL of filling into the cut and grilled side. Press mixture to secure it in place.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB98BA7783E07B45A94BBA5FBEC0B81B', X'470CC9CFA70CA34B807F1B4DB8151037', X'470CC9CFA70CA34B807F1B4DB8151037', 4, NULL, NULL, 2, NULL, 0, 'Place chayote back onto the grill, skin side down. Cook on indirect heat for 25 to 35 minutes, or until chayote is nice and tender (not mushy). Right before taking them off the grill, add some more cheese on top of the filling and let it cook for an additional 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8134C5B79B867D47B4BB2B67892F9B44', X'470CC9CFA70CA34B807F1B4DB8151037', X'470CC9CFA70CA34B807F1B4DB8151037', 5, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat and let rest for 5 minutes. Serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F1BA83F8044634FA3797B70D9C38B67', X'CB6B59BEB5501A4DB4BA0D5821097527', X'CB6B59BEB5501A4DB4BA0D5821097527', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'398E258522C31D4DACFE5D47C3BE4242', X'CB6B59BEB5501A4DB4BA0D5821097527', X'CB6B59BEB5501A4DB4BA0D5821097527', 1, NULL, NULL, 2, NULL, 0, 'Rinse the lemons clean. If you have any plans to use or otherwise eat the peel, you may even want to go so far as to scrub them with soap before rinsing them. Pat them dry. To release extra juice, roll them on a hard surface.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2E045CB53DE364581D564441093BCCA', X'CB6B59BEB5501A4DB4BA0D5821097527', X'CB6B59BEB5501A4DB4BA0D5821097527', 2, NULL, NULL, 2, NULL, 0, 'Cut the lemons in half crosswise.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB02B4240DBE1E4885102C814082C3AA', X'CB6B59BEB5501A4DB4BA0D5821097527', X'CB6B59BEB5501A4DB4BA0D5821097527', 3, NULL, NULL, 2, NULL, 0, 'Optional: Pop out and discard any visible seeds. Use the tip of a paring knife to urge any deeper-set seeds to the surface. This is a kind service to your fellow diners since it minimizes the chance that they''ll bite into a bitter seed while enjoying their lovely grilled lemons.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D5F972695A0B74EA6C99D1CC07B04D9', X'CB6B59BEB5501A4DB4BA0D5821097527', X'CB6B59BEB5501A4DB4BA0D5821097527', 4, NULL, NULL, 2, NULL, 0, 'Use a pastry brush, silicone grilling brush, or a paper towel to brush the cut sides with a bit of oil. You can sprinkle the cut sides with fine sea salt, too, if you want to add a more intense savory effect to the grilled lemons.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D8D121ABB9F0C489D157967CFFB4A54', X'CB6B59BEB5501A4DB4BA0D5821097527', X'CB6B59BEB5501A4DB4BA0D5821097527', 5, NULL, NULL, 2, NULL, 0, 'Set the lemons cut-side-down on the hot grill or in the frying pan. Cook until the lemons are heated through and charred on the cut side, about 3 minutes. Ideally, you''ll cook them without moving them so they char nicely, but adjust them if the heat seems uneven so they char evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A4FB9BD1250F247BFF7632B34AD06BF', X'CB6B59BEB5501A4DB4BA0D5821097527', X'CB6B59BEB5501A4DB4BA0D5821097527', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBEE2E80AF8886418B2C4A82148F17C6', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C157B8A8E641945A7F3F5C978CBFCB9', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, dissolve the 2 tablespoons of salt in 1 cup warm water; stir until the salt is fully dissolved. Add 6 to 8 cups cold water. Set the brine aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3552177D76D2A544A480C308DD2B72EB', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 2, NULL, NULL, 2, NULL, 0, 'Cut off and discard the stem end of the eggplant.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17E7A639B030AE4CBDF93C20857104BF', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 3, NULL, NULL, 2, NULL, 0, 'Cut the eggplant into 3/4-inch thick slices (either diagonal, crosswise, or lengthwise).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57272E520818704D9B429302C2B27AE5', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 4, NULL, NULL, 2, NULL, 0, 'Place the slices in the saltwater brine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A6D50CD91497B44A37DA79050735514', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 5, NULL, NULL, 2, NULL, 0, 'Weigh the eggplant slices down with an upside-down plate; let soak for at least 30 minutes or up to an hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96F6F316816F184DB8BEABBAA45EEF0F', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat a charcoal or gas grill to medium-high heat. You should be able to hold your hand about an inch above the cooking grate for 3 to 4 seconds before pulling it away from the heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16BD6CB45723904193182F24445BAF5B', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 7, NULL, NULL, 2, NULL, 0, 'Drain the eggplant and pat it dry with paper towels or a clean kitchen towel.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEB255E27D47D64885CF8BE969AE1168', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 8, NULL, NULL, 2, NULL, 0, 'Lay the slices on a large baking sheet or tray. Brush one side with olive oil and sprinkle with salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BC8883B40782342BB382ABA879E208F', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 9, NULL, NULL, 2, NULL, 0, 'Place the slices with the oiled side on the grill. Close the lid if using a gas or electric grill and cook until grill marks appear; about 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46F160B38FF58143B45A48293AB2BF31', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 10, NULL, NULL, 2, NULL, 0, 'Brush the top sides of the slices with oil and sprinkle them with salt. Turn the slices over, close the lid of the grill, and cook until grill marks appear on the other side and the eggplant is very tender; about 5 more minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C04BBD626DFC764C80224941C883135D', X'78C79DD57408C8499EF280832ED01FDF', X'78C79DD57408C8499EF280832ED01FDF', 11, NULL, NULL, 2, NULL, 0, 'Serve hot or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B18E9F1982B7A04089BD21A8E810D7C7', X'61942EE14D4D87429A6DF4E07D63D594', X'61942EE14D4D87429A6DF4E07D63D594', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CD7431E742CB246812FCE026998FD3B', X'61942EE14D4D87429A6DF4E07D63D594', X'61942EE14D4D87429A6DF4E07D63D594', 1, NULL, NULL, 2, NULL, 0, 'Prepare a gas or charcoal grill for high heat (you can hold your hand an inch above the cooking grate for just 1 to 2 seconds before pulling it away because it feels so hot).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0C91ED499CB3542A5E2F731AA70637E', X'61942EE14D4D87429A6DF4E07D63D594', X'61942EE14D4D87429A6DF4E07D63D594', 2, NULL, NULL, 2, NULL, 0, 'Trim or peel the asparagus (see How to Trim Asparagus and/or How to Peel Asparagus if you''ve never prepared asparagus before).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7784E52948682247855BD146876F8789', X'61942EE14D4D87429A6DF4E07D63D594', X'61942EE14D4D87429A6DF4E07D63D594', 3, NULL, NULL, 2, NULL, 0, 'Toss the asparagus with the oil to coat it thoroughly. Feel free to use more oil, if you want; to coat it with a minimal amount involves lots of tossing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B3DE1503D5E944BA184919211AE2304', X'61942EE14D4D87429A6DF4E07D63D594', X'61942EE14D4D87429A6DF4E07D63D594', 4, NULL, NULL, 2, NULL, 0, 'Place the asparagus on the grill, cover, and cook until the asparagus color heightens and grill marks appear, 4 to 5 minutes. Turn the asparagus, cover, and cook until tender, about 4 to 5 more minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E9D98D38DF16A4A9E8182D67C4B3A1A', X'61942EE14D4D87429A6DF4E07D63D594', X'61942EE14D4D87429A6DF4E07D63D594', 5, NULL, NULL, 2, NULL, 0, 'Remove the asparagus from the grill. Sprinkle it with salt and lemon juice, if you like. Serve the grilled asparagus hot, warm, or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5AFDADDFA237A4784A5F4A62D1F7D3E', X'61942EE14D4D87429A6DF4E07D63D594', X'61942EE14D4D87429A6DF4E07D63D594', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDDCC5F154CA6947AF49745708DF5304', X'187DE1767CA472408B166AC00C24A3E1', X'187DE1767CA472408B166AC00C24A3E1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2F0CCC415B4074AA443CBD52CE3DE98', X'187DE1767CA472408B166AC00C24A3E1', X'187DE1767CA472408B166AC00C24A3E1', 1, NULL, NULL, 2, NULL, 0, 'Heat the grill to medium-high');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9217C79DE32FA9488B1FD4BA2D49B72F', X'187DE1767CA472408B166AC00C24A3E1', X'187DE1767CA472408B166AC00C24A3E1', 2, NULL, NULL, 2, NULL, 0, 'When the grill is hot, slice eggplant about 1/2-inch thick.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5645BBF554B6154194CA7F1CC4926F9A', X'187DE1767CA472408B166AC00C24A3E1', X'187DE1767CA472408B166AC00C24A3E1', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the olive oil, balsamic vinegar, garlic, herbs, salt, and pepper. Brush both sides of the eggplant slices with the oil and vinegar mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A91835730C89D14AB3F5846531A0059F', X'187DE1767CA472408B166AC00C24A3E1', X'187DE1767CA472408B166AC00C24A3E1', 4, NULL, NULL, 2, NULL, 0, 'Place eggplant on the hot preheated grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C0E498D2591004BACE3F63ACEAC3EC2', X'187DE1767CA472408B166AC00C24A3E1', X'187DE1767CA472408B166AC00C24A3E1', 5, NULL, NULL, 2, NULL, 0, 'Grill the eggplant for about 10 to 15 minutes, turning and basting occasionally.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B76D4D5A4D371499AF3BF8A0877A895', X'C5742A67E172B4439BDB1C48BC3FC018', X'C5742A67E172B4439BDB1C48BC3FC018', 0, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 400 F (Anywhere in the 350 to 425 F range will work, so if you already have something in the oven, there is no need to wait before popping the broccoli rabe in).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'141524406C10D141ACB2EC916CE3BE05', X'C5742A67E172B4439BDB1C48BC3FC018', X'C5742A67E172B4439BDB1C48BC3FC018', 1, NULL, NULL, 2, NULL, 0, 'Trim the broccoli rabe and spread it on a baking sheet or in a roasting pan. Drizzle the greens with the olive oil. Use your hands or two spoons to toss the broccoli rabe around, so it is all lightly coated with the oil. Spread the broccoli rabe back out into as much of a single layer as possible and sprinkle it with salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFAA6F8094AF2842958CB9D825727583', X'C5742A67E172B4439BDB1C48BC3FC018', X'C5742A67E172B4439BDB1C48BC3FC018', 2, NULL, NULL, 2, NULL, 0, 'Roast the broccoli rabe until it is reduced in size by about 2/3 and is brown on the edges, about 20 minutes. Depending on your oven (and what else may be in it), this may take longer. Feel free to use tongs to toss the leaves around a bit every 10 minutes or so. Serve roasted broccoli rabe hot or warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37CBFDB579A04D439F7112879A6527F1', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', 0, NULL, NULL, 2, NULL, 0, 'Brush the olive oil onto the cut sides of the acorn squash.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A11C8ADFE15AD9419A333D3F61C4F7DE', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', 1, NULL, NULL, 2, NULL, 0, 'Cover the squash halves with foil, and poke holes to let steam and smoke through.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B54917254203E4F8645EC08F9A988FB', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', 2, NULL, NULL, 2, NULL, 0, 'Prepare smoker. Place squash on smoker rack cut-side down. Smoke at 225 F for 1 1/2 to 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0830C7624701747B8478FAA2F83B076', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', 3, NULL, NULL, 2, NULL, 0, 'When the squash is done, remove from the smoker and allow to sit while you make the spiced butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C41F1B38F0E3F458854C0C810E47294', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', 4, NULL, NULL, 2, NULL, 0, 'Melt the butter along with the sugar and spices in a saucepan. Stir to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB427A61F916FA4184051DEB5E69E903', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', X'3A31975A83D5D64B9C7E0C9BBBBEB41F', 5, NULL, NULL, 2, NULL, 0, 'Remove the aluminum foil from the squash. Place a spoonful of the butter mixture on each squash half when ready to serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C51CAAC11DA9A947953CE8E7F31AF512', X'2661CD95922CEC41958CD4B95FC0C2A4', X'2661CD95922CEC41958CD4B95FC0C2A4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8549E9773994DC4FAD825148ED60F5C5', X'2661CD95922CEC41958CD4B95FC0C2A4', X'2661CD95922CEC41958CD4B95FC0C2A4', 1, NULL, NULL, 2, NULL, 0, 'Bake potatoes until tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5B98C74BFADD44292457129158AF196', X'2661CD95922CEC41958CD4B95FC0C2A4', X'2661CD95922CEC41958CD4B95FC0C2A4', 2, NULL, NULL, 2, NULL, 0, 'Cut lengthwise in quarters.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30E00864110D4840B21F194886C90C74', X'2661CD95922CEC41958CD4B95FC0C2A4', X'2661CD95922CEC41958CD4B95FC0C2A4', 3, NULL, NULL, 2, NULL, 0, 'Scoop out to make shells about 1/2 inch thick.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17B22908E0DDBF42A790B45CFABD4080', X'2661CD95922CEC41958CD4B95FC0C2A4', X'2661CD95922CEC41958CD4B95FC0C2A4', 4, NULL, NULL, 2, NULL, 0, 'Paint with olive oil and coat both sides with dry ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6CB36F7031766439FCDCAF949DB40FB', X'2661CD95922CEC41958CD4B95FC0C2A4', X'2661CD95922CEC41958CD4B95FC0C2A4', 5, NULL, NULL, 2, NULL, 0, 'Place in prepared smoker at about 200 F/95 C and smoke for 20 to 40 minutes, depending on how smoky you want them.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E06521F63F90B499A599FFBDBA6F0DA', X'2661CD95922CEC41958CD4B95FC0C2A4', X'2661CD95922CEC41958CD4B95FC0C2A4', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle with cheese and let smoke until cheese melts.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB55166CA9C11846A60C1998C904E1D7', X'2661CD95922CEC41958CD4B95FC0C2A4', X'2661CD95922CEC41958CD4B95FC0C2A4', 7, NULL, NULL, 2, NULL, 0, 'Top with chopped green onions and serve with barbecue sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9091D913A292EE4AB1BD3107FEF12578', X'2661CD95922CEC41958CD4B95FC0C2A4', X'2661CD95922CEC41958CD4B95FC0C2A4', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBBE4FAFC2AFC644B14E840F23F76EFB', X'943477FE11AA394D86227CDF00BE9A0F', X'943477FE11AA394D86227CDF00BE9A0F', 0, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 375 F. Peel the carrots and trim off their ends, if you like; or simply trim their greens as pictured. Cut the carrots in long, even sticks or even, angled cross-section slices. If the carrots are relatively thin, as those pictured here, you can leave them whole—just be sure to check their thicker ends for tenderness at the end.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'646A02AA43E7464B942AE559B31EC66F', X'943477FE11AA394D86227CDF00BE9A0F', X'943477FE11AA394D86227CDF00BE9A0F', 1, NULL, NULL, 2, NULL, 0, 'Put the prepared carrots in a large baking dish. If you want to add a few garlic cloves to roast with the carrots, add them now—peel them or at least move most of their papery peel to make them easier to eat later. This is also the time to add a few sprigs of herbs if you like.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4A16C0A3114CE4C964826FB34A2B39A', X'943477FE11AA394D86227CDF00BE9A0F', X'943477FE11AA394D86227CDF00BE9A0F', 2, NULL, NULL, 2, NULL, 0, 'Drizzle the carrots (and whatever you may have added with them) with the oil. Toss to coat the carrot pieces thoroughly. Sprinkle with salt and pop the dish in the preheated oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'438D35DA7D18B34EBC86B87A3604EFA7', X'943477FE11AA394D86227CDF00BE9A0F', X'943477FE11AA394D86227CDF00BE9A0F', 3, NULL, NULL, 2, NULL, 0, 'Roast until the carrots are starting to brown on the bottom, about 20 minutes. Stir and continue roasting until the carrots are tender with lovely brown edges, about 20 more minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6E59613C0CDB248B3AF28388CE96697', X'5F6242D33B9E0A488F6D8B823573A9C6', X'5F6242D33B9E0A488F6D8B823573A9C6', 0, NULL, NULL, 2, NULL, 0, 'Combine orange juice, brown sugar, and butter in a saucepan. Simmer until it starts to thicken.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'706B121AE23AF2438E7DC60C88909DF0', X'5F6242D33B9E0A488F6D8B823573A9C6', X'5F6242D33B9E0A488F6D8B823573A9C6', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84FCA49FD925FE48BBC886B793312418', X'5F6242D33B9E0A488F6D8B823573A9C6', X'5F6242D33B9E0A488F6D8B823573A9C6', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat the grill and oil the cooking grates.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B13DB06925BB34DBC3E2D4229ADAE8E', X'5F6242D33B9E0A488F6D8B823573A9C6', X'5F6242D33B9E0A488F6D8B823573A9C6', 3, NULL, NULL, 2, NULL, 0, 'Place carrots on grill over medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'951DC7B7FE57454AAC56E449A49FB71C', X'5F6242D33B9E0A488F6D8B823573A9C6', X'5F6242D33B9E0A488F6D8B823573A9C6', 4, NULL, NULL, 2, NULL, 0, 'Grill the carrots, turning occasionally for about 4 to 5 minutes depending on the size of the carrots.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6EC1DED64584F46AE245FD0A33D11A1', X'5F6242D33B9E0A488F6D8B823573A9C6', X'5F6242D33B9E0A488F6D8B823573A9C6', 5, NULL, NULL, 2, NULL, 0, 'As carrots start to soften, brush with glaze.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'393B830BD590A74D99A13300A1B3026D', X'5F6242D33B9E0A488F6D8B823573A9C6', X'5F6242D33B9E0A488F6D8B823573A9C6', 6, NULL, NULL, 2, NULL, 0, 'Continue cooking until glaze starts to darken on the carrots (about 3 to 4 more minutes).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'554BED699A52F14A9A92E2248C86E2D6', X'5F6242D33B9E0A488F6D8B823573A9C6', X'5F6242D33B9E0A488F6D8B823573A9C6', 7, NULL, NULL, 2, NULL, 0, 'Remove, season lightly with salt and pepper and serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D07C6FB03C484747851A4693BAB48B2D', X'1D8D9469A031BD4688FC3F690559D06A', X'1D8D9469A031BD4688FC3F690559D06A', 0, NULL, NULL, 2, NULL, 0, 'Cut poblano chiles in half lengthwise to create a boat for filling and scrape out seeds.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E1A8C9ADBB5234A9A04229742D81305', X'1D8D9469A031BD4688FC3F690559D06A', X'1D8D9469A031BD4688FC3F690559D06A', 1, NULL, NULL, 2, NULL, 0, 'Heat 2 tablespoons olive oil in a nonstick skillet. Add onion, garlic, jalapenos, red bell pepper, cilantro and cumin, and cook over medium heat until golden brown for about 4 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AB234EB3390234BA45FA81CE411682E', X'1D8D9469A031BD4688FC3F690559D06A', X'1D8D9469A031BD4688FC3F690559D06A', 2, NULL, NULL, 2, NULL, 0, 'Remove pan from heat and stir in the baked beans, hot sauce and 8 ounces of cheese. Add a bit of salt and pepper to taste. Spoon the mixture into the hollowed chiles and sprinkle with remaining cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87A56487F13F274DA0235B23298B0690', X'1D8D9469A031BD4688FC3F690559D06A', X'1D8D9469A031BD4688FC3F690559D06A', 3, NULL, NULL, 2, NULL, 0, 'Set up your grill for indirect grilling and preheat to medium. Arrange the chilies on the grill away from the direct heat. Cook until the chilies are tender and cheese is browned and bubbling about 30 to 40 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0898A03B33355A45992585C7B1250BF3', X'1D8D9469A031BD4688FC3F690559D06A', X'1D8D9469A031BD4688FC3F690559D06A', 4, NULL, NULL, 2, NULL, 0, 'Remove from grill and serve at once.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14FE56AB17040240895367C308BB20B4', X'9C60BCCD4F7FD844B6EE7874028134A6', X'9C60BCCD4F7FD844B6EE7874028134A6', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F89BC437A6DD2944A6AD549087C24E72', X'9C60BCCD4F7FD844B6EE7874028134A6', X'9C60BCCD4F7FD844B6EE7874028134A6', 1, NULL, NULL, 2, NULL, 0, 'Cut unpeeled potatoes into 8 equal pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBF2B922A7A1A7409C66CB0C20002BC0', X'9C60BCCD4F7FD844B6EE7874028134A6', X'9C60BCCD4F7FD844B6EE7874028134A6', 2, NULL, NULL, 2, NULL, 0, 'Place potatoes pieces in the center of a 12-14 inch piece of aluminum foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6119D09E87917343A04BC51CF5DC85B2', X'9C60BCCD4F7FD844B6EE7874028134A6', X'9C60BCCD4F7FD844B6EE7874028134A6', 3, NULL, NULL, 2, NULL, 0, 'Combine other ingredients, except cheese, in a small bowl and drizzle over potatoes. Toss lightly. Fold foil over and seal.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8F04CE1450A864EBCB59A822FF412E4', X'9C60BCCD4F7FD844B6EE7874028134A6', X'9C60BCCD4F7FD844B6EE7874028134A6', 4, NULL, NULL, 2, NULL, 0, 'Place on medium hot grill for 30-40 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA39B6DCEC7C5042873B831C94A96CB9', X'9C60BCCD4F7FD844B6EE7874028134A6', X'9C60BCCD4F7FD844B6EE7874028134A6', 5, NULL, NULL, 2, NULL, 0, 'Potatoes should be easily pierced with a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE1850F4790DA4438CB13A107C51C93C', X'9C60BCCD4F7FD844B6EE7874028134A6', X'9C60BCCD4F7FD844B6EE7874028134A6', 6, NULL, NULL, 2, NULL, 0, 'Open packet and add cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D49F7D2A2098DF428F2586FF4D3078D4', X'9C60BCCD4F7FD844B6EE7874028134A6', X'9C60BCCD4F7FD844B6EE7874028134A6', 7, NULL, NULL, 2, NULL, 0, 'Return to grill for 5 minutes or until cheese is melted.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E20417CEBB325E4A9C3306335C0122C3', X'9C60BCCD4F7FD844B6EE7874028134A6', X'9C60BCCD4F7FD844B6EE7874028134A6', 8, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCE6001E384E654E89462759B14921C3', X'FE8973640488EF49A1B1433708E80991', X'FE8973640488EF49A1B1433708E80991', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B64CB0278E2F9547B942BC811F7D3B60', X'FE8973640488EF49A1B1433708E80991', X'FE8973640488EF49A1B1433708E80991', 1, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1A7F19FF0F018409E18989B0E0A2909', X'FE8973640488EF49A1B1433708E80991', X'FE8973640488EF49A1B1433708E80991', 2, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a large bowl and toss well to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95EA87887317454D809317569F37D135', X'FE8973640488EF49A1B1433708E80991', X'FE8973640488EF49A1B1433708E80991', 3, NULL, NULL, 2, NULL, 0, 'Tear off two very large pieces of foil to make a double-thick sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1810E94889C4FC4A8B19BE36E3C832F7', X'FE8973640488EF49A1B1433708E80991', X'FE8973640488EF49A1B1433708E80991', 4, NULL, NULL, 2, NULL, 0, 'Place potatoes in the center of the sheet, fold sides up to create a packet and to seal in juices.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F462E91A8A717846A2AFADE2E0B92B16', X'FE8973640488EF49A1B1433708E80991', X'FE8973640488EF49A1B1433708E80991', 5, NULL, NULL, 2, NULL, 0, 'Place packet on grill and cook for 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B263CDEF0A959B4380FC95DE4A717026', X'FE8973640488EF49A1B1433708E80991', X'FE8973640488EF49A1B1433708E80991', 6, NULL, NULL, 2, NULL, 0, 'Turn, and allow to cook for an additional 15-18 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B46AE55B07FB4F458BB619BF561CD43E', X'FE8973640488EF49A1B1433708E80991', X'FE8973640488EF49A1B1433708E80991', 7, NULL, NULL, 2, NULL, 0, 'To serve, cut packet with kitchen shears, allow to cool for a minute or two, then open foil enough to take out potatoes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13034384536BC04E82810B683E3E16FC', X'5BF53F9256B35345AAC86FDFFBE68B21', X'5BF53F9256B35345AAC86FDFFBE68B21', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'256C6AA0D55A784D9047C2F0D0C557F7', X'5BF53F9256B35345AAC86FDFFBE68B21', X'5BF53F9256B35345AAC86FDFFBE68B21', 1, NULL, NULL, 2, NULL, 0, 'Heat a gas or charcoal grill to high heat (you should be able to hold our hand about an inch over the cooking grate for just 1 to 2 seconds before pulling it away from the heat).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D9BF6F4BE22BE4DB3F12EB281ABDB27', X'5BF53F9256B35345AAC86FDFFBE68B21', X'5BF53F9256B35345AAC86FDFFBE68B21', 2, NULL, NULL, 2, NULL, 0, 'While the grill heats, trim the cabbage, removing any damaged outer leaves and cutting off any browned or excess stem still attached to the head, but leaving the center core intact. Cut the cabbage into 8 wedges, being careful to keep a bit of the core in each cabbage wedge to hold the wedge of leaves together.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'769EF49FA23D4C40AD5F7FB6BF121510', X'5BF53F9256B35345AAC86FDFFBE68B21', X'5BF53F9256B35345AAC86FDFFBE68B21', 3, NULL, NULL, 2, NULL, 0, 'For ease of handling, and to make sure the leaves don''t fall apart and into the flames once they''re on the grill, skewer each cabbage wedge onto a grill-proof skewer. Brush the cabbage wedges lightly with olive oil and sprinkle them with salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FF7FAF3188718489965F05F6F45087C', X'5BF53F9256B35345AAC86FDFFBE68B21', X'5BF53F9256B35345AAC86FDFFBE68B21', 4, NULL, NULL, 2, NULL, 0, 'Set the skewered cabbage wedges on the hot grill and cook until the edges are blackened and crispy and the center of the cabbage wedges are warm, turning to color all sides, 10 to 15 minutes. For more tender cabbage, cover the grill while they cook.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB414916CAD5974C91B2A56199E261DD', X'5BF53F9256B35345AAC86FDFFBE68B21', X'5BF53F9256B35345AAC86FDFFBE68B21', 5, NULL, NULL, 2, NULL, 0, 'Serve the grilled cabbage hot, warm, or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F261C6F6922954D9CDA27B7F94BB8C1', X'5BF53F9256B35345AAC86FDFFBE68B21', X'5BF53F9256B35345AAC86FDFFBE68B21', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94D85C6593BE37478BC50EB6D10F3F02', X'368282301F18DF4BA3617373ABC1B861', X'368282301F18DF4BA3617373ABC1B861', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57B6E4902CF24C489B8A8B39D59DFC7B', X'368282301F18DF4BA3617373ABC1B861', X'368282301F18DF4BA3617373ABC1B861', 1, NULL, NULL, 2, NULL, 0, 'Trim ends off tough ends from asparagus. Cut prosciutto slices into strips. Wrap half of one slice onto each asparagus spear.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F863CC2AC6D7FD4B9683B163423F1673', X'368282301F18DF4BA3617373ABC1B861', X'368282301F18DF4BA3617373ABC1B861', 2, NULL, NULL, 2, NULL, 0, 'Combine minced garlic with olive oil and brush each spear with the mixture. The seasoned and wrapped spears with black pepper and place onto grill. Cook for 4 to 5 minutes, turning occasionally. Keep a close eye on these as the prosciutto is paper thin and prone to burning. Once cooked through, remove asparagus from heat and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60A894DE1315DF4181696E24134275B3', X'4074F70875B5E04396206C463C421E28', X'4074F70875B5E04396206C463C421E28', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat an oven to 425 F (if you have something else going, know that these will roast up at any temperature down to 350 F; they''ll just take longer and not get quite as nicely browned without getting a tad desiccated).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7622EF0F032FC41A3376D7CD97DCB26', X'4074F70875B5E04396206C463C421E28', X'4074F70875B5E04396206C463C421E28', 1, NULL, NULL, 2, NULL, 0, 'While the oven heats, trim the green beans.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF890351A6E05F49AA956B35A0D573EF', X'4074F70875B5E04396206C463C421E28', X'4074F70875B5E04396206C463C421E28', 2, NULL, NULL, 2, NULL, 0, 'Put the green beans in a large bowl, drizzle them with the olive oil, and toss to coat the beans thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BB3416D76804C409D5B712B7550C7D1', X'4074F70875B5E04396206C463C421E28', X'4074F70875B5E04396206C463C421E28', 3, NULL, NULL, 2, NULL, 0, 'Lift the green beans out of the bowl (allowing any excess oil to stay in the bottom of the bowl, you want them coated but not dripping in oil) and spread them in a single layer on a baking sheet or in a shallow baking pan. The single layer part is important since it gives the beans the space they need to get properly roasted, instead of being piled together, which will lead them to partially steam more than roasted.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'836C4C8FA139604B9E1FD64C1F5541F1', X'4074F70875B5E04396206C463C421E28', X'4074F70875B5E04396206C463C421E28', 4, NULL, NULL, 2, NULL, 0, 'Roast the beans for about 10 minutes. While the beans roast, peel and mince the garlic if you''re using it. Remove the pan from the oven, shake the beans about a bit, spreading them back into a single layer, and sprinkle with the garlic and lemon zest, if you like. Roast the beans until they''re starting to brown on the tips, for another 2 or 3 minutes. If you want the beans more browned, feel free to leave them in the oven a bit longer.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'838067FF539D104AACC4E0F94124F944', X'4074F70875B5E04396206C463C421E28', X'4074F70875B5E04396206C463C421E28', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle with salt to taste, and serve hot or warm. They''re still good closer to room temperature, which is good to know if you''re looking for something to put on a buffet.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22A1B39DDEE3FF498DC5695F03876415', X'1A67D4073497814BAB8656104E0F1924', X'1A67D4073497814BAB8656104E0F1924', 0, NULL, NULL, 2, NULL, 0, 'Heat an oven to 400 F (anywhere between 350 F and 425 F will work, just adjust the timing up or down and pay attention to how the asparagus looks). While the oven heats, trim the asparagus. You can either break off the tough ends where they naturally snap by grabbing both ends of an asparagus spear and bending it until it breaks, or, for a more elegant presentation, you can trim off any dried part at the end of each spear and peel the spears.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1EBF0599D368F4088C8846495C5C251', X'1A67D4073497814BAB8656104E0F1924', X'1A67D4073497814BAB8656104E0F1924', 1, NULL, NULL, 2, NULL, 0, 'Lay the asparagus spears in a single layer in a baking dish or on a baking sheet. If you have an oven-proof serving dish, use that so you can bring the roasted asparagus directly to the table and serve it in the dish, but any oven-proof baking vessel works and a baking sheet is almost ideal since it let''s the hot air circulate around the asparagus so effectively.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CDEFBEB4A98DB408EEA878F9170C6EE', X'1A67D4073497814BAB8656104E0F1924', X'1A67D4073497814BAB8656104E0F1924', 2, NULL, NULL, 2, NULL, 0, 'Drizzle the asparagus with olive oil. Toss it around a bit so it''s all more or less coated with the oil and spread it back into a single layer. Sprinkle it lightly with sea salt. Bake the asparagus until it''s tender and starting to brown at the tips, about 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF9BA55E5D0FFD45BBF002C343F1D9E1', X'1A67D4073497814BAB8656104E0F1924', X'1A67D4073497814BAB8656104E0F1924', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the roasted asparagus with freshly grated lemon zest and/or squirt it with fresh lemon juice, if you like. If you have a Meyer lemon, its floral aroma pairs particularly well with fresh spring asparagus.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'882406171939494BA7F7C0858E129EB8', X'1A67D4073497814BAB8656104E0F1924', X'1A67D4073497814BAB8656104E0F1924', 4, NULL, NULL, 2, NULL, 0, 'Serve the roasted asparagus hot, warm, or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3075AF202911324E9F5F4673CA035FB2', X'264372DF1DC4B2499F42A45C25855F01', X'264372DF1DC4B2499F42A45C25855F01', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CA3B82F4BA3BC4DA494C92F66EA2D70', X'264372DF1DC4B2499F42A45C25855F01', X'264372DF1DC4B2499F42A45C25855F01', 1, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF064BC268BE4F4BBA1BCB071465CB1A', X'264372DF1DC4B2499F42A45C25855F01', X'264372DF1DC4B2499F42A45C25855F01', 2, NULL, NULL, 2, NULL, 0, 'While the oven heats, trim the green garlic: remove and discard the roots and dark green stalk. Exactly how much of the stalk to trim depends on how much chewing you''re interested in doing. Peel off as much of the paper peel that has developed; this will depend greatly on exactly how `green` and immature the garlic is.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0CFE706C5276A41B9902CB0FFD4EF7A', X'264372DF1DC4B2499F42A45C25855F01', X'264372DF1DC4B2499F42A45C25855F01', 3, NULL, NULL, 2, NULL, 0, 'Lay the trimmed green garlic in a baking pan or on a piece of aluminum foil large enough to fold over and cover the green garlic. Lightly drizzle the green garlic with the olive oil and sprinkle it with the salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46C549717956E846BB9EAFFB168D0DF9', X'264372DF1DC4B2499F42A45C25855F01', X'264372DF1DC4B2499F42A45C25855F01', 4, NULL, NULL, 2, NULL, 0, 'Cover the green garlic with foil and roast it for 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF505E20109A2E4F96F34F2A58706A53', X'264372DF1DC4B2499F42A45C25855F01', X'264372DF1DC4B2499F42A45C25855F01', 5, NULL, NULL, 2, NULL, 0, 'Remove the foil covering the green garlic and continue roasting the green garlic until it''s tender and nicely browned, about 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C1F582A70500B4D91D7B0EC0ABFCE82', X'264372DF1DC4B2499F42A45C25855F01', X'264372DF1DC4B2499F42A45C25855F01', 6, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature, and know that an additional drizzle of extra virgin olive oil doesn''t hurt anything.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F29036DAF70464FAF404D6C9F1591B5', X'264372DF1DC4B2499F42A45C25855F01', X'264372DF1DC4B2499F42A45C25855F01', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2990A20EA61F95498FCC63493ABCF23E', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 0, NULL, NULL, 2, NULL, 0, 'Peel and core apples.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E1E3D7606249144B91D27B447B6B6BB', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 1, NULL, NULL, 2, NULL, 0, 'Cut into thin strips of matchstick pieces.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C5D87C9EC3CA84DB0C038AFF1A416FE', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 2, NULL, NULL, 2, NULL, 0, 'Place into a non-metallic bowl with 1 cup of water the juice of 2 lemons. This will help to keep the apples from turning brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D0EC86420843C46BB31E7E9AF19EECE', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 3, NULL, NULL, 2, NULL, 0, 'Let apples sit in the mixture for 15 minutes or so.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64F2C628E3B0684FB81D29B992794EBB', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 4, NULL, NULL, 2, NULL, 0, 'In a large plastic or glass bowl, add mustard, lemon juice, salt, thyme, sugar, and caraway seeds. Stir a few times.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16FA98316C3A584EBFC2252CE1A256CC', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 5, NULL, NULL, 2, NULL, 0, 'Using a whisk, slowly whisk in oil to emulsify the dressing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12D6C82062CA1046B30E39763D1C5F94', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 6, NULL, NULL, 2, NULL, 0, 'Once the oil is well incorporated, stir in mayonnaise.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'773D13CE08A67940B1854041A007907D', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 7, NULL, NULL, 2, NULL, 0, 'Add the chopped cabbage and apple pieces and toss to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CAAD7377872DD4F84028B3D32B95DD5', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 8, NULL, NULL, 2, NULL, 0, 'Cover bowl with plastic wrap and place into the refrigerator for at least 1 hour before serving. This slaw should be used the day it is prepared.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55238CEA27D8F4469290AAD26485C7C2', X'7AA22774530A864ABCBF526337174E09', X'7AA22774530A864ABCBF526337174E09', 9, NULL, NULL, 2, NULL, 0, 'Add to pulled pork or pulled chicken sandwiches or serve slaw as a side dish. Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E56197DF96487640AF562CB1F4E6EEA7', X'F5F7CBDE470E9F4FA5585609B0A0C7EC', X'F5F7CBDE470E9F4FA5585609B0A0C7EC', 0, NULL, NULL, 2, NULL, 0, 'Cut pumpkins in half, top to bottom. The insides can be scooped out if you desire, but it isn''t necessary. It is easier to remove all this once the pumpkins are cooked. Prepare smoker of a two hour smoke at a temperature around 250 F. (120 C.). If using a charcoal grill, build the fire to one side for indirect grilling. Adjust vents to maintain a temperature around 250 F. (120 C.).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C0BB8B684078A4990B6767E39C0E11C', X'F5F7CBDE470E9F4FA5585609B0A0C7EC', X'F5F7CBDE470E9F4FA5585609B0A0C7EC', 1, NULL, NULL, 2, NULL, 0, 'Place pumpkin halves on the smoker or charcoal grill (away from the fire) and add wood chunks to the fire. This process will smoke the pumpkins as they cook and take 1 to 2 hours depending on the size of the pumpkins. Add additional smoking wood as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89B30B57B3851C4DBFBB702EBD463429', X'F5F7CBDE470E9F4FA5585609B0A0C7EC', X'F5F7CBDE470E9F4FA5585609B0A0C7EC', 2, NULL, NULL, 2, NULL, 0, 'Once finished (the flesh of the pumpkin will be tender and they will start to collapse down), remove from smoker or grill and allow to cool. Pumpkins can be transferred to the oven to finish cooking if you want to limit the smokiness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F27C0E18D917148B701523E8B29914A', X'F5F7CBDE470E9F4FA5585609B0A0C7EC', X'F5F7CBDE470E9F4FA5585609B0A0C7EC', 3, NULL, NULL, 2, NULL, 0, 'Remove seeds and insides if you haven''t already, peel away skin and coarse chop the remaining flesh. The pumpkin should have a strong smoky smell. Use as desired for soups, sauces, or pumpkin pie filling.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2769151625089A4582661168B2BCE6BF', X'8D2EC9740B610143B10B3FF1B2F769C0', X'8D2EC9740B610143B10B3FF1B2F769C0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FC788437964E249866D779CE2A13B3A', X'8D2EC9740B610143B10B3FF1B2F769C0', X'8D2EC9740B610143B10B3FF1B2F769C0', 1, NULL, NULL, 2, NULL, 0, 'Wash beans and pour into a large saucepan or dutch oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B8DF97902B0234F9286A5274C8485C3', X'8D2EC9740B610143B10B3FF1B2F769C0', X'8D2EC9740B610143B10B3FF1B2F769C0', 2, NULL, NULL, 2, NULL, 0, 'Add all other ingredients except the salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DBC8ADD039D344980E0F28315D5B1C6', X'8D2EC9740B610143B10B3FF1B2F769C0', X'8D2EC9740B610143B10B3FF1B2F769C0', 3, NULL, NULL, 2, NULL, 0, 'Bring to a boil while stirring.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DE4187A42CA2046AD95F5D62C77D6A6', X'8D2EC9740B610143B10B3FF1B2F769C0', X'8D2EC9740B610143B10B3FF1B2F769C0', 4, NULL, NULL, 2, NULL, 0, 'Reduce heat and allow to simmer for two hours, covered.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42AEC9BAC78E684892703BBFF031C9AF', X'8D2EC9740B610143B10B3FF1B2F769C0', X'8D2EC9740B610143B10B3FF1B2F769C0', 5, NULL, NULL, 2, NULL, 0, 'Stir occasionally, bringing up the beans from the bottom to prevent burning to so they will cook evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2185D27D08853E4699DEDE30FDEAC79E', X'8D2EC9740B610143B10B3FF1B2F769C0', X'8D2EC9740B610143B10B3FF1B2F769C0', 6, NULL, NULL, 2, NULL, 0, 'Add the salt after 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'566E19CD87808247888F2ED438970CFC', X'8D2EC9740B610143B10B3FF1B2F769C0', X'8D2EC9740B610143B10B3FF1B2F769C0', 7, NULL, NULL, 2, NULL, 0, 'The beans are done when soft but still hold their shape.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A5EF1FAF90F9D4AB56A090485C9820B', X'8D2EC9740B610143B10B3FF1B2F769C0', X'8D2EC9740B610143B10B3FF1B2F769C0', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EED4A5A366C6F40AEEB25D190E8EC2F', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC66839EB4691441BBF12094452AB771', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A79694DCC6DCE46B9B6A72B486F5508', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 2, NULL, NULL, 2, NULL, 0, 'Place cut the pumpkin into a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C8FA1E57E5E0A41807AACB99C9FF88D', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 3, NULL, NULL, 2, NULL, 0, 'Pour melted butter on top and add the remaining seasonings. Gently toss to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5D60F1A56A5564CB812755342C72B35', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 4, NULL, NULL, 2, NULL, 0, 'Scoop pumpkin out onto a very large piece of aluminum foil. The size is entirely dependent on how much pumpkin you have. Just make sure that the foil is large enough that once folded, there is ample room left for steam to rise (at least 2-3 inches). Consider using two pieces if necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'200FB42516DDDB4EB87C3A2D230445E7', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 5, NULL, NULL, 2, NULL, 0, 'Gather edges of the aluminum foil from opposing sides and roll downward, leaving enough room for steam.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF633C2BEE64EA4C8D30E998C10A2E20', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 6, NULL, NULL, 2, NULL, 0, 'Secure open ends and place onto the grill and cook indirectly for 35 to 40.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B9071CD4B640B44A558A89B2A44CF81', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 7, NULL, NULL, 2, NULL, 0, 'To check for doneness, use kitchen gloves and carefully unravel packet.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'012052566203DB488A38137EA211BAD7', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 8, NULL, NULL, 2, NULL, 0, 'Pierce a few slices with a knife. If it goes through with ease, the pumpkin is done.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3FFC377B4FBA94AA936789E9109848E', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 9, NULL, NULL, 2, NULL, 0, 'Rewrap the packet and remove from the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23FAD3DC8969144CA673564CEA56B101', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 10, NULL, NULL, 2, NULL, 0, 'Allow pumpkin to sit for a 5 minutes before serving. If using in another recipe, let the pumpkin cool completely before using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22B4BF5929EA5A4C81F992F82A108002', X'A61F7956AD60E845AEA4FE322123A516', X'A61F7956AD60E845AEA4FE322123A516', 11, NULL, NULL, 2, NULL, 0, 'If baking in the oven, place the packet on a cookie sheet and cook at 350 F, for 35-40 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2468E2ECE4C39D4F96B22AFEC0E63FBD', X'405311F209D3A64983B6C08B8EACF9A0', X'405311F209D3A64983B6C08B8EACF9A0', 0, NULL, NULL, 2, NULL, 0, 'To prepare dressing, add apple jelly, red wine vinegar, water, and salt to a saucepan. Bring mixture to a boil, reduce heat and let simmer until mixture has reduced to a syrupy consistency. Remove from heat and cool to room temperature before using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00203FBB66F8D041AFBD8EE714E3A952', X'405311F209D3A64983B6C08B8EACF9A0', X'405311F209D3A64983B6C08B8EACF9A0', 1, NULL, NULL, 2, NULL, 0, 'Place sweet potato slices into a large stock pot with water and cook for 10-13 minutes. Potatoes should be soft, but not all the way cooked through. Remove from water, pay dry with paper towel and place onto baking sheet, cutting board, or platter. Brush with olive oil and add salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37809253E9BBE247B8BD08AC5189A5E3', X'405311F209D3A64983B6C08B8EACF9A0', X'405311F209D3A64983B6C08B8EACF9A0', 2, NULL, NULL, 2, NULL, 0, 'Carefully place sweet potato and pear slices onto grill grate. Cook both for 3-4 minutes per side. Watch for burning. Once potatoes and pears have good grill marks and a caramelized surface, remove from heat and place onto clean baking sheet. This will allow them to cool slightly before assembling.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB355ADBF0E7DF4BBDBCEFBB3260F151', X'405311F209D3A64983B6C08B8EACF9A0', X'405311F209D3A64983B6C08B8EACF9A0', 3, NULL, NULL, 2, NULL, 0, 'To assemble: place salad greens onto large serving dish, top with sweet potatoes and pears, then crumbled feta cheese. Drizzle cooled dressing onto salad and garnish with chopped pecans.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E72BB3A5FC64FB43850D2644E510FDFA', X'D6AE76DAA70A8341BA0D9927A96B3864', X'D6AE76DAA70A8341BA0D9927A96B3864', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B083332A1C56264F98421D9D648BFF5E', X'D6AE76DAA70A8341BA0D9927A96B3864', X'D6AE76DAA70A8341BA0D9927A96B3864', 1, NULL, NULL, 2, NULL, 0, 'Thread vegetables onto skewers, alternating between the items. Place in a shallow baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDB0A898EC0F4F489C741FDFE4CA6B45', X'D6AE76DAA70A8341BA0D9927A96B3864', X'D6AE76DAA70A8341BA0D9927A96B3864', 2, NULL, NULL, 2, NULL, 0, 'Combine lemon juice, vinegar, Italian seasoning, and salt. Pour over kebabs. Cover and refrigerate for 1 hour to overnight.?');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E31DE4CC27393A469ACD74CA2C8CD87C', X'D6AE76DAA70A8341BA0D9927A96B3864', X'D6AE76DAA70A8341BA0D9927A96B3864', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill. Place skewers on grill over medium heat and grill for 10 minutes or until done, turning occasionally as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D936A2B70C0F841B228F722F3BBE0C3', X'D6AE76DAA70A8341BA0D9927A96B3864', X'D6AE76DAA70A8341BA0D9927A96B3864', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B0422AA2508EB4E96A68D3DDB49F14B', X'E65E739965B8784BA4A102AE2D294CC7', X'E65E739965B8784BA4A102AE2D294CC7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'598AAA4C3F346B47BD477A6C18C16BD7', X'E65E739965B8784BA4A102AE2D294CC7', X'E65E739965B8784BA4A102AE2D294CC7', 1, NULL, NULL, 2, NULL, 0, 'Gently pull back the husks on each ear of corn. Remove the silk but not the husks.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87057F91FCB4394DBD89F3DA90827DCE', X'E65E739965B8784BA4A102AE2D294CC7', X'E65E739965B8784BA4A102AE2D294CC7', 2, NULL, NULL, 2, NULL, 0, 'Place the ears in a large pan and fill with water to cover the corn. Let sit for a minimum of 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'611E456590660448885F55FAEA16C367', X'E65E739965B8784BA4A102AE2D294CC7', X'E65E739965B8784BA4A102AE2D294CC7', 3, NULL, NULL, 2, NULL, 0, 'Combine olive oil with onion powder, brown sugar, paprika, chili powder, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6FC7C13FC664942927E904608FA3153', X'E65E739965B8784BA4A102AE2D294CC7', X'E65E739965B8784BA4A102AE2D294CC7', 4, NULL, NULL, 2, NULL, 0, 'Remove from water and brush the corn with oil mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14A47E5098B62245A3DC52E636861A5A', X'E65E739965B8784BA4A102AE2D294CC7', X'E65E739965B8784BA4A102AE2D294CC7', 5, NULL, NULL, 2, NULL, 0, 'Pull the husks back over the corn.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D0CDC7123E3148B8423D46A3501426', X'E65E739965B8784BA4A102AE2D294CC7', X'E65E739965B8784BA4A102AE2D294CC7', 6, NULL, NULL, 2, NULL, 0, 'Prepare your smoker and smoke the ears of corn at 225 F/110 C for about 45 minutes to one hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A412A57A2E4A444B057018BE13DA902', X'E65E739965B8784BA4A102AE2D294CC7', X'E65E739965B8784BA4A102AE2D294CC7', 7, NULL, NULL, 2, NULL, 0, 'When they''re done smoking, pull back the husks (you can leave them whole or cut them in half if you''d like) and top with green onions. Serve and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77948A92CFEF514BA9FAABF69A1F496B', X'9DD9B5E9CC2A9D48AD55A8FF9A4B0C2E', X'9DD9B5E9CC2A9D48AD55A8FF9A4B0C2E', 0, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 400 F. While the oven heats, trim the okra, removing the tough stem ends. Cut the okra pods in half lengthwise for faster, more even roasting. Cutting them before cooking in the dry heat of the oven also seems to further minimize the slime-factor for which these nutty pods are so famous.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B258F49896C2C64BB232DCE87830E291', X'9DD9B5E9CC2A9D48AD55A8FF9A4B0C2E', X'9DD9B5E9CC2A9D48AD55A8FF9A4B0C2E', 1, NULL, NULL, 2, NULL, 0, 'Trim off and discard the root end and any dried part from the green ends of the green onions. Cut the green onions in half lengthwise, and then cut them (white and green parts) into 3-inch lengths.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C5227D8D8D344DBE22418FFC133573', X'9DD9B5E9CC2A9D48AD55A8FF9A4B0C2E', X'9DD9B5E9CC2A9D48AD55A8FF9A4B0C2E', 2, NULL, NULL, 2, NULL, 0, 'Either in a roasting pan, on a rimmed baking sheet, or in a large bowl, combine the okra and the green onion. Drizzle the vegetables with the oil, and sprinkle with the salt. Toss to coat everything evenly and thoroughly. Spread the vegetables in a single layer in a roasting pan or rimmed baking sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBD7F403CCFD17469473DC83752C788F', X'9DD9B5E9CC2A9D48AD55A8FF9A4B0C2E', X'9DD9B5E9CC2A9D48AD55A8FF9A4B0C2E', 3, NULL, NULL, 2, NULL, 0, 'Roast the okra until it''s tender and its edges are browning; this tends to happen somewhere in the neighborhood of 25 minutes. After 15 minutes, you may want to take the pan out and stir the vegetables around a bit, being sure to settle the okra back into a single layer before returning the pan to the oven to finish roasting. Serve the roasted okra hot or warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBF11CE0333496409E2F4C921E5CE16F', X'BBA6D8C56B20564E8C1B03BE515CF709', X'BBA6D8C56B20564E8C1B03BE515CF709', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 450 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'072D63493A933B4AA8637625BD167ED1', X'BBA6D8C56B20564E8C1B03BE515CF709', X'BBA6D8C56B20564E8C1B03BE515CF709', 1, NULL, NULL, 2, NULL, 0, 'Scrub the new potatoes and then cut them in half or quarter them, depending on their size.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'254062902A91304D8FE6DC94E69E147A', X'BBA6D8C56B20564E8C1B03BE515CF709', X'BBA6D8C56B20564E8C1B03BE515CF709', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, toss the new potatoes with the oil, garlic, thyme, salt, and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB9330724544BA459FB3AA38E4EBB508', X'BBA6D8C56B20564E8C1B03BE515CF709', X'BBA6D8C56B20564E8C1B03BE515CF709', 3, NULL, NULL, 2, NULL, 0, 'Transfer potatoes to a baking pan and arrange in a single layer. Roast the new potatoes for 40 to 45 minutes, or until the potatoes are tender and browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E63EA33F327D9E40977E332E2B9785B2', X'B41A889741EA484BBB88B304AFEE00DC', X'B41A889741EA484BBB88B304AFEE00DC', 0, NULL, NULL, 2, NULL, 0, 'Cut tomatoes in half and remove the stems.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8DF8C26AA0896438B33224FE73A9757', X'B41A889741EA484BBB88B304AFEE00DC', X'B41A889741EA484BBB88B304AFEE00DC', 1, NULL, NULL, 2, NULL, 0, 'Using 2 tablespoons of olive oil, lightly brush the cut ends of the tomatoes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80E659BCE7F541478E4E683E4C8F1E3B', X'B41A889741EA484BBB88B304AFEE00DC', X'B41A889741EA484BBB88B304AFEE00DC', 2, NULL, NULL, 2, NULL, 0, 'Place tomatoes cut side down on a preheated grill over a medium heat. Grill for about 5 minutes or until the tomatoes start to brown and you can easily lift the skin off. Remove from grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83396292FE92A7498B870B5AAB66CAA5', X'B41A889741EA484BBB88B304AFEE00DC', X'B41A889741EA484BBB88B304AFEE00DC', 3, NULL, NULL, 2, NULL, 0, 'Carefully, remove skins from tomatoes, lightly blend in a blender or food processor and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51065A92D863D443958F61373F1534DE', X'B41A889741EA484BBB88B304AFEE00DC', X'B41A889741EA484BBB88B304AFEE00DC', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat 2 tablespoons of olive oil in a large pot over a medium heat. Add onions. While stirring cook onions until they soften and turn translucent.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'177F235C9E03A74AA127F4B3B4282DA6', X'B41A889741EA484BBB88B304AFEE00DC', X'B41A889741EA484BBB88B304AFEE00DC', 5, NULL, NULL, 2, NULL, 0, 'Add remaining ingredients and stir together. Simmer on low for 10 to 15 minutes. Add water if the sauce becomes too thick.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC0921B6110ADD40A4C645E451D957B7', X'10CA54101C44B049B05FE104792B769E', X'10CA54101C44B049B05FE104792B769E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53D206928258414FA3EE7CC90B5A0799', X'10CA54101C44B049B05FE104792B769E', X'10CA54101C44B049B05FE104792B769E', 1, NULL, NULL, 2, NULL, 0, 'Mix olive oil, vinegar, lemon juice, mustard, basil, parsley, garlic, salt, and black pepper together in a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDEDCCE5E65CF444BA151F0AD82F7266', X'10CA54101C44B049B05FE104792B769E', X'10CA54101C44B049B05FE104792B769E', 2, NULL, NULL, 2, NULL, 0, 'Place cut vegetables in a resealable plastic bag. Pour marinade over vegetables, toss to coat, and seal bag. Place bag in the refrigerator and let sit for 1 to 3 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF2F1F60BBB39941A889C8CDD42A62CF', X'10CA54101C44B049B05FE104792B769E', X'10CA54101C44B049B05FE104792B769E', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill to medium heat. Place vegetables, alternating as you go, on skewers. Reserve marinade to brush vegetables as they cook.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD2BE7C04F3A094CB9E4F0A122373AD6', X'10CA54101C44B049B05FE104792B769E', X'10CA54101C44B049B05FE104792B769E', 4, NULL, NULL, 2, NULL, 0, 'Place kebabs on grill and cover. Cook for 10 to 12 minutes, brushing with marinade every 2 to 3 minutes and rotating the skewers to cook the vegetables evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5CE82F558A9F6419A9E9E1AF87239C8', X'10CA54101C44B049B05FE104792B769E', X'10CA54101C44B049B05FE104792B769E', 5, NULL, NULL, 2, NULL, 0, 'Remove kababs from grill when the vegetables are brown and tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B59435466BB554D989A497F39A74B0D', X'B19C1FD6F0EC3346BA444032D930B5FE', X'B19C1FD6F0EC3346BA444032D930B5FE', 0, NULL, NULL, 2, NULL, 0, 'Combine sugar, vinegar, oil, mustard, mayonnaise, salt and pepper in a saucepan over a low heat. Stir until sugar dissolves. Refrigerate dressing for 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9F30BC3E0ADF74CA203700D61AB2218', X'B19C1FD6F0EC3346BA444032D930B5FE', X'B19C1FD6F0EC3346BA444032D930B5FE', 1, NULL, NULL, 2, NULL, 0, 'Chop cabbage and sweet onion finely into thin pieces. Remove stem, core with seeds and any white parts of bell pepper. Slice into thin strips. If vegetables have accumulated a lot of liquid from washing and slicing, pat them dry with paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38F6C1D043A1744CAD7099D769A5D21F', X'B19C1FD6F0EC3346BA444032D930B5FE', X'B19C1FD6F0EC3346BA444032D930B5FE', 2, NULL, NULL, 2, NULL, 0, 'Place vegetables in a serving dish with cooled dressing. Using kitchen tongs, carefully, toss vegetables with dressing until well coated. Transfer to a serving bowl, cover with plastic wrap and refrigerate for 1 1/2-2 hours before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'477D36B6763E40418BC7D34E5E74F2B4', X'B19C1FD6F0EC3346BA444032D930B5FE', X'B19C1FD6F0EC3346BA444032D930B5FE', 3, NULL, NULL, 2, NULL, 0, 'Coleslaw will keep for up to 2 days after preparation if stored, covered in the refrigerator. Serve dish on the side with brisket, pork, or chicken, or serve right on top of pulled pork in a delicious sandwich.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8DBC807C351E64A97DA8735FE60B2D5', X'35A32B899A840D4C9A72202382BA775C', X'35A32B899A840D4C9A72202382BA775C', 0, NULL, NULL, 2, NULL, 0, 'Trim off the top and outer skin of the onions. Cut a small bowl in the top of the onion and pour in 1 tablespoon of barbecue sauce. Wrap onions tightly in aluminum foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5748E39544E5C4F815AC9BC713AB636', X'35A32B899A840D4C9A72202382BA775C', X'35A32B899A840D4C9A72202382BA775C', 1, NULL, NULL, 2, NULL, 0, 'Place on grill and cook on an upper rack for about 45 minutes. These onions can also be cooked over a campfire.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'875A53B623F975459DE767C7C095AB9F', X'35A32B899A840D4C9A72202382BA775C', X'35A32B899A840D4C9A72202382BA775C', 2, NULL, NULL, 2, NULL, 0, 'Remove wrapped onions from the grill and set them aside to rest for 10 minutes. Carefully unwrap and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1E6B051E14B4341A86F41283AEC6B4D', X'652F3DE9BEE28E47A0C193309E95E3CC', X'652F3DE9BEE28E47A0C193309E95E3CC', 0, NULL, NULL, 2, NULL, 0, 'Prepare smoker for a 1 1/2-hour cook time at around 275 F/135 C.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F73322046B2C442B73AE737C681B226', X'652F3DE9BEE28E47A0C193309E95E3CC', X'652F3DE9BEE28E47A0C193309E95E3CC', 1, NULL, NULL, 2, NULL, 0, 'Cut the stem end of the butternut squash off, about an inch from the top. Brush with olive oil and sprinkle with salt. Place on the smoker and cook until soft all the way through.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1135855228F1FD48A72D07F73274DF53', X'652F3DE9BEE28E47A0C193309E95E3CC', X'652F3DE9BEE28E47A0C193309E95E3CC', 2, NULL, NULL, 2, NULL, 0, 'A butternut squash, smoked for an hour and a half will have a strong smoke flavor. While this will cook down some when used in other recipes (like smoked butternut squash soup). To reduce the smoke flavor, cut the smoking time in half and finish the squash off in the oven.?');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'983D3B6A2002C04BA4209F6D876A2F48', X'A1E40D55D90D824AB3ECE6B6993DBAB7', X'A1E40D55D90D824AB3ECE6B6993DBAB7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FD77841156CB945A24BAE9907269B85', X'A1E40D55D90D824AB3ECE6B6993DBAB7', X'A1E40D55D90D824AB3ECE6B6993DBAB7', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 Fahrenheit. Place the carrots in a roasting pan or baking dish just large enough to hold them in a single layer.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82BB3A0D433B8344AC37FDB32C075F93', X'A1E40D55D90D824AB3ECE6B6993DBAB7', X'A1E40D55D90D824AB3ECE6B6993DBAB7', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl or large liquid measuring cup, whisk together the water or broth, maple syrup, soy sauce, vinegar, olive oil, smoked paprika, and garlic powder. Pour the marinade over the carrots, cover the pan with foil, and roast in the preheated oven for 20 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E136541303A0F8429B535D243224EEC6', X'A1E40D55D90D824AB3ECE6B6993DBAB7', X'A1E40D55D90D824AB3ECE6B6993DBAB7', 3, NULL, NULL, 2, NULL, 0, 'While the carrots are roasting, place the tomatoes in a small baking dish, drizzle with 1 teaspoon of olive oil, and toss to coat. Place the sliced mushrooms in another small baking dish, add the remaining teaspoon of olive oil, and toss to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51FACC41BF48734A911328C77B989B53', X'A1E40D55D90D824AB3ECE6B6993DBAB7', X'A1E40D55D90D824AB3ECE6B6993DBAB7', 4, NULL, NULL, 2, NULL, 0, 'After the carrots have roasted for 20 minutes, carefully remove the foil (watch out for escaping steam!). Turn the carrots and return the pan to the oven, along with the tomatoes and mushrooms. Roast, stirring the tomatoes and mushrooms once or twice, until the carrots are tender and glazed with the marinade, and the tomatoes and mushrooms are beginning to caramelize in spots, about 20 to 25 minutes more.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81FEB5FE9DD6834DAE798D4C79A655C9', X'A1E40D55D90D824AB3ECE6B6993DBAB7', X'A1E40D55D90D824AB3ECE6B6993DBAB7', 5, NULL, NULL, 2, NULL, 0, 'To assemble: Warm or toast the hot dog buns if desired. Place a carrot in each bun, and top with a spoonful of roasted mushrooms and roasted tomatoes. Add some crumbled goat cheese or feta and/or a sprinkling of za''atar, if desired. Serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A33C1F95036BA147822A10C4B1418F06', X'9F1F4987F7DB57488FA7645AAB1F38BC', X'9F1F4987F7DB57488FA7645AAB1F38BC', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
