INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0120CE55A8D42A49932110FF9C74F904', X'9F1F4987F7DB57488FA7645AAB1F38BC', X'9F1F4987F7DB57488FA7645AAB1F38BC', 5, NULL, NULL, 2, NULL, 0, 'Continue grilling for about 6 to 8 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA04A447A1A4C3419155492CAA367CF1', X'9F1F4987F7DB57488FA7645AAB1F38BC', X'9F1F4987F7DB57488FA7645AAB1F38BC', 6, NULL, NULL, 2, NULL, 0, 'Turn and spread on more honey butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2585AEBAB6CD04FA81B15A24E9EC98C', X'9F1F4987F7DB57488FA7645AAB1F38BC', X'9F1F4987F7DB57488FA7645AAB1F38BC', 7, NULL, NULL, 2, NULL, 0, 'Continue grilling until the honey butter gets all bubbly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8DAB0A890B6C443A209019E5DC48FBE', X'9F1F4987F7DB57488FA7645AAB1F38BC', X'9F1F4987F7DB57488FA7645AAB1F38BC', 8, NULL, NULL, 2, NULL, 0, 'Make sure to keep an eye on the potatoes once the honey mixture is applied, given the sugar content of the honey.');
