INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A828E1AD34B06D49B74298330E196C6E', X'73AF762FFA0C30409877D5889852C861', X'73AF762FFA0C30409877D5889852C861', 4, NULL, NULL, 2, NULL, 0, 'Brush the other sides with oil and sprinkle them with salt. Turn the tomato halves over, close the lid on a gas grill and cook until grill marks appear on the other side, about 5 more minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32378DA0AE8EF2429D24135654E69280', X'73AF762FFA0C30409877D5889852C861', X'73AF762FFA0C30409877D5889852C861', 5, NULL, NULL, 2, NULL, 0, 'Use tongs or a spatula to transfer the tomatoes back to the baking sheet or onto a servings platter.');
