INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EB0B5A861E4314DBD4DFC047162B3B1', X'40CFD235F6D8614292C84876896C88A9', X'40CFD235F6D8614292C84876896C88A9', 1, NULL, NULL, 2, NULL, 0, 'Pat the brisket dry on all sides using paper towels. With a spoon, sprinkle the rub liberally onto the meat. Simultaneously press it in and rub it with your fingertips until it adheres to the entire surface. Turn the meat and repeat on all sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96E22EA1B42E1642821E41A135DB6505', X'40CFD235F6D8614292C84876896C88A9', X'40CFD235F6D8614292C84876896C88A9', 2, NULL, NULL, 2, NULL, 0, 'Let the brisket sit for up to 24 hours (in the refrigerator) or cook immediately according to your recipe.');
