INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCC9469EF9C97E40B31E48C386680D15', X'77BE1F39ECA4974DA58CCA6B183A6B20', X'77BE1F39ECA4974DA58CCA6B183A6B20', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the dark-brown sugar, paprika, salt, black pepper, white pepper, garlic powder, onion powder, and cayenne pepper until well mixed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14D341E82C7D8C499CB94785907C07BE', X'77BE1F39ECA4974DA58CCA6B183A6B20', X'77BE1F39ECA4974DA58CCA6B183A6B20', 2, NULL, NULL, 2, NULL, 0, 'To use, apply rub evenly over ribs. The general rule is what sticks is the perfect amount. Cook as directed in your recipe. Discard any rub that has come into contact with raw meat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CCFBF2AF2BFAE4C8AC2B819E683234D', X'77BE1F39ECA4974DA58CCA6B183A6B20', X'77BE1F39ECA4974DA58CCA6B183A6B20', 3, NULL, NULL, 2, NULL, 0, 'Store any leftover rub that has not come into contact with raw meat by transferring it to a container with a lid. Store in a cool, dark place for up to 6 months.');
