INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CEB72A266195C448161355C7BED513E', X'B09359C0C252724BAD914A030B9FB285', X'B09359C0C252724BAD914A030B9FB285', 6, NULL, NULL, 2, NULL, 0, 'Preheat the grill. Place the beef ribs on the grill and cook over medium-low heat. Rewarm the basting sauce. Once the ribs have reached 165 F, begin basting. Turn and baste the ribs with the basting sauce every 10 minutes, until finished cooking, about 1 1/2 hours. Remove the ribs from the heat and serve immediately.');
