INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C737AF689F36A478746527B68170EB4', X'7B22F43EE45C1D499352C74A6748B840', X'7B22F43EE45C1D499352C74A6748B840', 2, NULL, NULL, 2, NULL, 0, 'Apply the rub to pork with a shaker right before it goes into the smoker, charcoal grill, gas grill, or oven. Apply the rub evenly over ribs. The general rule is what sticks is the perfect amount.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57B8C7610EBC6E41A875375606F97694', X'4B74A6FFB1DA9244A23F74FF4C9F0C32', X'4B74A6FFB1DA9244A23F74FF4C9F0C32', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients and mix well. Work into the surface of the brisket, especially over the exposed meat. Cook as directed. This rub may be stored in an airtight container in a cool, dark place for several months.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05A0F44BE7D84B4EA7705DA73544C9F2', X'4B74A6FFB1DA9244A23F74FF4C9F0C32', X'4B74A6FFB1DA9244A23F74FF4C9F0C32', 1, NULL, NULL, 2, NULL, 0, 'Double or triple the recipe if you intend to smoke a few briskets. Make sure that you portion out what you will be using and save the rest as instructed above. Discard any remaining rub that has come into contact with raw meat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67621EBD731BC245B64FDAB94D474DF6', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'023D749E40D3D640BF07E15BF771F952', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', 1, NULL, NULL, 2, NULL, 0, 'Trim excess fat from brisket.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3155CD24F8706746892A1207BF377F76', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', 2, NULL, NULL, 2, NULL, 0, 'Season with garlic powder, onion powder, black pepper, cayenne, and paprika.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'478A287DCCB1EA40A378F04374081363', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', 3, NULL, NULL, 2, NULL, 0, 'Place on the rotisserie and put on a preheated grill. Prop the lid a little and let cook.');
