INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23ADA48C5878024F9080ACA9B3F362E0', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', 5, NULL, NULL, 2, NULL, 0, 'Add water, cider vinegar, brown sugar, Worcestershire sauce, chili powder, and a little salt and pepper to taste. Let simmer for 10 to 15 minutes. Add beer.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'140A010F197C5A44AD0EAC0D997A5102', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', 6, NULL, NULL, 2, NULL, 0, 'Baste over brisket about every 30 minutes. Cook brisket over low heat for about 4 to 5 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70D6CD6C01165745B3A2DA158FA83989', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', 7, NULL, NULL, 2, NULL, 0, 'Remove brisket from grill and slice thinly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E0B6F5A4E60ED468376B8FEFB616162', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', X'A1B6FF4BBCC3E94D88D75EFE935C3E35', 8, NULL, NULL, 2, NULL, 0, 'Serve with your favorite sides and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'170704D012DDD244A0066BB2D10794F7', X'17D91BA2ECD97247AE97AA6AD56DA599', X'17D91BA2ECD97247AE97AA6AD56DA599', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E52D54FBBC80246959BF41F310280DF', X'17D91BA2ECD97247AE97AA6AD56DA599', X'17D91BA2ECD97247AE97AA6AD56DA599', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, mix rub ingredients into a paste. Coat pork shoulder with rub, cover with plastic wrap and refrigerate 1 hours. Preheat smoker and prepare for an 8 hour smoke.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B62363A4429704B94B86F01C7AA583D', X'17D91BA2ECD97247AE97AA6AD56DA599', X'17D91BA2ECD97247AE97AA6AD56DA599', 2, NULL, NULL, 2, NULL, 0, 'Make sure wood chips are soaked and drained before using. Add charcoal and wood chips as necessary to keep the temperature low, around 225 F/110 C. Smoke pork shoulder for about 8 hours or until internal temperatures reach 185 F/85 C.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F37B41F56192CE4586BBEFCFBF3BC48C', X'17D91BA2ECD97247AE97AA6AD56DA599', X'17D91BA2ECD97247AE97AA6AD56DA599', 3, NULL, NULL, 2, NULL, 0, 'Once the pork is cooked remove from the smoker. Wrap tightly in foil and allow to sit for an hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A5807D72F570F438C85A6F46C1CC2E4', X'17D91BA2ECD97247AE97AA6AD56DA599', X'17D91BA2ECD97247AE97AA6AD56DA599', 4, NULL, NULL, 2, NULL, 0, 'Afterward, unwrap pork butt and shred.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B4ECDF3CAB44546B598B344999D81BB', X'17D91BA2ECD97247AE97AA6AD56DA599', X'17D91BA2ECD97247AE97AA6AD56DA599', 5, NULL, NULL, 2, NULL, 0, 'Combine pork with 1/3 of your favorite table sauce. Keep warm and serve on white bread buns with remaining sauce on the side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5AA1947FFF7CF478BDB0505C01B6F03', X'17D91BA2ECD97247AE97AA6AD56DA599', X'17D91BA2ECD97247AE97AA6AD56DA599', 6, NULL, NULL, 2, NULL, 0, 'Top with cole slaw and favorite finishing sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CC8B47E0058514BB60760E9AC212A73', X'17D91BA2ECD97247AE97AA6AD56DA599', X'17D91BA2ECD97247AE97AA6AD56DA599', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'180374F55B074D4FB655179B60B72BF0', X'B7C7B8F4BC5BDA45A04C813C32397D8A', X'B7C7B8F4BC5BDA45A04C813C32397D8A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
