INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE171A29F6046B48B20B54193E15CAB1', X'BF72F5039C35664594939601760EE56F', X'BF72F5039C35664594939601760EE56F', 2, NULL, NULL, 2, NULL, 0, 'If making ahead of time, store in an airtight container in a cool cupboard for up to 6 months. Do not store any rub that has come into contact with raw meat.');
