INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'355FCD0D57756B4B9C406E1CEF174262', X'2CBB983F0FA588428A2502F5A96EB92A', X'2CBB983F0FA588428A2502F5A96EB92A', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
