INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B646338C05BCFC4E9F077F9B3871D7A5', X'31604789ECF082468F5196F7DBA37AB2', X'31604789ECF082468F5196F7DBA37AB2', 0, NULL, NULL, 2, NULL, 0, 'Combine the mixture in a small bowl and use right away. Store mixture in an airtight container in a cool, dry place for up to 6 months after preparation.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DAD19E353338141BB0AA52ED7FB3870', X'31604789ECF082468F5196F7DBA37AB2', X'31604789ECF082468F5196F7DBA37AB2', 1, NULL, NULL, 2, NULL, 0, 'If you are interested in using this mixture in a wet rub, mix rub with the juice of two limes and 1/4 cup of oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4BA9D3ECA309844A01CD1A88965CCFB', X'38FB5CE7920D6F4680A29F30EBAEEEF8', X'38FB5CE7920D6F4680A29F30EBAEEEF8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68328789879E9847823F7B5A40A2C08E', X'38FB5CE7920D6F4680A29F30EBAEEEF8', X'38FB5CE7920D6F4680A29F30EBAEEEF8', 1, NULL, NULL, 2, NULL, 0, 'Take the whole chicken wings and flip the tips around, tucking them in so they don''t burn.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A48B134D0107E4A841373C74E3392B8', X'38FB5CE7920D6F4680A29F30EBAEEEF8', X'38FB5CE7920D6F4680A29F30EBAEEEF8', 2, NULL, NULL, 2, NULL, 0, 'Combine the spices in a large resealable plastic bag.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98BAB7F0F493FA4092368639AAA2C294', X'38FB5CE7920D6F4680A29F30EBAEEEF8', X'38FB5CE7920D6F4680A29F30EBAEEEF8', 3, NULL, NULL, 2, NULL, 0, 'Toss the chicken wings in the bag with the spices to coat. Let sit in the refrigerator for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C2A17C1C8869843AB9701362A3EB271', X'38FB5CE7920D6F4680A29F30EBAEEEF8', X'38FB5CE7920D6F4680A29F30EBAEEEF8', 4, NULL, NULL, 2, NULL, 0, 'Preheat the grill to medium-high heat and set up for indirect grilling.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A994F189A847744A1E9410980E8F16B', X'38FB5CE7920D6F4680A29F30EBAEEEF8', X'38FB5CE7920D6F4680A29F30EBAEEEF8', 5, NULL, NULL, 2, NULL, 0, 'When hot, place the chicken wings over the direct heat for 5 minutes, turning frequently.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5D5FF3D39CA104BAA708261D2A5DAD5', X'38FB5CE7920D6F4680A29F30EBAEEEF8', X'38FB5CE7920D6F4680A29F30EBAEEEF8', 6, NULL, NULL, 2, NULL, 0, 'Move the wings to the indirect (cooler) side and grill, turning once, and continue grilling until done (you want an internal temperature of 175 to 180 F). This should take about 20 to 25 minutes. Watch closely to prevent burning.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3DF242C5D11684681644EC14E5F7C13', X'38FB5CE7920D6F4680A29F30EBAEEEF8', X'38FB5CE7920D6F4680A29F30EBAEEEF8', 7, NULL, NULL, 2, NULL, 0, 'Remove the wings from grill and serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCD9DF93C88A8746AF41903C1EFC8C1D', X'B5ABD43D44AAF749AD0FF2BA52FC2695', X'B5ABD43D44AAF749AD0FF2BA52FC2695', 0, NULL, NULL, 2, NULL, 0, 'Place steak in resealable plastic bag. Combine marinade ingredients and pour over steak. Work marinade into ?the meat. Seal bag and place in refrigerator for 2 to 4 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6086E361A3D9D46AAF1D5E8965BCE72', X'B5ABD43D44AAF749AD0FF2BA52FC2695', X'B5ABD43D44AAF749AD0FF2BA52FC2695', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat. Remove steak from ?the dish, reserving marinade. Place meat on the grill and allow to cook for 15 to 17 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21085059BBE1AB4E84F6DD65ED9FFA16', X'B5ABD43D44AAF749AD0FF2BA52FC2695', X'B5ABD43D44AAF749AD0FF2BA52FC2695', 2, NULL, NULL, 2, NULL, 0, 'When ?the meat reaches desired doneness, remove from heat and allow to sit for five minutes before slicing. Serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CA1B69890CF2B4FAAFBE4674A01FBA6', X'491D36F05FF0E04F899DA2C497381689', X'491D36F05FF0E04F899DA2C497381689', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD4535A75CE8F64EB0301FFA94B0D6DE', X'491D36F05FF0E04F899DA2C497381689', X'491D36F05FF0E04F899DA2C497381689', 1, NULL, NULL, 2, NULL, 0, 'Place chilies, cloves, peppercorns, mustard and coriander seeds in a spice grinder. Grind until spices are a fine powder. Pour into a bowl and add remaining spices.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70F950D4CF3E5643B0F1530C197688D2', X'491D36F05FF0E04F899DA2C497381689', X'491D36F05FF0E04F899DA2C497381689', 2, NULL, NULL, 2, NULL, 0, 'Place pork chops onto a baking sheet. Take rub and apply all over the front and back of the meat. Let stand at room temperature for up to 20 minutes. If you are making ahead of time, coat with spices, cover with plastic wrap and place into the refrigerator. Let stand for 20 to 30 minutes at room temperature before placing onto? the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'776D648926013948A2041E8C4B23CB96', X'491D36F05FF0E04F899DA2C497381689', X'491D36F05FF0E04F899DA2C497381689', 3, NULL, NULL, 2, NULL, 0, 'To prepare for charcoal grilling, remove the cooking grate and light the charcoal in a charcoal chimney (look for a red, ashen color to the briquettes). You will need enough charcoal to cover half the coal grate, about two layers thick. Place cooking grate over hot coals and open vent. Oil grate and place chops onto grill directly over the coals. Grill for two minutes per side. Move to the indirect cooking space (where there are no coals) and continue cooking for 4 to 6 minutes per side or until the internal temperature is between 160 and 165 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3347B713BBE584483CF911F3D014DAA', X'491D36F05FF0E04F899DA2C497381689', X'491D36F05FF0E04F899DA2C497381689', 4, NULL, NULL, 2, NULL, 0, 'Remove from grill and cover with foil. Allow chops to rest for 5 minutes. If using a gas unit, set temperature for medium-high heat and cook 8 to 9 minutes per side or until internal temperature is right. After the meat has had a chance to rest, serve immediately with your favorite side dishes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6653F74AFBE7304784D0D4F32E049E5B', X'491D36F05FF0E04F899DA2C497381689', X'491D36F05FF0E04F899DA2C497381689', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5665FF079594B943ACDD3008170FFABF', X'154FDF7721ED144F939B9D186AE6FB38', X'154FDF7721ED144F939B9D186AE6FB38', 0, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together and store in an airtight container in a cool, dry place.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DD96C15E4F15047AA0739BE4FFD3A19', X'154FDF7721ED144F939B9D186AE6FB38', X'154FDF7721ED144F939B9D186AE6FB38', 1, NULL, NULL, 2, NULL, 0, 'To use, rub mixture over chicken, cover, and place into refrigerator for 1 hour before grilling or baking.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58A5BBD4F4ECF348A941351A41583EE1', X'154FDF7721ED144F939B9D186AE6FB38', X'154FDF7721ED144F939B9D186AE6FB38', 2, NULL, NULL, 2, NULL, 0, 'The mixture can be added to plain yogurt and lemon or lime juice to form a delicious marinade. In this case, place chicken in a resealable plastic bag, add marinade made out of rub ingredients and place into refrigerator for 2 to 6 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BDF72E4D612924984B515B38E6B5671', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', 0, NULL, NULL, 2, NULL, 0, 'Preheat smoker and add wood chips (follow your instruction manual).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2CEBA6FB7113B41AA88BFA582DD45CF', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', 1, NULL, NULL, 2, NULL, 0, 'Clean and dry the brisket.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8AAB274EAC91F46B14E6E10DA9977E1', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', 2, NULL, NULL, 2, NULL, 0, 'Combine rub ingredients and apply them onto brisket.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B17C75CC5971E14198E9B4A6EACE8566', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', 3, NULL, NULL, 2, NULL, 0, 'Place brisket in the smoker for 8 to 10 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B79B0FD0FE06794D8A85295559BD1529', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', 4, NULL, NULL, 2, NULL, 0, 'Keep the smoker temperature at about 220 F/105 C. To increase tenderness, take the brisket out of the smoker, wrap in aluminum foil and place in the oven at 180 to 200 F/80 to 90 C for the last two hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E473CB0323BE9848A3E5FE5B0EA14F21', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', 5, NULL, NULL, 2, NULL, 0, 'Typically after 8 to 10 hours a piece of meat will have absorbed as much smoke as it can and additional smoking may impart a bitter flavor.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98CA18D0637AB248AB55672F365664C1', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', 6, NULL, NULL, 2, NULL, 0, 'Take brisket off of heat and allow to sit for 20 minutes before slicing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06A7C5FF116E2047B20D2E20BD3C863E', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', X'B7D6D8BC36EA3B4DA8568ED81EF026C3', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDBC60465D826A4D96D31772E36BD889', X'CCC047C7E091324A8BD3A47589486623', X'CCC047C7E091324A8BD3A47589486623', 0, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together. Grind this paste together to force the seasonings into the garlic.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CFE052CB181D0478BBFBC14FCA48E0C', X'CCC047C7E091324A8BD3A47589486623', X'CCC047C7E091324A8BD3A47589486623', 1, NULL, NULL, 2, NULL, 0, 'Rub into the meat before cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFFBBAAECEEF2542B1BAF550243160BB', X'CCC047C7E091324A8BD3A47589486623', X'CCC047C7E091324A8BD3A47589486623', 2, NULL, NULL, 2, NULL, 0, 'This rub can be stored in an airtight container in the refrigerator for up to two weeks.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95C679FCB552064A8D528457DE3DC0D9', X'4A5C56F7CEEDD74F9C154C29C5C10E79', X'4A5C56F7CEEDD74F9C154C29C5C10E79', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B84B293809EB7E449C6FFE7B01272216', X'4A5C56F7CEEDD74F9C154C29C5C10E79', X'4A5C56F7CEEDD74F9C154C29C5C10E79', 1, NULL, NULL, 2, NULL, 0, 'In a spice grinder, grind the bay leaves into a fine powder. Place in a small bowl. Also grind the thyme, oregano, peppercorns.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEAE2088EA995445AD09E72B103A6DE5', X'4A5C56F7CEEDD74F9C154C29C5C10E79', X'4A5C56F7CEEDD74F9C154C29C5C10E79', 2, NULL, NULL, 2, NULL, 0, 'Add all ingredients to bowl and mix together. Divide into three equal parts.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3D7374BF477A74F89AC225DE52EB5FB', X'4A5C56F7CEEDD74F9C154C29C5C10E79', X'4A5C56F7CEEDD74F9C154C29C5C10E79', 3, NULL, NULL, 2, NULL, 0, 'Rub one part on the inside of the turkey; rub the second part under the skin around the breasts; and rub the last portion over the outside of the turkey.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A11B7F0007D36942AB5FC1364AC3F398', X'4A5C56F7CEEDD74F9C154C29C5C10E79', X'4A5C56F7CEEDD74F9C154C29C5C10E79', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate overnight.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'206006C7E10B5C48AD4EAC3CEBF4AEB9', X'4A5C56F7CEEDD74F9C154C29C5C10E79', X'4A5C56F7CEEDD74F9C154C29C5C10E79', 5, NULL, NULL, 2, NULL, 0, 'Cook your turkey and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DF6DBF0F44296499D95A70BD1DB13B8', X'77780DF48493FF44B7F59693236CFF14', X'77780DF48493FF44B7F59693236CFF14', 0, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45569DCF76169241B73A9B2DB4851990', X'77780DF48493FF44B7F59693236CFF14', X'77780DF48493FF44B7F59693236CFF14', 1, NULL, NULL, 2, NULL, 0, 'Store in an airtight container in a cool, dark place. This rub will last up to six months if stored properly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD8A8086AD3CEF4284E5331E4D488C06', X'77780DF48493FF44B7F59693236CFF14', X'77780DF48493FF44B7F59693236CFF14', 2, NULL, NULL, 2, NULL, 0, 'To use, simply coat meat thoroughly with a liberal amount of rub mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30D987F15EDAB3418E528C06A6B0665D', X'77780DF48493FF44B7F59693236CFF14', X'77780DF48493FF44B7F59693236CFF14', 3, NULL, NULL, 2, NULL, 0, 'Grill or bake as directed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFAF5A9BCAD1AE40A7EADBEB4D2AABA7', X'FF6BF1F1C2D2D541910CD415A70A575D', X'FF6BF1F1C2D2D541910CD415A70A575D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72EBB695B2EA0540B8297EE27DBC8D06', X'FF6BF1F1C2D2D541910CD415A70A575D', X'FF6BF1F1C2D2D541910CD415A70A575D', 1, NULL, NULL, 2, NULL, 0, 'Season the pork on the outside as you wish. Combine the apple juice or cider, apple cider vinegar, water, Worcestershire sauce, maple syrup, melted butter, and pork rub in a small mixing bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'070EDB2096E48F40B7F319D34693CD7E', X'FF6BF1F1C2D2D541910CD415A70A575D', X'FF6BF1F1C2D2D541910CD415A70A575D', 2, NULL, NULL, 2, NULL, 0, 'Using a meat injection syringe, inject the mixture into several different spots in the pork roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BCDC376158B3B4C8CC549573166EEA0', X'FF6BF1F1C2D2D541910CD415A70A575D', X'FF6BF1F1C2D2D541910CD415A70A575D', 3, NULL, NULL, 2, NULL, 0, 'Grill or smoke right away or store in the fridge until ready to use (up to a few hours).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'471CCDBDACFB5B4C8E17030795103B99', X'61DE8F5EC04C7F49803F45A8182D7AD3', X'61DE8F5EC04C7F49803F45A8182D7AD3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC8D47E0B6029C4495DC0E1CA46A0B2A', X'61DE8F5EC04C7F49803F45A8182D7AD3', X'61DE8F5EC04C7F49803F45A8182D7AD3', 1, NULL, NULL, 2, NULL, 0, 'In a large resealable food storage bag (plastic), combine the oil, vinegar, sugar, ketchup, onion, Worcestershire sauce, salt, garlic, and Tabasco. Add the chicken and turn to coat thoroughly. Seal the bag and refrigerate for at least 4 hours or overnight, turning occasionally.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81D04EDF10D74544A6FE97F9004DB0F2', X'61DE8F5EC04C7F49803F45A8182D7AD3', X'61DE8F5EC04C7F49803F45A8182D7AD3', 2, NULL, NULL, 2, NULL, 0, 'Pour marinade into a saucepan and bring to a full rolling boil over high heat. Remove from the heat and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'914DC3E8A50F2C43B305C5831098E6A9', X'61DE8F5EC04C7F49803F45A8182D7AD3', X'61DE8F5EC04C7F49803F45A8182D7AD3', 3, NULL, NULL, 2, NULL, 0, 'Heat the grill to moderate or medium-high heat, about 350 F to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6D76D69DABFE143B23C503B5D3EEF52', X'61DE8F5EC04C7F49803F45A8182D7AD3', X'61DE8F5EC04C7F49803F45A8182D7AD3', 4, NULL, NULL, 2, NULL, 0, 'First, sear the chicken over direct heat for about 2 to 3 minutes per side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'826AE16B619E994C83EC0E0698DDC715', X'61DE8F5EC04C7F49803F45A8182D7AD3', X'61DE8F5EC04C7F49803F45A8182D7AD3', 5, NULL, NULL, 2, NULL, 0, 'Continue grilling the chicken leg quarters over indirect heat -- not directly over coals or gas burners -- turning frequently and basting with the reserved boiled marinade, for about 35 to 55 minutes, depending on size.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9370437AFB83E40BF7E5A9368F57607', X'61DE8F5EC04C7F49803F45A8182D7AD3', X'61DE8F5EC04C7F49803F45A8182D7AD3', 6, NULL, NULL, 2, NULL, 0, 'The chicken should be fork tender and juices will run clear. The temperature should register at least 165 F on an instant-read thermometer inserted into the thickest part of the meat but not touching bone.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90A40926087EB743AA3496E1B2CBE000', X'61DE8F5EC04C7F49803F45A8182D7AD3', X'61DE8F5EC04C7F49803F45A8182D7AD3', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0A3CDA11087A04C8661BB64DC43376D', X'027F57A5ED547D4DB070E5CDA219E903', X'027F57A5ED547D4DB070E5CDA219E903', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53CA40C7AEA4E94D8CDF95B697420895', X'027F57A5ED547D4DB070E5CDA219E903', X'027F57A5ED547D4DB070E5CDA219E903', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter over low heat in a small saucepan. Add the chicken broth, lemon juice, garlic powder, black pepper, and white pepper. Mix well. Add salt until mixture has a good but not overpowering flavor.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86ECFF9B311D1148B9A6954C73F55F66', X'027F57A5ED547D4DB070E5CDA219E903', X'027F57A5ED547D4DB070E5CDA219E903', 2, NULL, NULL, 2, NULL, 0, 'Remove the mixture from heat and allow to cool until just warm for 8 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9D3462E78408D47AC74FB12B37C98D9', X'027F57A5ED547D4DB070E5CDA219E903', X'027F57A5ED547D4DB070E5CDA219E903', 3, NULL, NULL, 2, NULL, 0, 'Load it into a meat injector and slowly inject it into various spots in the bird. The breast meat really benefits from this mixture. It''s good to inject dozens of sites with about 2 teaspoons per site, or less if you see the fluid oozing from the site.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'261C877C69ED8C48BB88C66443FD6F0A', X'027F57A5ED547D4DB070E5CDA219E903', X'027F57A5ED547D4DB070E5CDA219E903', 4, NULL, NULL, 2, NULL, 0, 'Massage around all of the injection sites to distribute the mixture throughout the bird.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07729AEC4B843D48A006F2F977BB2468', X'027F57A5ED547D4DB070E5CDA219E903', X'027F57A5ED547D4DB070E5CDA219E903', 5, NULL, NULL, 2, NULL, 0, 'The leftover mixture can be used as a baste while the chicken or turkey is on the grill or in the oven. Otherwise, discard the leftover sauce.');
