INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B986B6B89C24F74D94FFC30E3A690AEA', X'CF0F0F2BBBA8214E8B281DEFE93572D1', X'CF0F0F2BBBA8214E8B281DEFE93572D1', 2, NULL, NULL, 2, NULL, 0, 'Place the ribs in a container large enough to lay the racks flat. Pour the marinade over the ribs, coating them completely. Make sure to turn the rack of ribs a couple of times so it is coated with the marinade on all sides. Cover the container with plastic wrap and let it sit in the refrigerator for 12 to 24 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD0E61ED2762434BAE978A06A312671D', X'CF0F0F2BBBA8214E8B281DEFE93572D1', X'CF0F0F2BBBA8214E8B281DEFE93572D1', 3, NULL, NULL, 2, NULL, 0, 'Prepare a smoker or grill. Remove the ribs from the marinade and cook according to the smoker instructions or your favorite rib recipe.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C8DD7F0E8EC342B2EED3C99E80E6D7', X'9E2878D26A408543811800456155F1E9', X'9E2878D26A408543811800456155F1E9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
