INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D22E33ACAA574478FB15BFD04FEB151', X'9E2878D26A408543811800456155F1E9', X'9E2878D26A408543811800456155F1E9', 2, NULL, NULL, 2, NULL, 0, 'Slowly drizzle in olive oil or avocado oil, whisking constantly, until well combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ED70EA47793B04D805A105BB6221917', X'9E2878D26A408543811800456155F1E9', X'9E2878D26A408543811800456155F1E9', 3, NULL, NULL, 2, NULL, 0, 'Add sea salt, oregano, pepper, thyme, onion powder, and minced garlic, and stir until well combined. Let the mixture stand at room temperature for 5 minutes or so before using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D807EAC834DB2C46AC4203C95B9B9D08', X'9E2878D26A408543811800456155F1E9', X'9E2878D26A408543811800456155F1E9', 4, NULL, NULL, 2, NULL, 0, 'Place steak in a resealable plastic bag and pour the marinade over the top, massaging it into the meat and making sure all surfaces are coated well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADBA2B6081C90144A2D5DF52592E0DD2', X'9E2878D26A408543811800456155F1E9', X'9E2878D26A408543811800456155F1E9', 5, NULL, NULL, 2, NULL, 0, 'Seal the bag, put it on a rimmed pan to catch any escaping juices or in a shallow bowl and place in the refrigerator for about 2 1/2 hours. Marinating time for beef depends on the cut and thickness. Consult this marinating beef chart to get the right time for your steak.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7D4BF140DF1D340A37482867C0C6362', X'9E2878D26A408543811800456155F1E9', X'9E2878D26A408543811800456155F1E9', 6, NULL, NULL, 2, NULL, 0, 'Once the meat is marinated, it''s time to grill according to your preferred method.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCEBB215F635CD418A9F0E8BCC403ED5', X'9E2878D26A408543811800456155F1E9', X'9E2878D26A408543811800456155F1E9', 7, NULL, NULL, 2, NULL, 0, 'This marinade can be made ahead of time and stored in an airtight container in the refrigerator for up to five days.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E27AC9AE8F0F540923B1AA812311E9A', X'F1BB4206985AC142B6846CABB5257B5C', X'F1BB4206985AC142B6846CABB5257B5C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67A919A77493A84788E3AC15600C16D7', X'F1BB4206985AC142B6846CABB5257B5C', X'F1BB4206985AC142B6846CABB5257B5C', 1, NULL, NULL, 2, NULL, 0, 'Add balsamic vinegar to a small bowl, slowly whisk in olive oil. Add salt and sugar and continue whisking until dissolved. Add garlic, oregano, basil, onion powder, black pepper, and red pepper flakes (if using for some heat). Stir to combine. Let mixture stand at room temperature for at least 5 to 10 minutes before using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EC80FA14F7E9D47A8DC9F75B9C0091D', X'F1BB4206985AC142B6846CABB5257B5C', X'F1BB4206985AC142B6846CABB5257B5C', 2, NULL, NULL, 2, NULL, 0, 'Pour into a zip-top bag and add the chicken breasts. Marinate for 30 minutes to 2 hours. This is enough marinade for 4 to 6 chicken breasts.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCEFE8C53C99C647936DD75C6E8D9EE2', X'F1BB4206985AC142B6846CABB5257B5C', X'F1BB4206985AC142B6846CABB5257B5C', 3, NULL, NULL, 2, NULL, 0, 'If making ahead of time, store marinade in an airtight container in the refrigerator for up to 5 days after initial preparation.');
