INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'217939FA41A30E4F8DECC4482C31C66A', X'11E0AFEFB099BB4A8AE2B99CC2799025', X'11E0AFEFB099BB4A8AE2B99CC2799025', 2, NULL, NULL, 2, NULL, 0, 'Slowly drizzle olive oil into the lemon juice while whisking. This will help to emulsify the mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE62C2E124EB1D42991B2EADC8B4FE13', X'11E0AFEFB099BB4A8AE2B99CC2799025', X'11E0AFEFB099BB4A8AE2B99CC2799025', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining ingredients and stir.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A02E5EA88A05EC498AEE3AA2F07F631B', X'11E0AFEFB099BB4A8AE2B99CC2799025', X'11E0AFEFB099BB4A8AE2B99CC2799025', 4, NULL, NULL, 2, NULL, 0, 'Allow the mixture to stand at room temperature for 10 minutes before using.');
