INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C37124980B5EE40A0EB6DC714EE7647', X'1DB529692CC39F48B090576F18BAA666', X'1DB529692CC39F48B090576F18BAA666', 2, NULL, NULL, 2, NULL, 0, 'Use immediately or store in a refrigerator, covered for 6 to 7 days after preparation.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9763668CFC31954E8D1BFB94886B7CC5', X'4A136DE9A5A0E846A46E68A014A7F68A', X'4A136DE9A5A0E846A46E68A014A7F68A', 0, NULL, NULL, 2, NULL, 0, 'Combine the olive oil, rice wine vinegar, red wine, garlic, lemon or lime juice, sugar, salt, and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F45D152EC630EB469A76EE256AAB9F13', X'4A136DE9A5A0E846A46E68A014A7F68A', X'4A136DE9A5A0E846A46E68A014A7F68A', 1, NULL, NULL, 2, NULL, 0, 'Heat one side of the barbecue grill and place a chunk of soaked mesquite wood directly on hot coals (or lava rocks when using a gas grill). Adjust the heat to keep the wood from flaming. You want ?to smoke it and not fire.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17FAF01513D5DB4A9B22FB66FFBF448C', X'4A136DE9A5A0E846A46E68A014A7F68A', X'4A136DE9A5A0E846A46E68A014A7F68A', 2, NULL, NULL, 2, NULL, 0, 'Place the marinade in an open pot on the opposite side of the grill and close the lid of the grill. Let it smoke for 20 to 25 minutes. The smoke will flavor the marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'739C6537C539B14FA423C3D14027579F', X'4A136DE9A5A0E846A46E68A014A7F68A', X'4A136DE9A5A0E846A46E68A014A7F68A', 3, NULL, NULL, 2, NULL, 0, 'Remove the pot and add additional red wine to replace the liquid lost by evaporation.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4DF2B5EF6B5D34F9519A757BB61B206', X'4A136DE9A5A0E846A46E68A014A7F68A', X'4A136DE9A5A0E846A46E68A014A7F68A', 4, NULL, NULL, 2, NULL, 0, 'Cool it to room temperature, then refrigerate if not using immediately. Be sure to keep it in a tightly closed container to keep the delicious smoke in the marinade and not flavoring everything else in your refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D97DCE90DFFBA64EABF3BDDFD086D966', X'0B8FF45A6993E84489DD35EC4D86AC89', X'0B8FF45A6993E84489DD35EC4D86AC89', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28F16EE536CE2845844D60EC747221AC', X'0B8FF45A6993E84489DD35EC4D86AC89', X'0B8FF45A6993E84489DD35EC4D86AC89', 1, NULL, NULL, 2, NULL, 0, '2 hours prior to grilling, prepare fish by rinsing with cold water. Pat dry with paper towel. Cut into 1-inch cubes. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6DD48A9E8A5FC49A9585CC3FB17268E', X'0B8FF45A6993E84489DD35EC4D86AC89', X'0B8FF45A6993E84489DD35EC4D86AC89', 2, NULL, NULL, 2, NULL, 0, 'Mix olive oil, lemon juice, dill, and vinegar to make the marinade. Pour marinade into a large freezer bag and add fish. Make sure fish is well coated with marinade. Place freezer bag in the refrigerator for two hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8D168C3736DCF4CB8D19BFCD744E553', X'0B8FF45A6993E84489DD35EC4D86AC89', X'0B8FF45A6993E84489DD35EC4D86AC89', 3, NULL, NULL, 2, NULL, 0, 'While fish is marinating, chop vegetables. Half the cucumber lengthwise and slice into 1/2 inch pieces. Cut the onions into eights. Cut red pepper into 1/3 chunks. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'273AD6D7CA770045BA107602C413E03B', X'0B8FF45A6993E84489DD35EC4D86AC89', X'0B8FF45A6993E84489DD35EC4D86AC89', 4, NULL, NULL, 2, NULL, 0, 'Remove fish from marinade. Discard marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB10BE81AA48FF45BD587088FF84564F', X'0B8FF45A6993E84489DD35EC4D86AC89', X'0B8FF45A6993E84489DD35EC4D86AC89', 5, NULL, NULL, 2, NULL, 0, 'Assemble kebabs according to taste. Many people like a combination of alternating pieces of meat and vegetables. This combination is entirely a matter of personal choice. It does not affect the cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79065B4F4AC54D40AA569F8016D880DF', X'0B8FF45A6993E84489DD35EC4D86AC89', X'0B8FF45A6993E84489DD35EC4D86AC89', 6, NULL, NULL, 2, NULL, 0, 'If you are using steel skewers, be sure to put a light coat of oil on them before adding the fish and vegetables. They will slide on and off much easier.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F566E5A4F03524EB004D7929E9F190F', X'0B8FF45A6993E84489DD35EC4D86AC89', X'0B8FF45A6993E84489DD35EC4D86AC89', 7, NULL, NULL, 2, NULL, 0, 'Spray the grill lightly with cooking oil and place the fish kebabs on the grill. Cook for about 4-6 minutes each side or until the fish flakes easily with a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F1B8CC526F673468470178C8BEAB3A3', X'0B8FF45A6993E84489DD35EC4D86AC89', X'0B8FF45A6993E84489DD35EC4D86AC89', 8, NULL, NULL, 2, NULL, 0, 'Serve over white rice.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C7A52908AF7A343BB82842224CD4411', X'1D91A4B0D279434783982EC0EB9CE02A', X'1D91A4B0D279434783982EC0EB9CE02A', 0, NULL, NULL, 2, NULL, 0, 'Mix together mustard and vinegar. Add minced garlic, dried sage, bay leaves, salt, and black pepper. Using a whisk, combine mixture while slowly adding oil. This will create an emulsion, blending the oil into the remaining ingredients. Let mixture stand for 5 to 10 minutes so flavors can combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77BD4B9115D30E498375ADCA2A753144', X'1D91A4B0D279434783982EC0EB9CE02A', X'1D91A4B0D279434783982EC0EB9CE02A', 1, NULL, NULL, 2, NULL, 0, 'Use right away or store in the refrigerator in an airtight container for up to 5 days after preparation.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF67D10ABE45DA4C99D73AF932F7A63F', X'1D91A4B0D279434783982EC0EB9CE02A', X'1D91A4B0D279434783982EC0EB9CE02A', 2, NULL, NULL, 2, NULL, 0, 'Marinate pork for up to 12 hours, chicken for 4 to 8 hours, vegetables and vegetable substitutes for 30 to 60 minutes. Cook as directed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B0BFE2150CAA34A82291A7144562BE7', X'88DEB7BB6819D64887D359047295267B', X'88DEB7BB6819D64887D359047295267B', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients and mix well. Let mixture stand for 5 minutes before using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C91D8C3E8C7D9419B2BC6C1ED90AE2E', X'88DEB7BB6819D64887D359047295267B', X'88DEB7BB6819D64887D359047295267B', 1, NULL, NULL, 2, NULL, 0, 'Marinade meats as follows: red meat and pork, 4 to 12 hours, chicken and other poultry, 2 to 6 hours, fish, seafood, vegetables, and meat alternatives, 15 to 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60E6F091618DCD4F8F6CED2838512331', X'88DEB7BB6819D64887D359047295267B', X'88DEB7BB6819D64887D359047295267B', 2, NULL, NULL, 2, NULL, 0, 'If making ahead of time, store in an airtight container in the fridge for no more than one week after preparation.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0ABF1073B5AF141892E7436E399EF5E', X'1887E9EE33D60147835B7573F5FE7BD6', X'1887E9EE33D60147835B7573F5FE7BD6', 0, NULL, NULL, 2, NULL, 0, 'Trim excess fat from the meat and place in ?slow cooker crockery insert. Add onion, green pepper and 1 cup water, along with the beef bouillon.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2029719ACCA76F45B8EF42A049F8723C', X'1887E9EE33D60147835B7573F5FE7BD6', X'1887E9EE33D60147835B7573F5FE7BD6', 1, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 8 to 10 hours, until meat is well done and shreds easily with a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B0F57D56672D34FA114C815457FD038', X'1887E9EE33D60147835B7573F5FE7BD6', X'1887E9EE33D60147835B7573F5FE7BD6', 2, NULL, NULL, 2, NULL, 0, 'Pour liquids into a saucepan and boil until reduced to about 1/2 cup.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D7BD9865CB03947BF462DB16C268422', X'1887E9EE33D60147835B7573F5FE7BD6', X'1887E9EE33D60147835B7573F5FE7BD6', 3, NULL, NULL, 2, NULL, 0, 'Add the reduced liquid back to the slow cooker along with the remaining ingredients; stir to mix. Cover and cook on LOW for about 1 hour, stirring occasionally. Meat should be pretty well shredded by the end of the cooking time.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'906A4528A93B0C46BF7FE68FF889691A', X'1887E9EE33D60147835B7573F5FE7BD6', X'1887E9EE33D60147835B7573F5FE7BD6', 4, NULL, NULL, 2, NULL, 0, 'Spoon over toasted bun halves to serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3E31F91A30DEB4B9A5D9EE1C68823BF', X'1EF80CFAFE3EB749A9A94F018ADCCE7B', X'1EF80CFAFE3EB749A9A94F018ADCCE7B', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a large bowl, stirring constantly until salt is dissolved.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D487132E152FB4E908A9BF10B435291', X'1EF80CFAFE3EB749A9A94F018ADCCE7B', X'1EF80CFAFE3EB749A9A94F018ADCCE7B', 1, NULL, NULL, 2, NULL, 0, 'This is enough brine for about 6 pounds of meat. Brine pork and beef for 1 hour per pound and poultry for 30 minutes per pound.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5029BFD6E13CF34B9BAC45C491931CAD', X'730B54ECF0B8864A81640B3E88051515', X'730B54ECF0B8864A81640B3E88051515', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA25516168F8504B950A446205313DD3', X'730B54ECF0B8864A81640B3E88051515', X'730B54ECF0B8864A81640B3E88051515', 1, NULL, NULL, 2, NULL, 0, 'Make sure that your Italian seasoning is finely ground. If you need to, run it through a spice grinder or a mortar and pestle.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DEEB14E2A696745BD4694580AF44D8B', X'730B54ECF0B8864A81640B3E88051515', X'730B54ECF0B8864A81640B3E88051515', 2, NULL, NULL, 2, NULL, 0, 'Combine all ingredients and mix well. This marinade will settle so you need to give it a stir every time you reload the needle.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB282F5543E42346ACC764A5332AE4A9', X'730B54ECF0B8864A81640B3E88051515', X'730B54ECF0B8864A81640B3E88051515', 3, NULL, NULL, 2, NULL, 0, 'Inject the marinade into the turkey, butter the inside, and/or season the outside if you like. Discard remaining marinade.');
