INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89232EB5FF8A9240AB18A0E89CE9D757', X'2DC898FCE617F042B55313F9376068ED', X'2DC898FCE617F042B55313F9376068ED', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'522E9D084D48D7489503E196C55B2387', X'2DC898FCE617F042B55313F9376068ED', X'2DC898FCE617F042B55313F9376068ED', 1, NULL, NULL, 2, NULL, 0, 'Combine the oil, vinegar, soy sauce, Worcestershire, ketchup, mustard, garlic, pepper, oregano, and crushed red pepper flakes in a large measuring cup or bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99A895161417BB4E915066226F7CB018', X'2DC898FCE617F042B55313F9376068ED', X'2DC898FCE617F042B55313F9376068ED', 2, NULL, NULL, 2, NULL, 0, 'Put the steak in a large food storage bag or nonreactive container and pour the marinade over it. Seal the bag and turn it several times to coat the meat; refrigerate the steak for 4 to 8 hours. Turn the steak frequently during its time in the refrigerator so the marinade covers both sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45129F06B5CBF34B8CB05ABD1F9F80D0', X'CD10E34C1C01E44DB1D04F26E1BEF7DD', X'CD10E34C1C01E44DB1D04F26E1BEF7DD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B305849E0B8B644F8FAC05E30F7C3F62', X'CD10E34C1C01E44DB1D04F26E1BEF7DD', X'CD10E34C1C01E44DB1D04F26E1BEF7DD', 1, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a blender and blend until smooth. Depending on size of the blender, this process might need to be done in batches.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FFACB1D50436647872A298A2C3C32BD', X'CD10E34C1C01E44DB1D04F26E1BEF7DD', X'CD10E34C1C01E44DB1D04F26E1BEF7DD', 2, NULL, NULL, 2, NULL, 0, 'Use marinade right away or, if making ahead, simply store in an airtight container and keep in the refrigerator for up to 7 days. We recommend making this the day before using to let the flavors marry well; however, it is not required.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EA419882AE96B4CAC9B4ECC76B97C02', X'9BAA0E89790C6E45BFBC1D0C6D9F0C80', X'9BAA0E89790C6E45BFBC1D0C6D9F0C80', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together all the ingredients, except the pork loin. Place the pork into a plastic freezer bag and pour in the marinade. Press out air, seal the bag and marinate the pork for 3 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6073E99055750D4ABA583F258571904E', X'9BAA0E89790C6E45BFBC1D0C6D9F0C80', X'9BAA0E89790C6E45BFBC1D0C6D9F0C80', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F. Place the pork in a baking dish, season with additional salt and fresh ground black pepper to taste, and pour in the marinade. Add 1/2 cup of water, and place in the preheated oven. Roast for about 1 hour, basting occasionally with the marinade until the internal temperature reaches 145 to 150 F. Remove and let the pork loin rest for 10 minutes before serving. Slice and serve with some of the marinade drizzled over the top.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03A8205DE7CA9549B42931A8A48D6CD1', X'A64E0CE61083714B88BDFF67A3BC8245', X'A64E0CE61083714B88BDFF67A3BC8245', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
