INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2BB6E3552ABBA4E844BD76FC99AEE41', X'A8C687F9C124734F9D7C98921B09A145', X'A8C687F9C124734F9D7C98921B09A145', 3, NULL, NULL, 2, NULL, 0, 'Place the turkey in the plastic bag and close. Refrigerate for 10 to 20 hours.');
