INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B4B572F3C410B4E98E8945B08D06BC8', X'BDC36EAEA1F41C43AAD39EF4E3A593E5', X'BDC36EAEA1F41C43AAD39EF4E3A593E5', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining 6 cups of cold water and stir in the whole peppercorns and allspice berries. Let the mixture cool completely before using it.');
