INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2871EBEE84EA1E43B11EF0333B795843', X'BDC36EAEA1F41C43AAD39EF4E3A593E5', X'BDC36EAEA1F41C43AAD39EF4E3A593E5', 5, NULL, NULL, 2, NULL, 0, 'Pour the cooled brine mixture over the pork. Cover it with a lid or plastic wrap, and store in the refrigerator. Brine the pork for 8 to 12 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C3D9F7A3987F34D9EBEE40539978019', X'BDC36EAEA1F41C43AAD39EF4E3A593E5', X'BDC36EAEA1F41C43AAD39EF4E3A593E5', 6, NULL, NULL, 2, NULL, 0, 'When ready to cook, remove the meat from the brine. Quickly rinse it under cool water, and pat dry with paper towels. Do not over-rinse as this will remove all the salt and will leave the meat quite bland.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3CF3AD9FF9EFD448A1AA3A1319915DD', X'BDC36EAEA1F41C43AAD39EF4E3A593E5', X'BDC36EAEA1F41C43AAD39EF4E3A593E5', 7, NULL, NULL, 2, NULL, 0, 'Coat the tenderloin with the salt-free seasoning rub of your choice, and cook as directed per the grilling or oven roasting instructions of your preferred recipe.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2B9BF969C5E4D4D93171A3B05320E05', X'CCFE4327336C7D47881697A0E1C8608C', X'CCFE4327336C7D47881697A0E1C8608C', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a glass or plastic bowl, and mix thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B9E0064589B9444B45C7BBDF69EAB58', X'CCFE4327336C7D47881697A0E1C8608C', X'CCFE4327336C7D47881697A0E1C8608C', 1, NULL, NULL, 2, NULL, 0, 'Marinate beef for 4 to 8 hours, pork and poultry for 2 to 4 hours, and seafood for no more than 20 minutes in a zip-top bag in the refrigerator. If you make the marinade a day in advance and let it sit in the refrigerator, the flavors will have more time to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A673D9F6717DAA4FA5BC9FC26D80BAD1', X'E2FBE7B7425DCA4EBA9840E77BBFF059', X'E2FBE7B7425DCA4EBA9840E77BBFF059', 0, NULL, NULL, 2, NULL, 0, 'Combine all marinade ingredients in a medium bowl. Place the tri-tip roast in a large resealable plastic bag. Pour marinade over meat, seal bag and allow to marinate for 4 to 6 hours in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C191C98CE8A3D6478D0D8277C018A074', X'E2FBE7B7425DCA4EBA9840E77BBFF059', X'E2FBE7B7425DCA4EBA9840E77BBFF059', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill on medium heat and prepare for indirect cooking. Remove roast from the pan making sure to reserve marinade. Place roast onto a lightly oiled grill grate and allow to cook until internal temperature reaches 150 to 165 F or about 25 to 35 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B85F337A4484748B8A9A19983FF07D3', X'E2FBE7B7425DCA4EBA9840E77BBFF059', X'E2FBE7B7425DCA4EBA9840E77BBFF059', 2, NULL, NULL, 2, NULL, 0, 'While the tri-tip is cooking, it is time to prepare the sauce. Using side burner and a medium saucepan, bring reserved marinade to boil. Combine 1 tablespoon of cornstarch with 3 tablespoons of water. Add mixture to marinade. Stir until the sauce has thickened. Reduce heat to low and allow to simmer for an additional 3 to 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC7A381124C96B459581C87934E53797', X'E2FBE7B7425DCA4EBA9840E77BBFF059', X'E2FBE7B7425DCA4EBA9840E77BBFF059', 3, NULL, NULL, 2, NULL, 0, 'Once tri-tip has reached the desired doneness, remove from grill and tent with aluminum foil, and allow meat to rest for 10 minutes. Carve roast, top with sauce, and serve with you favorite sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F12931E53CB0674BB1ACE77C291B9521', X'E2FBE7B7425DCA4EBA9840E77BBFF059', X'E2FBE7B7425DCA4EBA9840E77BBFF059', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFA68AE7F8300D43B78193D3B5372891', X'BF3BA6FFEE9392448008D1C1A2C04961', X'BF3BA6FFEE9392448008D1C1A2C04961', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a small bowl until brown sugar has dissolved.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65FD26EC0E9E414C8EF5F504A38D4269', X'BF3BA6FFEE9392448008D1C1A2C04961', X'BF3BA6FFEE9392448008D1C1A2C04961', 1, NULL, NULL, 2, NULL, 0, 'Store marinade in an airtight container in refrigerator until ready to use. Mixture will last for 5-7 days if stored properly. Recipe yields about 3/4 cup or enough for about 4 pork chops. Increase recipe to suit your needs and quantity of pork chops.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'588C4A976473BA43A4C7BEF5C7A737E3', X'BF3BA6FFEE9392448008D1C1A2C04961', X'BF3BA6FFEE9392448008D1C1A2C04961', 2, NULL, NULL, 2, NULL, 0, 'To marinate: place pork chops into a resealable plastic bag (2 bags if you are preparing more than 4 chops). Pour marinade over top, making sure all surfaces of meat are well coated. Seal bag and place into refrigerator for 4 to 12 hours. Cook as directed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1E5243991CE4E4481E5966C91B2794D', X'BF3BA6FFEE9392448008D1C1A2C04961', X'BF3BA6FFEE9392448008D1C1A2C04961', 3, NULL, NULL, 2, NULL, 0, 'This mixture can also be used as a sauce or baste. Simmer over medium heat in a saucepan for 7 to 8 minutes, stirring often. If you''d like it thicker, increase heat to medium-high, mix together 1 to 2 teaspoons cornstarch with 2 tablespoons warm water until dissolved. Add to mixture and stir in for 1 to 2 minutes until desired thickness is achieved.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86DD6A754FA65F47B11D6CF4431A814D', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E011DC1DA2797E4F8866B787EA98D323', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 1, NULL, NULL, 2, NULL, 0, 'Place the beef roast and onions in a large resealable bag.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE1A8779F4987D41BD477F27FD67B343', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the wine, oil, garlic, salt, pepper, rosemary, celery seeds, thyme leaves, and sage to make a marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6F305D35A6271469A20455AA03091DB', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 3, NULL, NULL, 2, NULL, 0, 'Pour the marinade mixture over the top of the beef roast and seal the bag. Refrigerate the roast for several hours or up to one day.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E398E8FAEC1D041976643FEFB0C675E', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 4, NULL, NULL, 2, NULL, 0, 'Preheat the grill to high heat. Remove the roast from the bag, retaining the marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E99344D358F2B8448CC1226EC1EBB6E7', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 5, NULL, NULL, 2, NULL, 0, 'Place the roast on the rotisserie spit, making sure to get it as balanced as you can. Secure it tightly and place it on grill over high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CCE77C67B7EE94B96AFE07AA32C8BB7', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 6, NULL, NULL, 2, NULL, 0, 'After 15 minutes, reduce the heat or move the coals away from the roast and continue cooking it.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AF800EA25403248B074123E0234F4CC', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile, pour 2 cups of the marinade into a saucepan. Bring it to a boil, reduce the heat to medium, and simmer for 3 to 4 minutes, stirring often. Remove it from the heat and add the butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C26773B25DA2E4CA5559075D1F1DBF9', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 8, NULL, NULL, 2, NULL, 0, 'Baste the roast with the basting mixture every 30 minutes during the cooking time. Continue cooking until done, about 1 hour 30 minutes. Use a meat thermometer to ensure your roast is done to your liking—130 to 135 F is medium-rare, and recommended for a beef roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDE4296A8287B3438B23823EB47E11AD', X'9C72329EAB639F419C9A31FA42BB089A', X'9C72329EAB639F419C9A31FA42BB089A', 9, NULL, NULL, 2, NULL, 0, 'Remove the roast to a platter and allow the roast to rest for 10 minutes.');
