INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA6676D89D88854E9A40AAD147E79D13', X'77E14F919E319A408E2E33C05F20C8A5', X'77E14F919E319A408E2E33C05F20C8A5', 7, NULL, NULL, 2, NULL, 0, 'Serve with your choice of sides and enjoy!');
