INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A261E3BBC5B29D41A8589EA461B11053', X'3107E1A711EF5D45ACB4909002BFA13D', X'3107E1A711EF5D45ACB4909002BFA13D', 6, NULL, NULL, 2, NULL, 0, 'Once done, remove from grill and let rest for 5 to 10 minutes.');
