INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AC122677E41BB45AD57D50441BF7A61', X'08A96E34F7E730439F1BD4FDC3200C8D', X'08A96E34F7E730439F1BD4FDC3200C8D', 0, NULL, NULL, 2, NULL, 0, 'Place brisket into a large, deep container. Combine remaining ingredients in a large mixing bowl and pour over meat. Make sure all surfaces of the brisket are well coated. Cover dish with plastic wrap and allow to marinate in the refrigerator for 2 to 4 hours. Remove brisket, but reserve marinade for later, by storing in the refrigerator.');
