INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0220BDFB9531CB48995B5374B0725AB4', X'08A96E34F7E730439F1BD4FDC3200C8D', X'08A96E34F7E730439F1BD4FDC3200C8D', 2, NULL, NULL, 2, NULL, 0, 'Place reserved marinade in a saucepan and bring to a high simmer for 1 to 2 minutes. Reduce heat and simmer on medium-low to low for another 8 to 10 minutes or until sauce begins to thicken. Remove from heat and serve sangria sauce over sliced brisket.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2CD0164761EE842A5F36511207734B6', X'01EE2B3B566AAF439F6CC866F5536389', X'01EE2B3B566AAF439F6CC866F5536389', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
