INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30BAD22C753D654CBD7FEC8D87CB62BD', X'3920E2A01367FB4FA0275487FD011445', X'3920E2A01367FB4FA0275487FD011445', 2, NULL, NULL, 2, NULL, 0, 'For vegetables and shrimp, marinate for 1 hour, for chicken up to 3 hours, and for beef up to 8 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE64368CAE90284797FCCE68DCCF5C01', X'0CB7AC1819DFEE4F9265776D72BD61F5', X'0CB7AC1819DFEE4F9265776D72BD61F5', 0, NULL, NULL, 2, NULL, 0, 'Place skirt steak in resealable plastic bag. Combine marinade ingredients in a bowl. Pour over skirt steaks, making sure to evenly distribute marinade all over the meat. Seal bag and place into refrigerator for 2 to 5 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E599E41B9198E146A72F5054B012A8A3', X'0CB7AC1819DFEE4F9265776D72BD61F5', X'0CB7AC1819DFEE4F9265776D72BD61F5', 1, NULL, NULL, 2, NULL, 0, 'If using a charcoal grill, you want a two layer fire, with the coals nice and hot. Place marinated steaks onto grill grate and cook for 3 to 5 minutes per side, with the lid off. Make sure to watch it closely as these steaks cook quickly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFEF89823AD82449B44AD73BB406E2AD', X'0CB7AC1819DFEE4F9265776D72BD61F5', X'0CB7AC1819DFEE4F9265776D72BD61F5', 2, NULL, NULL, 2, NULL, 0, 'Once cooked through remove from fire and allow to rest for 5 minutes. Slice against the grain into thin strips, serve onto warmed tortillas, and top with corn salsa, sliced green onion, guacamole, and chopped cilantro.');
