INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A13ABF46AECD674699B26202C594F5A2', X'1DB333CEAAB65240932F04BCD3209638', X'1DB333CEAAB65240932F04BCD3209638', 1, NULL, NULL, 2, NULL, 0, 'In a large nonreactive baking dish or container, whisk together the soy sauce, garlic, dry mustard, and the coarsely ground black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B09623B4A3838242ABD2F7F98060B17D', X'1DB333CEAAB65240932F04BCD3209638', X'1DB333CEAAB65240932F04BCD3209638', 2, NULL, NULL, 2, NULL, 0, 'Do not rinse the steaks, but pat them dry with a paper towel. Add them to the marinade and turn them to coat thoroughly. Cover with plastic wrap and refrigerate for 2 to 5 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'603768ECA00DDE49B1950B7A1C3B7D60', X'1DB333CEAAB65240932F04BCD3209638', X'1DB333CEAAB65240932F04BCD3209638', 3, NULL, NULL, 2, NULL, 0, 'When ready to cook, oil the grill and prepare it for high direct heat, or about 450 F. If broiling, turn the oven to BROIL.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AF6D8E9F881974FA3E90F6BFC8F4D00', X'1DB333CEAAB65240932F04BCD3209638', X'1DB333CEAAB65240932F04BCD3209638', 4, NULL, NULL, 2, NULL, 0, 'When the grill or broiler has come up to temperature, remove the steaks from the marinade, discard the marinade, and place the steaks on your chosen cooking surface.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD7E7B822F6FCD4CAC0FF4D7C0914156', X'1DB333CEAAB65240932F04BCD3209638', X'1DB333CEAAB65240932F04BCD3209638', 5, NULL, NULL, 2, NULL, 0, 'Grill the steaks until the desired temperature and doneness has been reached, using tongs to turn the steaks only once. To obtain grill marks, halfway through cooking the first side, turn it perpendicular on the grates to create the crosshatch pattern. After turning the steak over (remember: only once), repeat halfway through on the second side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F5B56A2B7D0274785CD023BA0342B86', X'1DB333CEAAB65240932F04BCD3209638', X'1DB333CEAAB65240932F04BCD3209638', 6, NULL, NULL, 2, NULL, 0, 'Remove from the grill and serve with your favorite side dishes like a baked potato or French fries and a leafy green salad.');
