INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A73171F6A6024043912AD5D615CABC83', X'215EC481CD23CF4ABA471B55C75A5A09', X'215EC481CD23CF4ABA471B55C75A5A09', 3, NULL, NULL, 2, NULL, 0, 'Marinade pork chops or tenderloin in a zip-top bag for at least 4 to 6 hours in the refrigerator. You can also freeze bags of pork chops, marinade and all. They will last up to three months in the freezer if you release all of the air out of the plastic bag before sealing.');
