INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11288623FE662548A4AB6C2B76949464', X'52D386172381C04F997BC71648816F3C', X'52D386172381C04F997BC71648816F3C', 1, NULL, NULL, 2, NULL, 0, 'Cover and place in refrigerator to marinate in jerk seasoning for 30 minutes to 1 hour. Preheat grill. Remove chicken from jerk marinade and pour marinade into a saucepan.');
