INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DAE447EA9D739489F7CD9DDD28984A5', X'047005A6889AF640963C68568CF9EC8B', X'047005A6889AF640963C68568CF9EC8B', 4, NULL, NULL, 2, NULL, 0, 'Store any unused sauce (that hasn''t come into contact with uncooked meat) in an airtight container in the refrigerator for up to one week after preparation. The sauce can be made up to one week in advance. Simply reheat the sauce on the stovetop or in a microwave to use within 5 minutes.');
